INSERT INTO `quest_template_locale` VALUES (27274, 'itIT', 'Notizie da Patrice Lancaster', 'Parla con Patrice Lancaster nella Sala di Addestramento a Roccavento.', 'Ho ricevuto un messaggio urgente per te da Roccavento, $n. Patrice Lancaster ha chiesto di incontrarti a Roccavento. Nel messaggio non è specificato di cosa voglia parlare l\'Istruttrice dei Sacerdoti, ma dovresti andarci il prima possibile. Cerca Patrice Lancaster nella Sala di Addestramento della Città Vecchia.', '', '', '', '', '', 'Parla con Patrice Lancaster nella Sala di Addestramento a Roccavento.', 0);
INSERT INTO `quest_template_locale` VALUES (27274, 'koKR', '파트리스 랭커스터의 전언', '스톰윈드의 훈련장에 있는 파트리스 랭커스터와 대화해야 합니다.', '스톰윈드에서 급한 전갈이 왔습니다, $n. 파트리스 랭커스터가 스톰윈드에서 당신을 만나보고 싶어한다는군요. 무슨 일로 당신을 만나보려 하는지는 기록되어 있지 않지만, 최대한 빨리 스톰윈드로 가보는 것이 좋겠습니다. 구 시가지의 훈련장에서 파트리스 랭커스터를 찾아보십시오.', '', '', '', '', '', '스톰윈드의 훈련장에 있는 파트리스 랭커스터와 대화해야 합니다.', 0);
