INSERT INTO `quest_template_locale` VALUES (27274, 'ruRU', 'Весть от Патрисии Ланкастер', 'Поговорите с Патрисией Ланкастер в тренировочном зале Штормграда.', 'У меня срочное сообщение из Штормграда, $n. Патрисия Ланкастер просит тебя о встрече. В сообщении не сказано, что конкретно она хочет обсудить, но тебе нужно отправиться в Штормград как можно скорее. Наставница жрецов ждет тебя в тренировочном зале Старого города.', '', '', '', '', '', 'Поговорите с Патрисией Ланкастер в тренировочном зале Штормграда.', 0);
