INSERT INTO `quest_template_locale` VALUES (27276, 'ruRU', 'Снова вместе', 'Обратитесь к храбрецу заставы Вольного Ветра на пике Темного Облака в Тысяче Игл.', 'Мы вылечили храбреца и выдали ему новое снаряжение. Я уже отправил его на поле боя.$B$BСледуй к пику Темного Облака. Он ждет тебя там.', '', '', '', '', '', '', 0);
