INSERT INTO `quest_template_locale` VALUES (27277, 'koKR', '피닉스 아크샤인과의 만남', '오그리마의 병영에 있는 피닉스 아크샤인과 대화해야 합니다.', '오그리마에서 당신에게 보낸 긴급 전갈을 받았습니다, $n. 저명한 마법사 피닉스 아크샤인이 오그리마로 찾아와 달라고 부탁했다는군요. 무슨 일인지 모르겠지만 제가 당신이라면 단숨에 오그리마로 달려가서 명예의 골짜기의 병영에 있는 피닉스 아크샤인을 찾아볼 겁니다.', '', '', '', '', '', '오그리마의 병영에 있는 피닉스 아크샤인과 대화해야 합니다.', 0);
