INSERT INTO `quest_template_locale` VALUES (27278, 'deDE', 'Tamanjis Ruf', 'Sprecht mit Tamanji in der Kaserne in Orgrimmar.', 'Ein Bote aus Orgrimmar hat gerade eine Nachricht für Euch gebracht. Ich soll Euch ausrichten, dass Tamanji, die Meisterjägerin in Orgrimmar, Euch treffen möchte.$B$BReist schnell nach Orgrimmar und sucht Tamanji in der Kaserne im Tal der Ehre auf.', '', '', '', '', '', 'Sprecht mit Tamanji in der Kaserne in Orgrimmar.', 0);
