INSERT INTO `quest_template_locale` VALUES (27317, 'frFR', 'Le hochet en os', 'Remettez le Hochet en os au Brave de Librevent, à la Cime de Noir-Nuage, dans les Mille pointes.', 'Un hochet tauren est posé là. Il irradie d’énergie surnaturelle. Sa propriétaire est morte, l’objet est sans défense.$b$bLe brave aura peut-être une idée de ce dont il s’agit ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'itIT', 'Bastone delle Ossa Risonanti', 'Consegna il Bastone delle Ossa Risonanti al Guerriero di Ventolibero presso il Pinnacolo Nubenera a Millepicchi.', '<Trovi un bastone dei Tauren che trasuda poteri sovrannaturali. Il suo custode è morto e il manufatto è senza difese.>$B$B<Di certo il Guerriero ne conosce la natura.>', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'koKR', '뼈 주술막대', '버섯구름 봉우리의 먹구름 봉우리에 있는 높새바람 용사에게 뼈 주술막대를 전해야 합니다.', '여기 초자연적인 힘을 발산하는 타우렌 뼈로 만든 주술막대가 놓여 있습니다. 주인이 목숨을 잃었으니 무방비 상태로 버려진 셈입니다.$b$b과연 높새바람 용사가 이 막대의 중요성을 알아챌 수 있을까요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'ptBR', 'Chocalho de Ossos', 'Entregue o Chocalho de Ossos para o Valente de Vento Livre no Pináculo da Nuvem Negra, que fica em Mil Agulhas.', 'Eis um chocalho tauren largado no chão, emanando poderes sobrenaturais. Seu antigo dono está morto, e o artefato foi abandonado.$B$BTalvez o valente tenha alguma pista quanto à importância de tal artefato.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'ruRU', 'Костяная погремушка', 'Передайте костяную погремушку храбрецу Заставы Вольного Ветра на пике Темного Облака в Тысяче Игл.', 'Там лежит тауренская погремушка, от которой исходит сверхъестественная аура. Ее владельцы мертвы, и артефакт лишился защитника.$B$BБыть может, храбрец заставы Вольного Ветра сумеет определить, есть ли от нее толк?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'zhCN', '骨鸣响器', '将骨鸣响器交给千针石林黑云峰的乱风岗卫士。', '这是一件牛头人的响器，渗着超自然的力量。其持有者被杀了，神器自然任人宰割。$B$B显然卫士能洞悉其中奥妙所在？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27317, 'zhTW', '骨之波浪鼓', '將骨之波浪鼓帶去給千針石林黑雲峰的亂風崗勇士。', '一個牛頭人的波浪鼓放在此地，散發出超自然的力量。它的保護者死了，這件神器已經無人守護。$B$B肯定會有勇士對這件象徵物有更多看法？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'deDE', 'Häuptlinge der Grimmtotem: Ältester Sturmhuf', 'Tötet den Ältesten Sturmhuf.', 'Der Älteste Sturmhuf ist ein Schamane mit ernormer Macht. Anscheinend ist er die rechte Hand von Magatha Grimmtotem, der Ältestengreisin und Anführerin des Grimmtotemklans.$B$BWir wissen, dass er hier ist und damit beschäftigt ist, Geomanten auszubilden. Wir müssen ihn töten, $C.', '', 'Hütet Euch vor seiner Beherrschung der Elemente!', 'Ältester Sturmhuf', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'esES', 'Jefes Tótem Siniestro: Ancestro Pezuña Tempestuosa', 'Mata al ancestro Pezuña Tempestuosa.', 'El ancestro Pezuña Tempestuosa es un chamán de poder inmenso. Se supone que es un discípulo directo de Magatha Tótem Siniestro, la bruja anciana y líder del clan Tótem Siniestro.$b$bSabemos que está aquí y que ha estado instruyendo a sus geománticos. Tenemos que matarlo, $c.', '', '¡Cuidado con su maestría de los elementos!', 'Pezuña Tempestuosa', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'esMX', 'Jefes Tótem Siniestro: Ancestro Pezuña Tempestuosa', 'Mata al ancestro Pezuña Tempestuosa.', 'El ancestro Pezuña Tempestuosa es un chamán de poder inmenso. Se supone que es un discípulo directo de Magatha Tótem Siniestro, la bruja anciana y líder del clan Tótem Siniestro.$b$bSabemos que está aquí y que ha estado instruyendo a sus geománticos. Tenemos que matarlo, $c.', '', '¡Cuidado con su maestría de los elementos!', 'Pezuña Tempestuosa', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'frFR', 'Les chefs du Totem-Sinistre : l\'Ancien Sabot-Tempête', 'Tuez l\'Ancien Sabot-Tempête.', 'L’ancien Sabot-Tempête est un chaman extrêmement puissant. On dit qu’il a été l’élève de Magatha Totem-Sinistre, la vieille mégère qui dirige le clan Totem-Sinistre.$b$bNous savons où il se trouve et où il forme leurs géomanciens. Il va falloir le tuer, $c.', '', 'Prenez garde, il maîtrise les éléments !', 'Ancien Sabot-Tempête', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'itIT', 'Capi dei Totem Truce: Anziano Zoccolo Tempestoso', 'Uccidi l\'Anziano Zoccolo Tempestoso.', 'L\'Anziano Zoccolo Tempestoso è uno Sciamano dai poteri immensi. Sembra sia un discepolo diretto di Magatha Totem Truce, la Saggia Anziana, a capo della tribù Totem Truce.$B$BSappiamo che è qui e che sta addestrando i loro Geomanti. Dobbiamo ucciderlo, $C.', '', 'Attenzione alla sua padronanza degli elementi!', 'Anziano Zoccolo Tempestoso', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'koKR', '그림토템 부족의 족장: 장로 스톰후프', '장로 스톰후프를 처치해야 합니다.', '장로 스톰후프는 가공할만한 힘을 가진 주술사입니다. 그림토템 부족의 지도자이자 대모인 마가타 그림토템의 직속 제자로 추정되고 있죠.$b$b우리는 그 녀석이 여기서 흙점쟁이들을 훈련시키고 있다는 정보를 입수했습니다. 그를 반드시 처치해야 합니다, $c님.', '', '그가 능숙하게 다루는 정령을 조심하십시오!', '장로 스톰후프', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'ptBR', 'Chefes dos Temível Totem: Ancião Casco Feroz', 'Mate o Ancião Casco Feroz.', 'O Ancião Casco Feroz é um xamã de imenso poder. Dizem que ele foi discípulo de Magatha Temível Totem, a Bruxa Anciã, líder do clã Temível Totem.$B$BSabemos que ele está aqui, treinando geomantes. Temos que matá-lo, $c.', '', 'Tome cuidado com os poderes elementais dele!', 'Ancião Casco Feroz', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'ruRU', 'Лидеры племени Зловещего Тотема: старейшина Штормовое Копыто', 'Убейте старейшину Штормовое Копыто.', 'Старейшина Штормовое Копыто – чрезвычайно могущественный шаман. Скорее всего, он обучался у самой Магаты Зловещий Тотем, мудрой старицы, предводительницы всего клана.$B$BИзвестно, что он здесь – тренирует заклинателей земли из своего племени. Мы должны прикончить его, |3-6($c).', '', 'Берегитесь его умения управлять стихиями!', 'Старейшина Штормовое Копыто', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'zhCN', '恐怖图腾酋长：雷蹄长老', '杀死雷蹄长老。', '雷蹄长老是一名具有无比力量的萨满。据信，他是巫婆长老、恐怖图腾氏族领袖玛加萨·恐怖图腾的嫡系信徒。$B$B我们得知他在这里训练他们的地卜师。我们得杀了他，$c。', '', '小心他操纵元素的能力！', '雷蹄长老', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27318, 'zhTW', '恐怖圖騰的酋長:風暴之蹄長者', '殺死風暴之蹄長者。', '風暴之蹄長者是一個擁有強大力量的薩滿。據說，他是瑪加薩‧恐怖圖騰的直系門徒，而瑪加薩這名年邁的長者則是恐怖圖騰一族的領導者。$B$B我們知道他在此地一直訓練他們的地卜師。我們一定要殺死他，$c。', '', '小心他對元素的操控!', '風暴之蹄長者', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'deDE', 'Häuptlinge der Grimmtotem: Ältester Sturmhuf', 'Tötet den Ältesten Sturmhuf.', 'Der Älteste Sturmhuf ist ein Schamanenhäuptling mit ernormer Macht. Er wurde von Magatha Grimmtotem unterrichtet, der Ältestengreisin und Anführerin des Grimmtotemklans.$B$BWir wissen, dass er hier ist und damit beschäftigt ist, Geomanten auszubilden. Wir müssen ihn töten, $C.', '', 'Hütet Euch vor seiner Beherrschung der Elemente!', 'Ältester Sturmhuf', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'esES', 'Jefes Tótem Siniestro: Ancestro Pezuña Tempestuosa', 'Mata al ancestro Pezuña Tempestuosa.', 'El ancestro Pezuña Tempestuosa es un jefe chamán de poder inmenso. Aprendió de Magatha Tótem Siniestro, la bruja anciana y líder del clan Tótem Siniestro.$b$bSabemos que está aquí y que ha estado instruyendo a sus geománticos. Tenemos que matarlo, $c.', '', '¡Cuidado con su maestría de los elementos!', 'Pezuña Tempestuosa', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'esMX', 'Jefes Tótem Siniestro: Ancestro Pezuña Tempestuosa', 'Mata al ancestro Pezuña Tempestuosa.', 'El ancestro Pezuña Tempestuosa es un jefe chamán de poder inmenso. Aprendió de Magatha Tótem Siniestro, la bruja anciana y líder del clan Tótem Siniestro.$b$bSabemos que está aquí y que ha estado instruyendo a sus geománticos. Tenemos que matarlo, $c.', '', '¡Cuidado con su maestría de los elementos!', 'Pezuña Tempestuosa', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'frFR', 'Les chefs du Totem-Sinistre : l\'Ancien Sabot-Tempête', 'Tuez l\'Ancien Sabot-Tempête.', 'L’ancien Sabot-Tempête est un chef chaman très puissant. Il a été l’élève de Magatha Totem-Sinistre, la vieille mégère qui dirige le clan Totem-Sinistre.$b$bNous savons où il se trouve et où il forme leurs géomanciens. Il va falloir le tuer, $c.', '', 'Prenez garde, il maîtrise les éléments !', 'Ancien Sabot-Tempête', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'itIT', 'Capi dei Totem Truce: Anziano Zoccolo Tempestoso', 'Uccidi l\'Anziano Zoccolo Tempestoso.', 'L\'Anziano Zoccolo Tempestoso è uno Sciamano dai poteri immensi. Ha studiato con Magatha Totem Truce, la Saggia Anziana, a capo della tribù Totem Truce.$B$BSappiamo che è qui e che sta addestrando i loro Geomanti. Dobbiamo ucciderlo, $C.', '', 'Attenzione alla sua padronanza degli elementi!', 'Anziano Zoccolo Tempestoso', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'koKR', '그림토템 부족의 족장: 장로 스톰후프', '장로 스톰후프를 처치해야 합니다.', '장로 스톰후프는 가공할만한 힘을 가진 주술사 족장입니다. 그림토템 부족의 지도자이자 대모인 마가타 그림토템에게 직접 훈련받았다고 하더군요.$b$b놈이 여기서 흙점쟁이들을 훈련시키고 있다는 정보를 입수했습니다. 그를 반드시 처치해야 합니다, $c님.', '', '그가 능숙하게 다루는 정령을 조심하십시오!', '장로 스톰후프', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'ptBR', 'Chefes dos Temível Totem: Ancião Casco Feroz', 'Mate o Ancião Casco Feroz.', 'O Ancião Casco Feroz é um chefe xamã de imenso poder. Ele foi treinado pela própria Magatha Temível Totem, a Bruxa Anciã, líder do clã Temível Totem.$B$BSabemos que ele está aqui, treinando geomantes. Temos que matá-lo, $c.', '', 'Tome cuidado com os poderes elementais dele!', 'Ancião Casco Feroz', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'ruRU', 'Лидеры племени Зловещего Тотема: старейшина Штормовое Копыто', 'Убейте старейшину Штормовое Копыто.', 'Старейшина Штормовое Копыто – чрезвычайно могущественный шаман. Он обучался у самой Магаты Зловещий Тотем, мудрой старицы, предводительницы всего клана.$B$BИзвестно, что он здесь – тренирует заклинателей земли из своего племени. Мы должны прикончить его, |3-6($c).', '', 'Берегитесь его умения управлять стихиями!', 'Старейшина Штормовое Копыто', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'zhCN', '恐怖图腾酋长：雷蹄长老', '杀死雷蹄长老。', '雷蹄长老是一名具有无比力量的萨满。据信，他受教于巫婆长老、恐怖图腾氏族领袖玛加萨·恐怖图腾的嫡系信徒。$B$B我们得知他在这里训练他们的地卜师。我们得杀了他，$c。', '', '小心他操纵元素的能力！', '雷蹄长老', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27319, 'zhTW', '恐怖圖騰的酋長:風暴之蹄長者', '殺死風暴之蹄長者。', '風暴之蹄長者是一個擁有強大力量的薩滿。據說，他是瑪加薩‧恐怖圖騰的直系門徒，而瑪加薩這名年邁的長者則是恐怖圖騰一族的領導者。$B$B我們知道他在此地一直訓練他們的地卜師。我們一定要殺死他，$c。', '', '小心他對元素的操控!', '風暴之蹄長者', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'deDE', 'Die Niederschrift der Geschichte', 'Findet die Niederschrift der Geschichte.', 'In der Mythologie der Shu\'halo ist die Rassel der Knochen eng mit der Niederschrift der Geschichte und den Trommeln des Krieges verknüpft. Es handelt sich dabei anscheinend um wertvolle Artefakte der Tauren.$B$BWir müssen unsere Augen offen halten, wenn wir die anderen zwei Artefakte finden wollen. Wenn man den Geschichten Glauben schenken will, sieht die Niederschrift wohl aus wie eine Schriftrolle mit Piktogrammen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'esES', 'Las escrituras históricas', 'Obtén las escrituras históricas.', 'Según los mitos shu\'halo, el sonajero de huesos está vinculado a las escrituras históricas y los tambores de la guerra. Se supone que son poderosos artefactos tauren.$b$bDebemos mantener los ojos abiertos para encontrar los otros dos. Si las historias son ciertas, las escrituras seguramente tendrán el aspecto de algún tipo de pergamino pictográfico.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'esMX', 'Las escrituras históricas', 'Obtén las escrituras históricas.', 'Según los mitos shu\'halo, el sonajero de huesos está vinculado a las escrituras históricas y los tambores de la guerra. Se supone que son poderosos artefactos tauren.$b$bDebemos mantener los ojos abiertos para encontrar los otros dos. Si las historias son ciertas, las escrituras seguramente tendrán el aspecto de algún tipo de pergamino pictográfico.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'frFR', 'L\'Écrit de l\'Histoire', 'Mettre la main sur l\'Écrit de l\'Histoire.', 'Dans le mythe shu’alo, le hochet d’os est lié à l’Écrit de l’Histoire et aux tambours de guerre. Il paraît que ce sont de puissants artéfacts taurens.$b$bSi nous devons trouver les deux autres, nous avons intérêt à garder les yeux ouverts. Si l’on en croit les histoires, l’écrit est sans doute une sorte de parchemin avec des pictogrammes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'itIT', 'Epistola della Storia', 'Recupera l\'Epistola della Storia.', 'Secondo la mitologia shu\'halo, il Bastone delle Ossa Risonanti è collegato all\'Epistola della Storia e ai Tamburi della Guerra. Si dice siano potenti manufatti dei Tauren.$B$BDobbiamo tenere gli occhi aperti, se vogliamo trovare gli altri due. Se la leggenda è vera, l\'epistola somiglia a una specie di pergamena con dei disegni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'koKR', '역사의 확인서', '역사의 확인서를 획득해야 합니다.', '슈할로의 전설에 따르면 뼈 주술막대는 역사의 확인서, 전쟁의 북과 연결되어 있다고 합니다. 셋 다 강력한 타우렌 유물로 알려졌죠.$b$b다른 두 유물을 찾아내려면 정신을 바짝 차리셔야 할 겁니다. 전설에 따르면 역사의 확인서는 상형 문자가 쓰인 두루마리라고 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'ptBR', 'Pergaminho da História', 'Consiga o Pergaminho da História.', 'Os mitos dos shu\'halo contam da existência de três poderosos artefatos taurens: o Chocalho de Ossos, o Pergaminho da História e os Tambores da Guerra.$B$BPrecisamos ficar de olhos bem abertos se quisermos encontrá-los. Se as histórias forem verdadeiras, o pergaminho deve parecer um objeto comum, um mero pergaminho ilustrado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'ruRU', 'Старинный манускрипт', 'Отыщите старинный манускрипт.', 'По легенде шу\'хало, костяная погремушка как-то связана со старинным манускриптом и барабанами войны. Думаю, это могущественные артефакты тауренов.$B$BНужно смотреть во все глаза, если мы хотим найти манускрипт и барабан. Если верить легенде, манускрипт выглядит просто как свиток с рисунками.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'zhCN', '历史文书', '找到历史文书。', '在舒哈鲁神话里，骨鸣响器连接着历史文书和战争之鼓。它们都是强大的牛头人神器。$B$B要想找到另外两件，我们必须擦亮眼睛。假如传说是真的，那么文书看起来应该像是某种象形文字卷轴。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27320, 'zhTW', '歷史令狀', '收集歷史令狀。', '在舒哈魯的神話裡，骨之波浪鼓是與歷史令狀和戰爭之鼓有所連結的。它們理當是一種強大的牛頭人神器。$B$B我們必須留意其他這兩件神器的所在。如果故事的內容可信的話，歷史令狀應該看起來會像是某種繪滿文字的卷軸。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'deDE', 'Die Niederschrift der Geschichte', 'Findet die Niederschrift der Geschichte.', 'Die Rassel der Knochen ist Teil der uralten Mythologie der Shu\'halo. Sie wurde schon immer mit der Niederschrift der Geschichte und den Trommeln des Krieges in Verbindung gebracht. Es handelt sich dabei anscheinend um wertvolle Artefakte der Tauren, Geschenke von der Erdmutter, wenn man den Geschichten Glauben schenken soll. Allerdings kann ich mir nicht vorstellen, dass die Erdmutter mit den Trommeln wirklich etwas zu tun hat.$B$BWir müssen aufmerksam sein, wenn wir die anderen zwei Artefakte finden wollen. Wenn ich mich recht erinnere, sieht die Niederschrift wohl aus wie eine Schriftrolle mit Piktogrammen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'esES', 'Las escrituras históricas', 'Obtén las escrituras históricas.', 'El sonajero de huesos forma parte de los antiguos mitos shu\'halo. Siempre se relaciona con las escrituras históricas y los tambores de la guerra. Se supone que son poderosos artefactos tauren, regalos de la Madre Tierra según las historias. Pero no puedo imaginarla a Ella relacionada con los tambores.$b$bDebemos prestar atención si queremos encontrar los otros dos. Las escrituras tienen el aspecto de un pergamino pictográfico, si mal no recuerdo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'esMX', 'Las escrituras históricas', 'Obtén las escrituras históricas.', 'El sonajero de huesos forma parte de los antiguos mitos shu\'halo. Siempre se relaciona con las escrituras históricas y los tambores de la guerra. Se supone que son poderosos artefactos tauren, regalos de la Madre Tierra según las historias. Pero no puedo imaginarla a Ella relacionada con los tambores.$b$bDebemos prestar atención si queremos encontrar los otros dos. Las escrituras tienen el aspecto de un pergamino pictográfico, si mal no recuerdo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'frFR', 'L\'Écrit de l\'Histoire', 'Mettre la main sur l\'Écrit de l\'Histoire.', 'Le hochet d’os fait partie d’un vieux mythe shu’alo. Il est toujours lié à l’Écrit de l’Histoire et aux tambours de guerre. Il paraît que ce sont de puissants artéfacts taurens, des dons de la Terre-Mère, d’après les légendes, même si j’ai du mal à l’imaginer liée aux tambours.$b$bSi nous devons trouver les deux autres, nous avons intérêt à rester vigilants. Si je me souviens bien, l’écrit est sans doute un parchemin avec des pictogrammes.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'itIT', 'Epistola della Storia', 'Recupera l\'Epistola della Storia.', 'Il Bastone delle Ossa Risonanti fa parte di un antico mito shu\'halo. È sempre collegato all\'Epistola della Storia e ai Tamburi della Guerra. Si dice siano potenti manufatti dei Tauren; doni della Madre Terra, se vogliamo credere alle leggende. Anche se non riesco a immaginarla con dei tamburi.$B$BDobbiamo restare vigili, se vogliamo trovare gli altri due. Se non ricordo male, l\'epistola assomiglia a una pergamena con dei disegni.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'koKR', '역사의 확인서', '역사의 확인서를 획득해야 합니다.', '뼈 주술막대는 고대 슈할로 전설에 등장하는 유물입니다. 전설에서는 역사의 확인서, 전쟁의 북과 함께 등장하죠. 셋 다 강력한 타우렌 유물이고 전설이 사실이라면 대지모신이 우리에게 내린 선물입니다만, 대지모신과 전쟁의 북이 연결되어 있다는 사실은 믿기 힘들군요.$b$b다른 두 유물을 찾으시려면 정신 똑바로 차리셔야 할 겁니다. 전설에 따르면 역사의 확인서는 상형 문자가 쓰인 두루마리라고 합니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'ptBR', 'Pergaminho da História', 'Consiga o Pergaminho da História.', 'O Chocalho de Ossos faz parte dos antigos mitos dos shu\'halo. Ele sempre esteve ligado ao Pergaminho da História e aos Tambores da Guerra. Diz-se que a Mãe Terra presentou os taurens com esses três poderosos artefatos. Entretanto, não consigo imaginar nossa Mãe associada aos tambores.$B$BPrecisamos ficar de olhos bem abertos se quisermos encontrar os artefatos que faltam. Se bem me lembro da lenda, o pergaminho se parece com um simples rolo ilustrado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'ruRU', 'Старинный манускрипт', 'Отыщите старинный манускрипт.', 'Костяная погремушка упоминается в древнем шу\'хальском мифе и всегда вместе со старинным манускриптом и барабанами войны. Предположительно, это могущественные артефакты тауренов – дары Матери-Земли. Правда, с трудом верится, что и с барабаном она как-то связана.$B$BМы должны удвоить бдительность, чтобы найти два оставшихся артефакта. Манускрипт должен выглядеть просто как свиток с рисунками, если мне не изменяет память.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'zhCN', '历史文书', '找到历史文书。', '在舒哈鲁古老的神话里，骨鸣响器总是和历史文书以及战争之鼓联系在一起。据传，它们都是强大的牛头人神器，大地母亲的馈赠。尽管我没法儿把她和鼓联系在一起。$B$B要想找到另外两件，我们必须时刻留心。记得没错的话，文书看起来应该像是一个象形文字卷轴。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27321, 'zhTW', '歷史令狀', '收集歷史令狀。', '骨之波浪鼓是舒哈魯神話的一部分。它總是會和歷史令狀和戰爭之鼓有所關連。它們理當是一種強大的牛頭人神器;如果傳說可信，它們是大地之母所賜與的禮物。雖然我並無法想像她與戰鼓有所關連。$B$B我們必須對其他這兩件神器的所在有所警覺。記憶中歷史令狀應該看起來會像是某種繪滿文字的卷軸。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'deDE', 'Korok der Koloss', 'Bringt Koroks zweiten Kopf zu Vorpostenkommandant Ansturm am Gefechtsstand der Verlassenen in den Ruinen von Gilneas.', 'Das Monster ist gefallen und hat den Soldaten der Verlassenen an der Front so eine kurze Atempause verschafft.$B$BBringt Koroks zweiten Kopf zu Vorpostenkommandant Ansturm am Gefechtsstand der Verlassenen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'esES', 'Korok el Coloso', 'Llévale la segunda cabeza de Korok al comandante de vanguardia Escabechina en el Mando Avanzado Renegado en las Ruinas de Gilneas.', 'La monstruosidad ha caído y eso supone un alivio para los soldados renegados del frente.$B$BCoge la segunda cabeza de Korok y reúnete con el comandante de vanguardia Escabechina en el Mando Avanzado Renegado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'esMX', 'Korok el Coloso', 'Llévale la segunda cabeza de Korok al comandante de vanguardia Escabechina en el Mando Avanzado Renegado en las Ruinas de Gilneas.', 'La monstruosidad ha caído y eso supone un alivio para los soldados Renegados del frente.$B$BCoge la segunda cabeza de Korok y vuelve con el comandante de vanguardia Escabechina en el Mando Avanzado Renegado.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'frFR', 'Le colosse fait la tête', 'Apportez la Deuxième tête de Korok au Commandant des lignes avancées Lamatraque au Quartier général avancé des Réprouvés, dans les Ruines de Gilnéas.', 'Le monstre est tombé, ce qui offre un bref répit aux combattants réprouvés, sur le front.$B$BPrenez la deuxième tête de Korok et apportez-la au commandant des lignes avancées Lamatraque au quartier général avancé des Réprouvés.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'itIT', 'Korok il Colosso', 'Porta la Seconda Testa di Korok al Comandante d\'Avanzata Devasto al Comando Avanzato dei Reietti presso le Rovine di Gilneas.', 'La mostruosità è caduta, concedendo un po\' di respiro ai soldati Reietti in prima linea.$B$BPrendi la seconda testa di Korok e portala al Comandante d\'Avanzata Devasto al Comando Avanzato dei Reietti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'koKR', '거인 코록', '코록의 두 번째 머리를 길니아스 폐허의 포세이큰 전방 지휘소에 있는 전방사령관 온슬롯에게 가져가야 합니다.', '마침내 괴수가 쓰러졌습니다. 전방의 포세이큰 병사들도 이제 잠깐이나마 꿀맛 같은 휴식을 취할 수 있습니다.$B$B코록의 두 번째 머리를 포세이큰 전방 지휘소의 전방사령관 온슬롯에게 가져다 주십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'ptBR', 'Korok, o Colosso', 'Leve a Segunda Cabeça de Korok para o Comandante Avançado da Ofensiva no Comando Avançado dos Renegados, que fica nas Ruínas de Guilnéas.', 'A monstruosidade caiu, garantindo um breve sossego para os soldados Renegados nas linhas de frente.$B$BLeve a segunda cabeça de Korok para o Comandante Avançado da Ofensiva no Comando Avançado dos Renegados.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'ruRU', 'Корок Великан', 'Отнесите вторую голову Корока передовому командиру Штурму в передовой отряд Отрекшихся в руинах Гилнеаса.', 'Чудовище пало, и это дало небольшую передышку солдатам Отрекшихся на передовой.$B$BВозьмите вторую голову Корока и отнесите ее командиру Штурму в передовой отряд Отрекшихся.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'zhCN', '双头巨人克洛科', '把克洛科的第二个头带给吉尔尼斯废墟被遗忘者前线指挥部的前锋军指挥官昂斯洛特。', '怪兽倒下了，让前线被遗忘者的士兵们能姑且苟活一阵。$B$B拿上克洛科的第二个头，把它带给被遗忘者前线指挥部的前锋军指挥官昂斯洛特。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27322, 'zhTW', '高大的可洛卡', '將可洛卡的第二顆頭帶去吉爾尼斯廢墟裡的被遺忘者前線指揮營地給前線指揮官安斯洛特。', '那隻怪物死了，前線的被遺忘者士兵得以暫時喘一口氣。$B$B取下可洛卡的第二顆頭，帶回去被遺忘者前線指揮營地給前線指揮官安斯洛特。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27323, 'deDE', 'Häuptlinge der Grimmtotem: Grundig Finsterwolke', 'Tötet Grundig Finsterwolke.', 'Einer der berüchtigten Anführer der Grimmtotems von Steinkrallengebirge ist zurückgekehrt.$B$BWir sollten diese Gelegenheit nutzen und sicherstellen, dass er nirgendwo anders hingeht.', '', 'Adel des Düsterwolkengipfels. Sein Geschlecht muss mit ihm vergehen.', 'Grundig Düsterwolke', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27323, 'esES', 'Jefes Tótem Siniestro: Grundig Nube Negra', 'Mata a Grundig Nube Negra.', 'Uno de los notorios líderes Tótem Siniestro de Sierra Espolón ha vuelto a su hogar.$b$bDeberíamos aprovechar esta oportunidad para asegurarnos de que no vuelve a salir de él.', '', 'Noble de la Cumbre de la Nube Negra. Su linaje debe morir con él.', 'Grundig Nube Negra', '', '', '', 0);
