INSERT INTO `quest_template_locale` VALUES (27323, 'frFR', 'Les chefs du Totem-Sinistre : Grundig Noir-Nuage', 'Tuez Grundig Noir-Nuage.', 'L\'un des infâmes chefs du Totem-Sinistre des Serres-Rocheuses est revenu ici.$b$bNous devrions saisir l\'occasion de veiller à ce qu\'il n’ait plus jamais l\'occasion d\'aller ailleurs.', '', 'Un noble de la Cime de Noir-Nuage. Son nom doit s\'éteindre avec lui.', 'Grundig Noir-Nuage', '', '', '', 0);
