INSERT INTO `quest_template_locale` VALUES (27323, 'ptBR', 'Chefes dos Temível Totem: Grundig Nuvem Negra', 'Mate Grundig Nuvem Negra', 'Um dos mais terríveis líderes dos Temível Totem da Cordilheira das Torres de Pedra voltou para cá.$B$BDevemos aproveitar esta chance e assegurar que ele não vá mais a lugar algum.', '', 'A nobreza do Pináculo da Nuvem Negra. O nome dos Nuvem Negra deve morrer com ele.', 'Grundig Nuvem Negra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27323, 'ruRU', 'Лидеры племени Зловещего Тотема: Грундиг Темное Облако', 'Прикончите Грундига Темное Облако.', 'Вернулся один из самых мерзких предводителей племени Зловещего Тотема в Когтистых горах.$B$BНужно не упустить момент и сделать так, чтобы он остался здесь навечно.', '', 'Родовая знать пика Темного Облака. Его имя будет последним в списке.', 'Грундиг Темное Облако', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27323, 'zhCN', '恐怖图腾酋长：格鲁迪格·黑云', '杀死格鲁迪格·黑云。', '一名臭名昭著的石爪山脉恐怖图腾首领回来了。$B$B我们得抓住机会，别让他跑别处去。', '', '黑云峰的贵族。他的姓氏必须和他一起终结。', '格鲁迪格·黑云', '', '', '', 0);
