INSERT INTO `quest_template_locale` VALUES (27324, 'ruRU', 'Лидеры племени Зловещего Тотема: Грундиг Темное Облако', 'Прикончите Грундига Темное Облако.', 'Вернулся один из самых заслуженных предводителей племени Зловещего Тотема в Когтистых горах.$B$BМы должны использовать шанс и остановить его разрушительный марш раз и навсегда.', '', 'Родовая знать пика Темного Облака. Его имя будет последним в списке.', 'Грундиг Темное Облако', '', '', '', 0);
