INSERT INTO `quest_template_locale` VALUES (27325, 'ptBR', 'Tambores da Guerra', 'Consiga os Tambores da Guerra.', 'Não sabemos o que o Temível Totem está tramando, mas não podemos permitir que fiquem com os Tambores da Guerra.$B$BNão se preocupe, $n. Tudo acabará bem. Porém, primeiro precisamos de todas as partes deste quebra-cabeça.', '', '', '', '', '', '', 0);
