INSERT INTO `quest_template_locale` VALUES (27326, 'itIT', 'Tamburi della Guerra', 'Recupera i Tamburi della Guerra.', 'Qualunque cosa stiano facendo i Totem Truce con questi Tamburi della Guerra, non vogliamo che li abbiano loro.$B$BNon temere, $n, capiremo cosa sta succedendo. Ma prima ci servono tutti i manufatti.', '', '', '', '', '', '', 0);
