INSERT INTO `quest_template_locale` VALUES (27564, 'deDE', 'Verteidigt das Refugium', 'Sprecht mit Calen im Zinnoberrefugium.', 'Calen hat uns benachrichtigt. Der Schattenhammer ist in das Refugium eingedrungen!$B$BWenn Ihr nach einer Gelegenheit sucht, um seine Gunst zu erlangen, dann ist dies der rechte Zeitpunkt!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27564, 'esES', 'En defensa del Reducto', 'Habla con Calen en el Reducto Bermellón.', 'Calen ha enviado un mensaje: ¡los Crepusculares están en el Reducto!$B$BSi hay una ocasión en la que puedas ganarte su favor, ¡es esta!', '', '', '', '', '', '', 0);
