INSERT INTO `quest_template_locale` VALUES (27687, 'esES', 'Gnash se la había ganado', 'Lleva la cabeza de Gnash a Samir en el Agujero del Contrabandista.', '¡Menuda hazaña! ¡Te has cargado a un gigante de mar!$b$bLos nuevos habitantes del Agujero del Contrabandista se sentirán considerablemente más seguros al saber que este monstruo ya no merodea en las inmediaciones de su refugio.', '', '', '', '', '', '', 0);
