INSERT INTO `quest_template_locale` VALUES (27858, 'koKR', '레아스트라자의 선물', '레아가 숨겨진 손아귀에서 알을 되찾아올 때까지 기다려야 합니다.', '여기서 기다리세요. 제가 알을 되찾아올게요.', '', '', '', '', '', '', 0);
