INSERT INTO `quest_template_locale` VALUES (27936, 'frFR', 'Déclenchez l\'avalanche !', 'Tuez Avalanchion.', 'Vous devez y retourner et tuer Avalanchion pour empêcher les troupes de Therazane d\'attaquer Âtrepierre !', '', 'Tuez-le pour empêcher l\'attaque imminente d\'Âtrepierre.', 'Avalanchion', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27936, 'itIT', 'Abbattere Valanghion', 'Uccidi Valanghion.', 'Devi tornare indietro e uccidere Valanghion per impedire alle forze di Therazane di attaccare Cordipietra!', '', 'Uccidilo per impedire l\'imminente attacco su Cordipietra.', 'Valanghion', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27936, 'koKR', '아발란치온 처치', '아발란치온을 처치해야 합니다.', '돌아가서 아발란치온을 처치하고 테라제인의 돌난로 공격을 저지하게!', '', '그를 처치하여 적의 돌난로 공격 계획을 막게.', '아발란치온', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27936, 'ptBR', 'Detendo a avalanche', 'Mate Avalanchion.', 'Você precisa voltar e matar o Avalanchion antes que as tropas de Therazane ataquem o Larpétreo.', '', 'Mate-o para evitar o iminente ataque contra o Larpétreo.', 'Avalanchion', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27936, 'ruRU', 'Победить Лавиниона', 'Убейте Лавиниона.', 'Тебе нужно вернуться и убить Лавиниона, чтобы помешать войскам Теразан напасть на Каменный очаг!', '', 'Убей его, чтобы предотвратить нападение на Каменный очаг!', 'Лавинион', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (27936, 'zhCN', '山崩地裂', '杀死阿瓦兰奇奥。', '你必须回去杀死阿瓦兰奇奥，不然塞拉赞恩的军队就会攻打巨石之炉！', '', '杀死他，阻止即将对巨石之炉发动的进攻。', '阿瓦兰奇奥', '', '', '', 0);
