INSERT INTO `quest_template_locale` VALUES (28024, 'frFR', 'On recherche : des aventuriers pour les souilles de Tranchebauge', 'Parlez à Koristrasza.', 'Le roi Anduin Wrynn somme tout aventurier digne de ce nom de se rendre immédiatement aux souilles de Tranchebauge, dans les Mille pointes !$B$BLa mystérieuse Koristrasza a besoin d’aide pour mettre un terme à l’alliance entre le Fléau et les hurans ! Si vous acceptez, rendez-vous dans les souilles et proposez vos services à Koristrasza.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28024, 'itIT', 'Avventurieri cercasi: Sotterranei di Lamaspina', 'Parla con Koristrasza nei Sotterranei di Lamaspina.', 'Re Anduin Wrynn chiede che tutti gli avventurieri idonei si dirigano immediatamente ai Sotterranei di Lamaspina, a Millepicchi.$B$BLa misteriosa Koristrasza ha bisogno del vostro aiuto per fermare l\'alleanza tra Flagello e Verrospino! Se decidete di accettare, recatevi nei Sotterranei di Lamaspina e parlate con Koristrasza per offrirle i vostri servigi.', '', '', '', '', '', '', 0);
