INSERT INTO `quest_template_locale` VALUES (28025, 'ruRU', 'Требуются герои: курганы Иглошкурых', 'Поговорите с Користразой.', 'По повелению вождя всем отважным искателям приключений надлежит явиться к курганам Иглошкурых в Тысяче Игл!$B$BПремудрой Користразе нужна помощь, чтобы остановить нечистый альянс Плети и свинобразов! Всем, кто отважится, велено отправляться к курганам Иглошкурых и оказать Користразе всемерную поддержку.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'deDE', 'Abenteurer gesucht: Uldaman', 'Sprecht mit dem Obersten Prüfer Tae\'thelan Blutwächter.', 'Der Kriegshäuptling fordert alle tauglichen Abenteurer auf, unverzüglich nach Uldaman unweit Neu-Kargaths zu reisen!$B$BDie Archäologische Akademie und ihr Leiter, der Oberste Prüfer Tae\'thelan Blutwächter, benötigt Unterstützung, um die mächtigen Artefakte im Inneren zu sichern! Wenn Ihr Euch für diesen Weg entscheidet, so begebt Euch nach Uldaman und sprecht mit dem Obersten Prüfer, um sie in ihren Bestrebungen zu unterstützen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'esES', 'Se buscan aventureros: Uldaman', 'Habla con el alto examinador Tae\'thelan Mirasangre.', 'El Jefe de Guerra ha solicitado que todos los aventureros aptos se dirijan a Uldaman, cerca de Nuevo Kargath, ¡de inmediato!$b$b¡El Relicario y su líder, el alto examinador Tae\'thelan Mirasangre, solicitan asistencia para obtener las poderosas reliquias de su interior! Si decides ayudar, dirígete a Uldaman y habla con el alto examinador para contribuir a la causa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'esMX', 'Se buscan aventureros: Uldaman', 'Habla con el alto examinador Tae\'thelan Mirasangre.', 'El Jefe de Guerra ha solicitado que todos los aventureros aptos se dirijan a Uldaman, cerca de Nuevo Kargath, ¡de inmediato!$b$b¡El Relicario y su líder, el alto examinador Tae\'thelan Mirasangre, solicitan asistencia para obtener las poderosas reliquias de su interior! Si decides ayudar, dirígete a Uldaman y habla con el alto examinador para contribuir a la causa.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'frFR', 'On recherche : des aventuriers pour Uldaman', 'Parlez au Haut-examinateur Tae\'thelan Guette-le-sang.', 'Le chef de guerre somme tout aventurier digne de ce nom de se rendre immédiatement à Uldaman, près de la Nouvelle-Kargath !$B$BLe Reliquaire et son dirigeant, le haut-examinateur Tae\'thelan Guette-le-sang, ont besoin d\'aide pour obtenir de puissantes reliques qui sont cachées dans cette cité ! Si vous acceptez, rendez-vous à Uldaman et parlez au haut-examinateur Tae\'thelan Guette-le-sang pour aider la cause du Reliquaire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'itIT', 'Avventurieri cercasi: Uldaman', 'Parla con il Gran Esaminatore Tae\'thelan Mirasangue.', 'Il Capoguerra chiede che tutti gli avventurieri idonei si dirigano immediatamente a Uldaman, vicino a Nuova Kargath.$b$bIl Circolo del Reliquiario e il suo Comandante, il Gran Esaminatore Tae\'thelan Mirasangue, necessitano di aiuto per ottenere delle potenti reliquie! Se decidete di accettare, dirigetevi a Uldaman e parlate con il Gran Esaminatore Tae\'thelan Mirasangue.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'ptBR', 'Procuram-se aventureiros: Uldaman', 'Fale com o Avaliador-mor Tae\'thelan Vigiassangre.', 'O Chefe Guerreiro solicitou que todos os aventureiros fisicamente capazes se dirijam a Uldaman, perto de Nova Karrath, imediatamente!$B$BO Relicário e seu líder, o Avaliador-mor Tae\'thelan Vigiassangre, precisam de ajuda para obter as poderosas relíquias do local! Caso decida ajudar, rume para Uldaman e fale com o Avaliador-mor.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28026, 'ruRU', 'Требуются герои: Ульдаман', 'Поговорите с главным дознавателем Тей\'теланом Кровавым Взором.', 'По повелению вождя всем отважным искателям приключений надлежит явиться в Ульдаман близ Нового Каргата.$B$BКоманде из Реликвария под руководством главного исследователя Тей\'телана Кровавого Взора нужна помощь в поиске древних реликвий. Если вы решите откликнуться на этот призыв, отправляйтесь в Ульдаман и поговорите с главным дознавателем Тей\'теланом. Он скажет, что делать.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'deDE', 'Abenteurer gesucht: Uldaman', 'Sprecht mit dem Oberausgrabungsleiter Durdin.', 'König Anduin Wrynn fordert alle tauglichen Abenteurer auf, unverzüglich nach Uldaman im Ödland zu reisen!$B$BDie Forscherliga und ihr Leiter, der Oberausgrabungsleiter Durdin, benötigen Unterstützung, um gegen die großen Gefahren zu bestehen, die die Jagd nach Wissen mit sich bringt! Wenn Ihr Euch für diesen Weg entscheidet, so begebt Euch nach Uldaman und sprecht mit dem Oberausgrabungsleiter Durdin, um sie in ihren Bestrebungen zu unterstützen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'esES', 'Se buscan aventureros: Uldaman', 'Habla con el prospector jefe Durdin.', 'El rey Anduin Wrynn ha solicitado que todos los aventureros aptos se dirijan a Uldaman, en las Tierras Inhóspitas, ¡de inmediato!$b$b¡La Liga de Expedicionarios, encabezada por el prospector jefe Durdin, solicita asistencia para enfrentarse a graves peligros en busca de conocimiento! Si decides ayudar, dirígete a Uldaman y habla con el prospector jefe Durdin para echarle una mano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'esMX', 'Se buscan aventureros: Uldaman', 'Habla con el prospector jefe Durdin.', 'El rey Anduin Wrynn ha solicitado que todos los aventureros aptos se dirijan a Uldaman, en las Tierras Inhóspitas, ¡de inmediato!$B$B¡La Liga de Expedicionarios, encabezada por el prospector jefe Durdin, solicita asistencia para enfrentarse a graves peligros en busca de conocimiento! Si decides ayudar, dirígete a Uldaman y habla con el prospector jefe Durdin para darle una mano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'frFR', 'On recherche : des aventuriers pour Uldaman', 'Parlez au Prospecteur en chef Durdin.', 'Le roi Anduin Wrynn somme tout aventurier digne de ce nom de se rendre immédiatement à Uldaman, dans les terres Ingrates !$B$BLa Ligue des explorateurs, sous la direction du prospecteur en chef Durdin, a besoin d’aide pour braver le danger dans sa quête de connaissances ! Si vous acceptez, rendez-vous à Uldaman et parlez au prospecteur en chef Durdin pour proposer votre aide.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'itIT', 'Avventurieri cercasi: Uldaman', 'Parla con il Capo Prospettore Durdin.', 'Re Anduin Wrynn chiede che tutti gli avventurieri idonei si dirigano immediatamente a Uldaman, nelle Maleterre.$b$bLa Lega degli Esploratori, guidata dal Capo Prospettore Durdin, ha bisogno di aiuto nella ricerca della conoscenza superiore! Se decidete di accettare, andate a parlare con il Capo Prospettore Durdin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28027, 'ptBR', 'Procuram-se aventureiros: Uldaman', 'Fale com o Prospector-chefe Durdin.', 'O Rei Anduin Wrynn solicita que todos os aventureiros fisicamente capazes se dirijam imediatamente para Uldaman, nos Ermos!$B$BA Liga dos Exploradores, liderada pelo prospector-chefe Durdin, precisa de auxílio para enfrentar grandes perigos em busca de conhecimento! Caso você decida ajudar, vá para Uldaman e fale com o prospector-chefe Durdin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'deDE', 'Belagerung!', 'Tötet 9 Schützen der Dunkeleisenzwerge.', 'Aufseher Schlickfaust hätte sich keinen schlechteren Zeitpunkt aussuchen können, um mich zu bitten, einem Kampf beizutreten. Meine Spitze wird bereits belagert!$b$bNichts, womit meine Wachen nicht umgehen könnten, aber trotzdem... es wird nicht leicht für mich sein, damit weiterzumachen und gleichzeitig Zwerge in diese höllische Schlucht zu entsenden.$b$bIch sage Euch was: Ihr schaltet deren Schützen aus, die unsere Festung belagern, und ich denke noch mal über Euer Angebot nach. Sie schleichen auf den Felsvorsprüngen im Osten herum.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'esES', '¡Asedio!', 'Mata a 9 tiradores Hierro Negro.', 'El sobrestante Puñaceite no podía haber elegido un momento peor para pedirme que me una a la lucha. ¡Mi cima ya está bajo asedio!$B$BNo es nada que mis guardias no puedan controlar; aun así, no me será nada fácil coger a algunos enanos y enviarlos a esa garganta infernal.$B$B¿Sabes qué te digo? ¡Elimina a sus tiradores, que son los que mantienen la fortaleza bajo asedio, y tendré tu oferta en consideración! Estarán escondiéndose en los llanos del este.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'esMX', '¡Asedio!', 'Mata a 9 tiradores Hierro Negro.', 'El sobrestante Puñaceite no podía haber elegido un momento peor para pedirme que me una a la lucha. ¡Mi cima ya está bajo asedio!$B$BNo es nada que mis guardias no puedan controlar; aun así, no me será nada fácil coger a algunos enanos y enviarlos a esa garganta infernal.$B$B¿Sabes qué te digo? ¡Elimina a sus tiradores, que son los que mantienen la fortaleza bajo asedio, y tendré tu oferta en consideración! Estarán escondiéndose en los llanos del este.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'frFR', 'Levez le siège !', 'Tuez 9 Tireurs d’élite sombrefer.', 'Ce surveillant Huilepoing n’aurait pas pu choisir un pire moment pour me demander mon aide. Mon sommet est déjà assiégé !$b$bRien dont mes gardes ne puissent se charger, mais il n’empêche ; je ne peux pas choisir une poignée de Nains et les envoyer au fond de la gorge aussi facilement.$b$bVoilà ma proposition : vous éliminez les tireurs d’élite qui maintiennent ma forteresse en état de siège, et je considérerai votre requête. Vous les trouverez en train de rôder dans la vallée, à l’est.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'itIT', 'Assedio!', 'Uccidi 9 Tiratori Ferroscuro.', 'Il Sovrintendente Pugnograsso non poteva scegliere un momento peggiore per chiedermi di unirmi a una battaglia. La mia vetta è già sotto assedio!$b$bNulla che le mie guardie non possano affrontare, ma non sarà comunque facile mandare dei Nani in quella gorgia infernale.$b$bFacciamo così: tu elimina i tiratori che tengono sotto assedio la nostra fortezza e io prenderò in considerazione la tua richiesta. Li troverai che si aggirano nelle piane a est.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'koKR', '포위 공격이다!', '검은무쇠 명사수 9명을 처치해야 합니다.', '오일피스트 감독관이 때를 잘못 맞춰서 내게 원조를 요청했군. 강철 멧부리가 이미 포위당했으니까 말이야!$b$b물론 내 경비병들이 가볍게 처리하겠지만, 이곳의 드워프들을 선별해 이글거리는 협곡으로 보내는 건 조금 어렵겠네.$b$b이렇게 하지. 자네가 우리 요새를 포위한 검은무쇠 명사수를 처치해 주면 자네의 부탁을 고려해 보겠네. 놈들은 동쪽의 평지 주변에 숨어 있을 것이네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'ptBR', 'Cerco!', 'Mate 9 Atiradores Peritos Ferro Negro.', 'O feitor Mão-de-graxa não poderia ter escolhido uma hora pior para pedir que eu compre uma briga. O meu cume já está sob cerco!$b$bNão é nada que meus guardas não possam resolver, mas, ainda assim, não vai ser fácil para mim simplesmente escolher alguns anões e mandá-los para aquela garganta infernal.$b$bTenho uma ideia: tu abate os atiradores peritos que estão mantendo o cerco em torno da nossa fortaleza e considerarei a sua oferta. Tu vai encontrar eles pelas planícies ao leste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'ruRU', 'Осада!', 'Убейте 9 стрелков из клана Черного Железа.', 'Худшего времени, чтобы попросить меня вступить в бой, надзиратель Скользорук выбрать не мог. Эту вершину и так уже осаждает неприятель!$b$bМои ребята справятся с чем угодно, но все же мне будет нелегко вот так взять и послать несколько дворфов в это дьявольское ущелье.$b$bВот что я тебе скажу: если справишься со стрелками, которые окружили нашу крепость, я рассмотрю твое предложение. Их целая уйма на равнине к востоку отсюда.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'zhCN', '围攻！', '杀掉9名黑铁神射手。', '工头奥菲斯特要我加入战斗，他可真会挑时候。我的山峰正在遭受围攻！$b$b我的矮人不是不能处理，但也不是说我就能简单地收拾收拾，把几个矮人送到地狱般的峡谷去那么简单。$b$b这么说吧：你把他们派来围攻我们要塞的神射手干掉，我就考虑你的提议。他们就溜达在平原的东边。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28028, 'zhTW', '團團包圍!', '殺死9個黑鐵神射手。', '監督者奧菲斯特盡找這種壞時機要我加入戰鬥。敵人已經在圍攻我的黑鐵峰了!$b$b我的守衛有能力面對任何險境，但要我挑出幾個矮人送去那個煉獄般的峽谷，可不是件容易的決定。$b$b這樣吧，你去解決掉那些包圍在堡壘四周的神射手，我就會考慮一下你的提議。他們就潛伏在東邊的平原上。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'deDE', 'Setzt sie in Brand!', 'Benutzt die Fackel der Vergeltung auf den vier Türmen, die den Kessel umgeben.', 'Ich weiß nicht, ob es Euch aufgefallen ist, aber mein Turm ist um einiges größer als die anderen Türme hier in der Schlucht. Manche Zwerge bilden sich etwas darauf ein, wie tief sie graben können... ich nicht. Manchmal glaube ich, ich hätte eigentlich als Wildhammerzwerg zur Welt kommen sollen.$b$bTrotzdem gefällt mir der Anblick dieser anderen Türme nicht. Wie sie sich so in den Himmel recken und meinen Turm an den Zehen kitzeln. Hier, nehmt diese Fackel und benutzt sie an den Kohlenpfannen auf den anderen Türmen. Es wird ihnen noch leidtun, diese Türme überhaupt gebaut zu haben.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'esES', '¡Préndeles fuego!', 'Usa la antorcha de reprensión en las cuatro torres que hay alrededor la Caldera.', 'No sé si lo habrás notado, pero mi torre es mucho más alta que cualquiera de las otras torres de La Garganta. Algunos enanos se enorgullecen de lo profundo que pueden cavar, pero yo no. A veces creo que debería haber sido un Martillo Salvaje.$B$BAunque sigue sin gustarme ver esas otras torres. Alzándose así, haciéndole cosquillas a los pies de mi torre. Toma, coge esta antorcha y úsala para prender los blandones que hay en lo alto de esas otras torres. Lamentarán haberlas construido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'esMX', '¡Préndeles fuego!', 'Usa la antorcha de reprensión en las cuatro torres que hay alrededor la Caldera.', 'No sé si lo habrás notado, pero mi torre es mucho más alta que cualquiera de las otras torres de La Garganta. Algunos enanos se enorgullecen de lo profundo que pueden cavar, pero yo no. A veces creo que debería haber sido un Martillo Salvaje.$B$BAunque sigue sin gustarme ver esas otras torres. Alzándose así, haciéndole cosquillas a los pies de mi torre. Toma, coge esta antorcha y úsala para prender los blandones que hay en lo alto de esas otras torres. Lamentarán haberlas construido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'frFR', 'Allumer le feu !', 'Utilisez la Torche de vindicte sur les quatre tours encerclant le Chaudron.', 'Je ne sais pas si vous avez remarqué, mais ma tour est de loin la plus grande de toutes celles de la gorge. Certains nains tirent une grande fierté de la profondeur à laquelle ils peuvent creuser, mais pas moi. Parfois, je regrette de ne pas être venu au monde chez les Marteaux-hardis.$b$bMalgré tout, je n’apprécie pas la vue de ces autres tours qui semblent me défier et me chatouiller les orteils. Tenez, prenez cette torche et utilisez-la sur les brasiers au sommet des autres tours. Ils vont regretter de les avoir jamais construites.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'itIT', 'Dagli fuoco!', 'Usa la Torcia del Castigo sulle quattro torri che circondano il Calderone.', 'Non so se l\'hai notato, ma la mia torre è molto più alta di tutte le altre della Gorgia. Alcuni Nani vanno fieri delle profondità che riescono a raggiungere scavando, ma non io. Alcune volte penso che sarei dovuto essere un Granmartello.$b$bMa non tollero ancora la vista delle altre torri che si ergono verso l\'alto, sfiorando la base della mia torre. Ecco, prendi questa torcia e usala sui bracieri in cima alle altre torri. Si pentiranno di averle costruite.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'koKR', '화염 공격!', '용광로를 둘러싼 4개의 경비탑에 응보의 횃불을 사용해야 합니다.', '자네가 알았는지는 확실히 모르겠지만, 내 경비탑은 이글거리는 협곡에 있는 어떤 경비탑들 보다도 높은 곳에 있네. 다른 드워프들은 땅을 얼마나 깊이 팔 수 있는지에 자부심을 느끼지만, 난 그렇지 않다네. 가끔 나는 내가 와일드해머에서 태어났어야 한다고 생각한다네.$b$b다른 곳의 경비탑은 여전히 별로 좋아하지 않네. 그렇게 높이 지어봤자 내 경비탑 코끝에도 못 미치거든. 자, 이 횃불을 가져가게. 그리고 다른 경비탑 위에 있는 화로에 사용하게나. 놈들은 처음부터 경비탑을 높게 짓지 않은 것을 후회하게 될 것이네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'ptBR', 'Taca fogo neles!', 'Use a Tocha da Retribuição nas quatro torres ao redor do Caldeirão.', 'Não sei se tu percebeu, mas a minha torre é muito mais alta do que qualquer uma das outras torres da Garganta. Alguns anões se orgulham do quão fundo conseguem cavar, mas não eu. De vez em quando, penso que eu deveria ter nascido um Martelo Feroz.$b$bMas, mesmo assim, não gosto nem de olhar essas outras torres. Aqui, pega esta tocha e usa ela nos braseiros que ficam no topo das outras torres. Primeiro, eles vão se arrepender do dia em que as construíram.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'ruRU', 'Задай им жару!', 'При помощи факела воздаяния подожгите четыре башни, окружающие Котлован.', 'Не знаю, $gзаметил:заметила; ли ты, но мои башни гораздо выше любых других башен ущелья. Некоторые дворфы гордятся тем, как глубоко они копают, но только не я. Иногда мне кажется, что я должен был родиться в клане Громового Молота.$b$bТем не менее, мне не очень нравится, как выглядят те, другие башни. Растут, понимаешь ли, догоняют мои. Вот, возьми этот факел и подожги с его помощью жаровни наверху тех башен. Они еще пожалеют, что вообще построили их.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'zhCN', '点着它们！', '用惩戒火炬点燃大熔炉附近的四座高塔。', '不知道你注意到没有，我的塔比峡谷里所有其他的塔都要高。有些矮人对于能挖多深而感到自豪，但我不。有时我觉得自己该是个蛮锤矮人。$b$b不过，我还是不喜欢那些塔的样子。高高耸起，不过是在给我的塔搔脚底心。给，拿上这火炬，点燃塔顶的火盆。他们会后悔当初干嘛要把它们建起来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28029, 'zhTW', '燒掉它們!', '使用懲戒火炬點燃大熔爐周圍的四座塔。', '不知道你有沒有注意到，我的哨塔比峽谷中其他哨塔都高上許多。有些矮人總為自己能挖得多深感到驕傲，我可不一樣。有時候我覺得我應該生做一個蠻錘矮人。$b$b不過我還是覺得其他哨塔挺礙眼的。像是在給我的塔搔腳底板一樣。喏，拿著火炬，在其他塔的火盆邊使用它。他們會很後悔自己曾經建造這些塔。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'deDE', 'Eine bessere Kugel', 'Beschafft Euch 200 Dunkeleisenkugeln.', 'Diese in der Schlucht grabenden Zwerge machen wenigstens eine Sache richtig: Sie stellen qualitativ hochwertige Patronen her. Es war mir bisher nicht möglich, ihre Ergebnisse zu reproduzieren, weshalb ich dazu gezwungen war, ihnen alle Kugeln, die ich finden konnte, zu stehlen.$b$bIhr wollt meine Hilfe? Besorgt mir ein paar Hundert Dunkeleisenkugeln von ihren Schützen da unten.$b$bIch bin mir sicher, dass sie Euch mit Vergnügen eine Kugel nach der anderen geben werden, aber es könnte einfacher sein, wenn Ihr nach den Kisten Ausschau haltet, in denen sie sie lagern.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'esES', 'Construyen mejores balas', 'Consigue 200 balas Hierro Negro.', 'Esos enanos cavadores de la garganta hacen al menos una cosa bien: hacen unas balas de primera. Yo no he logrado reproducir sus resultados, así que me he dedicado a robar cualquier bala suya que encuentre.$B$B¿Quieres ayudarme? Consígueme unos cuantos cientos de balas de esos tiradores de ahí abajo.$B$BEstoy seguro de que estarán encantados de darte sus balas de una en una, pero puede que te resulte más fácil buscarlas en los cajones que están llenos de ellas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'esMX', 'Construyen mejores balas', 'Consigue 200 balas Hierro Negro.', 'Esos enanos cavadores de la garganta hacen al menos una cosa bien: hacen unas balas de primera. Yo no he logrado reproducir sus resultados, así que me he dedicado a robar cualquier bala suya que encuentre.$B$B¿Quieres ayudarme? Consígueme unos cuantos cientos de balas de esos tiradores de ahí abajo.$B$BEstoy seguro de que estarán encantados de darte sus balas de una en una, pero puede que te resulte más fácil buscarlas en los cajones que están llenos de ellas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'frFR', 'Eh, mec ! T\'as pas deux cent balles ?', 'Obtenez 200 Balles sombrefer.', 'Ces nains fouisseurs font au moins une chose de bien : leurs balles sont d’excellente qualité. Je n’ai jamais réussi à atteindre le même niveau de perfection, donc je me suis mis à leur voler leurs balles dès que j’en ai l’occasion.$b$bVous voulez mon aide ? Vous n’avez qu’à me rapporter deux centaines de balles que leurs tireurs d’élites utilisent.$b$bJe suis sûr qu’ils préfèreraient vous les envoyer une par une, et entre les deux yeux, mais ça ira plus vite si vous leur volez des caisses de munitions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'itIT', 'Hanno costruito un proiettile migliore!', 'Recupera 200 Proiettili di Ferroscuro.', 'I Nani che vivono nella gorgia fanno almeno una cosa come si deve: proiettili di altissima qualità. Non sono stato in grado di riprodurli, quindi ho deciso di rubare tutti i loro proiettili che riesco a trovare.$b$bVuoi il mio aiuto? Portami un paio di centinaia di proiettili di quei tiratori.$b$bSono sicuro che saranno più che felici di darteli uno alla volta, ma potresti fare prima se cercassi le casse dove li stipano.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'koKR', '적군의 탄환', '검은무쇠 탄환 200발을 획득해야 합니다.', '이글거리는 협곡을 파먹는 저 검은무쇠 드워프 놈들도 잘하는 게 딱 하나 있다네. 바로 품질이 아주 좋은 탄환을 만들어내는 것이지. 검은무쇠 부족 탄환 제조법을 밝혀낼 수가 없어서 한때는 놈들이 사용하는 탄환은 모두 찾아서 훔쳐냈었다네.$b$b내 도움이 필요하다고 말했나? 그러면 이 밑에 있는 검은무쇠 명사수들로부터 탄환 몇백 발만 좀 가져다주게나.$b$b물론 놈들이 기쁜 마음으로 자네에게 탄환을 한 발씩 쏴주겠지만, 그것보다는 탄환이 들어 있는 상자를 찾아보는 게 더 빠를 것이네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'ptBR', 'A bala deles é melhor', 'Obtenha 200 Balas de Ferro Negro.', 'Aqueles anões cavadores de garganta sabem fazer ao menos uma coisa: projéteis de alta qualidade. Não consegui reproduzir os seus resultados, então, parti para roubar todos os projéteis deles que eu pude.$b$bVocê quer a minha ajuda? Arranje-me algumas centenas de balas com aqueles atiradores peritos lá embaixo.$b$bEstou certo de que eles não se oporão a entregar um projétil por vez, mas talvez você ache mais rápido procurar os caixotes repletos deles.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'ruRU', 'Пули у них отменные', 'Соберите 200 пуль Черного Железа.', 'По крайней мере одну вещь эти роющиеся в ущелье дворфы делают правильно: пули у них превосходные. У меня не получается добиться такого же качества, поэтому я привык воровать их пули, все, какие найду.$b$bХочешь, чтобы я помог тебе? Принеси мне пару сотен пуль от этих стрелков внизу.$b$bУверен, они охотно поделятся с тобой пулями, отдавая их по одной, но если ты найдешь целые ящики – получится быстрее.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'zhCN', '他们的子弹造得比我们好', '获得200颗黑铁子弹。', '这些挖掘峡谷的矮人至少有一件事情做得不错：他们制造的子弹质量很高。我没法进行仿造，于是我只好尽量把他们的子弹偷来。$b$b你想要我帮忙？那就先从下面的神射手身上给我弄几百发子弹来。$b$b我想他们会很乐意一次给你一颗，不过从弹药箱里去拿可能会更快一些。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28030, 'zhTW', '他們的子彈比較好', '取得200顆黑鐵子彈。', '那些整天在峽谷挖東挖西的矮人至少做對了一件事:他們製造了高級的子彈。我一直沒辦法複製出像樣的成品，所以我決定去把他們的子彈偷過來。$b$b想要我幫你嗎?去給我從神射手那裡拿來幾百顆子彈。$b$b我確定他們一定很樂意一發又一發地把子彈送給你，不過最快的方法，還是找出裝著這些子彈的箱子。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'deDE', 'Sonderlieferung für Langnase', 'Liefert die SchrEIS-Kryokanone an Langnase in der Versunkenen Ausgrabungsstätte in Tausend Nadeln.', 'Ich habe einen neuen Geschäftspartner, meinen besten Freund Langnase. Gerade jetzt arbeitet er daran, die Infrastruktur für unser neues Eiscremeimporium aufzuarbeiten!$B$BEr hat unseren Kühlschlepper, unser gesamtes Inventar, und auch die Mitarbeiter in die Versunkene Ausgrabungsstätte im Südwesten befördert. Leider sehe ich gerade keine Möglichkeit, ihm die nächste Lieferung zukommen zu lassen... eine äußerst wichtige Lieferung: unsere supergeheime SchrEIS-Kryokanone!$B$BEs würd Euch nicht stören, sie ihm zu bringen, oder?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'esES', 'Envío especial para Bridatuer', 'Entrégale el Criocañón E-lado a Bridatuer en la Excavación Sumergida, en Las Mil Agujas.', 'Tengo un nuevo socio empresarial: mi mejor amigo, Bridatuer. ¡Ahora mismo está levantando la infraestructura de nuestro nuevo emporio de helados!$b$bSe llevó nuestro congelante, nuestro inventario y nuestros empleados al suroeste, a la Excavación Sumergida. Por desgracia, en estos momentos no tengo forma de entregarle una cosa... Una cosa muy importante: ¡nuestro supersecreto Criocañón E-lado!$b$b¿Te importa llevárselo, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'esMX', 'Envío especial para Bridatuer', 'Entrégale el Criocañón E-lado a Bridatuer en la Excavación Sumergida, en Las Mil Agujas.', 'Tengo un nuevo socio empresarial: mi mejor amigo, Bridatuer. ¡Ahora mismo está levantando la infraestructura de nuestro nuevo emporio de helados!$b$bSe llevó nuestro congelante, nuestro inventario y nuestros empleados al suroeste, a la Excavación Sumergida. Por desgracia, en estos momentos no tengo forma de entregarle una cosa... Una cosa muy importante: ¡nuestro supersecreto Criocañón E-lado!$b$b¿Te importa llevárselo, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'frFR', 'Une livraison spéciale pour Bravorton', 'Livrez le Cryo-canon Gel-glace à Bravorton, au Site de fouilles englouti, dans les Mille pointes.', 'J’ai un nouvel associé, mon meilleur pote, Bravorton. Au moment où je vous parle, il est en train de bâtir l’infrastructure pour notre nouvel empire glacier !$b$bIl a pris notre remorqueur, l’a chargé de refroidissant, a embarqué matériel et employés, et est parti au sud-ouest vers le site de fouilles englouti. Malheureusement, je n’ai aucun moyen de lui livrer quelque chose de très très important… notre cryo-canon Gel-glace super-secret !$b$bÇa vous embêterait de le lui apporter, $n ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'itIT', 'Consegna speciale per Gelafrutta', 'Consegna il Criocannone Gel Ato a Gelafrutta presso gli Scavi Sommersi a Millepicchi.', 'Ho un nuovo socio, il mio grande amico Gelafrutta. Proprio ora sta costruendo le fondamenta del nostro nuovo impero dei gelati!$B$BHa portato il nostro impianto di refrigerazione, le scorte e i dipendenti a sud-ovest, agli Scavi Sommersi. Purtroppo devo consegnargli qualcosa di molto importante: il nostro segretissimo Criocannone Gel Ato, ma non so come fare!$B$BNon ti dispiace portarglielo tu... vero, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'koKR', '브리블스워프를 위한 특별 배달', '버섯구름 봉우리의 가라앉은 발굴현장에 있는 브리블스워프에게 나도꽁꽁 극저온대포를 가져가야 합니다.', '새로운 사업 동반자가 생겼어요, 바로 제 단짝인 브리블스워프요. 우리가 대화하는 지금 이 순간에도, 그 친구는 새 아이스크림 제국에 들어갈 기반 시설을 만들고 있죠!$B$B그 녀석, 우리의 냉각줄, 각종 장비와 직원들을 데리고 남서쪽에 있는 가라앉은 발굴현장으로 갔어요. 불행히도, 지금은 이걸 녀석에게 전해줄 방법이 없네요... 진짜 중요한 건데. 바로 대박 비밀리에 제작한 나도꽁꽁 극저온대포에요!$B$B당신이 전해 주실 수 있겠죠, $n 님?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'ptBR', 'Entrega especial para Pena Tabwa', 'Entregue o Canhão Criogênico de Pico-léguas para Pena Tabwa no Sítio Arqueológico Submerso, que fica em Mil Agulhas.', 'Arrumei um novo sócio! Meu melhor amigo, Pena Tabwa. Neste exato momento, ele está construindo a infraestrutura do nosso novo império de picolés!$B$BEle levou nossos empregados, o estoque de mercadorias e a máquina de resfriamento para o sítio arqueológico submerso, a sudoeste. Infelizmente, no momento não tenho como entregar algo para ele... algo extremamente importante: nosso supersecreto canhão criogênico de Pico-léguas!$B$BVocê se importaria de levá-lo para o Pena, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'ruRU', 'Особая посылка для Бривельверпа', 'Отнесите криопушку \"Морозко\" Бривельверпу на Затопленные раскопки в Тысяче Игл.', 'У меня новый деловой партнер – мой лучший друг Бривельверп. Пока мы тут лясы точим, он закладывает фундамент нашей новой империи мороженого!$B$BБривельверп отвел наш холодильный буксир, оборудование и рабочих на юго-запад, к Затопленным раскопкам. К сожалению, у меня нет возможности отправить ему кое-что... кое-что очень важное: нашу сверхсекретную криопушку \"Морозко\"!$B$BТы не $gмог:могла; бы отнести ее Бривельверпу, $n?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'zhCN', '给布里维普的特别货物', '将激冻冰弹交给千针石林沉没的挖掘场的布里维普。', '我有了一个新的合伙人，我最好的伙伴，布里维普。我们说话的这当口，他正在为我们全新的冰激凌帝国缔造基础呢！$B$B他把我们的冷却液拖船、库存，还有雇员都带到西南边的沉没的挖掘场去了。不幸的是，我现在没办法把那东西送过去……一样十分重要的东西：我们超级绝密的激冻冰弹！$B$B你不介意把它给他带去吧，对不对，$n？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28031, 'zhTW', '給布里維普的特別快遞', '將驚聲尖嘯冷凍火砲帶去千針石林裡沉沒的挖掘場給布里維普。', '我有一位新的商業夥伴，我最好的朋友布里維普。在我們談話的同時，他正在為了嶄新的冰淇淋王國興建設備呢！$B$B他帶走了我們的冷卻劑、貨物和工人前往西南方的沉沒的挖掘場。不幸的是，我目前沒有辦法託送東西給他…是個很重要的東西：我們的超級機密「驚聲尖嘯冷凍火砲」！$B$B你不介意替我送去給他吧，會嗎，$n？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'deDE', 'Die Geheimnisse des Feuermuskelmagens', 'Sammelt 6 Feuermuskelmägen.', 'Es gibt so vieles, was wir bei den Incendosauriern nicht wissen. Woher kommen sie? Wie überleben sie hier draußen in den Ebenen? Und warum schmecken sie so überhaupt nicht?$b$bDer Schlüssel zum Verständnis dieser prähistorischen Bestien liegt, so glaube ich, im Feuermuskelmagen. Jeder Incendosaurier verfügt über einen. Ich hätte gerne ein paar davon, um sie zu untersuchen.$b$bIhr findet Incendosaurier, die sich am Fuße des Eisengipfels aufhalten, hauptsächlich im Westen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'esES', 'Los misterios de las mollejas de fuego', 'Consigue 6 mollejas de fuego.', 'Hay tantas cosas que ignoramos de los incendiosauros. ¿De dónde vinieron? ¿Cómo sobreviven aquí, en los llanos? ¿Y por qué saben tan mal?$B$BCreo que la clave para comprender a estas bestias prehistóricas está en las mollejas de fuego. Todos los incendiosauros tienen una. Me gustaría tener unas cuantas para estudiarlas.$B$BEncontrarás incendiosauros alrededor del pie de la Cima de Hierro, más bien al oeste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'esMX', 'Los misterios de las mollejas de fuego', 'Consigue 6 mollejas de fuego.', 'Hay tantas cosas que ignoramos de los incendiosauros. ¿De dónde vinieron? ¿Cómo sobreviven aquí, en los llanos? ¿Y por qué saben tan mal?$B$BCreo que la clave para comprender a estas bestias prehistóricas está en las mollejas de fuego. Todos los incendiosauros tienen una. Me gustaría tener unas cuantas para estudiarlas.$B$BEncontrarás incendiosauros alrededor del pie de la Cima de Hierro, más bien al oeste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'frFR', 'Les mystères du gésier-feu', 'Obtenez 6 Gésiers-feu.', 'Nous connaissons si peu de choses sur les incendosaures. D’où viennent-ils ? Comment survivent-ils dans cette vallée ? Et pourquoi leur viande a-t-elle si mauvais goût ?$b$bJe pense que la clé pour comprendre ces animaux préhistoriques réside dans le gésier-feu. Les incendosaures en ont tous un. Vous pourriez m’en ramener quelques-uns pour que je les étudie ?$b$bVous trouverez des incendosaures tout autour du pied du sommet de Fer, mais surtout à l’ouest.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'itIT', 'I misteri dello Stomaco di Fuoco', 'Ottieni 6 Stomaci di Fuoco.', 'Sappiamo così poco degli Incendiosauri. Da dove vengono? Come sopravvivono nelle piane? E perché hanno un sapore così cattivo?$b$bRitengo che la chiave per comprendere queste bestie preistoriche stia nello Stomaco di Fuoco. Ogni Incendiosauro ne ha uno. Ne vorrei qualcuno per studiarlo.$b$bTroverai gli Incendiosauri ai piedi della Vetta di Ferro, verso ovest.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'koKR', '불꽃 모래주머니의 비밀', '불꽃 모래주머니 6개를 획득해야 합니다.', '우리는 인센도사우루스에 대해서 모르는 게 너무 많답니다. 놈들은 어디서 온 것인가? 어떻게 이곳의 평원에서 살아남은 것인가? 그리고 왜 그렇게 맛이 없나?$b$b이 선사시대의 야수를 이해하려면, 놈들이 가진 불꽃 모래주머니부터 모으는 게 중요하다고 생각해요. 인센도사우루스라면 모두 가지고 있는 기관이죠. 연구 목적으로 몇 개 있었으면 좋겠어요.$b$b놈들은 보통 서쪽의 강철 멧부리 기슭을 돌아다닌답니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'ptBR', 'Os mistérios da moela de fogo', 'Obtenha 6 Moelas de Fogo.', 'Ainda falta sabermos muito sobre os incendossauros. De onde eles vieram? Como eles sobrevivem por aqui? E por que o gosto deles é tão ruim?$b$bA chave para o entendimento dessas feras pré-históricas, creio eu, começa com a moela de fogo. Todo incendossauro tem uma. Eu gostaria de estudar algumas delas.$b$bVocê encontrará incendossauros circulando a base do Auge Férreo, principalmente a oeste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'ruRU', 'Тайны пламенной кишки', 'Добудьте 6 пламенных кишок.', 'Мы очень многого не знаем о пламезаврах. Откуда они взялись? Как они выживают на этих равнинах? И почему они такие мерзкие на вкус?$b$bДумаю, что ключ к секрету этих доисторических животных – в их пламенных кишках. Они есть у всех пламезавров. И я бы хотела иметь несколько для изучения.$b$bПламезавров можно найти у Железной вершины, в основном в западной части.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'zhCN', '火囊的秘密', '获得6副火囊。', '有很多关于熏火龙的事情我们还不知道。它们从哪儿来？它们是如何在平原上生存下来的？还有它们的肉为什么这么难吃？$b$b我相信，了解这些史前野兽的关键，就要从火囊着手。每条熏火龙都有一副火囊。我想要几副来研究研究。$b$b你可以在偏西边的铸铁峰那里找到熏火龙。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28032, 'zhTW', '火焰胃囊的奧秘', '收集6個火焰胃囊。', '關於燻火龍我們還有許多不清楚的地方。他們是從哪裡來的？他們怎麼在這片平原上生存？還有，他們的肉怎麼這麼難吃？$b$b暸解這些史前野獸的關鍵，我想，就在火焰胃囊。每個燻火龍都有一個火焰胃囊。我想找一些來研究。$b$b你可以在黑鐵峰底部發現這些燻火龍，絕大多數都在西邊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'deDE', 'Betrug', 'Findet einen Weg, den Bergfürsten Rendan davon zu überzeugen, sich der Thoriumbruderschaft anzuschließen.', 'Was? Warum seid Ihr noch immer hier? Oh, habe ich versprochen, dass ich Euch und der Thoriumbruderschaft in Eurem Kampf helfen würde?$b$bIch habe gelogen.$b$bIch habe im Moment einfach keine Männer übrig. Ich weiß, ich habe gesagt, ich würde helfen, aber ich werde mich unter keinen Umständen von Euch überzeugen lassen, den Eisengipfel jetzt zu verlassen. Unter keinen Umständen.', '', '', '', '', '', 'Sprecht mit Lunk auf dem Eisengipfel in der Sengenden Schlucht.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'esES', 'Engaño', 'Encuentra la forma de convencer al señor de la montaña Rendan para que se una a la Hermandad del Torio.', '¿Qué? ¿Por qué sigues aquí? Oh, ¿prometí que os ayudaría a ti y a la Hermandad del Torio en vuestra lucha?$B$BMentí.$B$BNo puedo prescindir de hombres ahora mismo. Sé que dije que os ayudaría, pero no podrás convencerme de ninguna manera para que deje la Cima de Hierro ahora. De ninguna manera.', '', '', '', '', '', 'Habla con: Ñoño. Zona: Cima de Hierro, La Garganta de Fuego.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'esMX', 'Engaño', 'Encuentra la forma de convencer al señor de la montaña Rendan para que se una a la Hermandad del Torio.', '¿Qué? ¿Por qué sigues aquí? Oh, ¿prometí que los ayudaría a ti y a la Hermandad del Torio en su lucha?$B$BMentí.$B$BNo puedo prescindir de hombres ahora mismo. Sé que dije que los ayudaría, pero no podrás convencerme de ninguna manera para que deje la Cima de Hierro ahora. De ninguna manera.', '', '', '', '', '', 'Habla con: Ñoño. Zona: Cima de Hierro, La Garganta de Fuego.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'frFR', 'Parjure !', 'Trouvez le moyen de convaincre le Seigneur de la montagne Rendan de se rallier à la Confrérie du thorium.', 'Quoi ? Vous êtes encore là ? Vous dites que je vous ai promis d’aider la Confrérie du thorium dans son combat ?$b$bEh bien, j’ai menti.$b$bEn ce moment, je ne peux me passer d’aucun de mes hommes. Je sais que j’ai dit que je vous aiderais, mais dans l’état actuel des choses, absolument rien ne pourrait me convaincre de quitter le sommet de Fer. Absolument rien.', '', '', '', '', '', 'Parlez à Benêt sur le Sommet de Fer, dans la Gorge des Vents brûlants.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'itIT', 'Inganno', 'Trova un modo per convincere Rendan il Signore delle Montagne a unirsi alla Fratellanza del Torio.', 'Cosa? Perché sei ancora qui? Oh, avevo promesso che avrei aiutato te e la Fratellanza del Torio nella vostra battaglia?$b$bMentivo.$b$bAl momento non ho abbastanza uomini. So che avevo detto che ti avrei aiutato, ma adesso come adesso non riuscirai a convincermi in nessun modo a lasciare Vetta di Ferro. Assolutamente nessuno.', '', '', '', '', '', 'Parla con Lunk sulla Vetta di Ferro nella Gorgia Rovente.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'koKR', '속임수', '산의 제왕 렌단이 토륨 대장조합에 합류하도록 설득할 방법을 찾아야 합니다.', '뭔가? 자네 왜 아직도 여기 있나? 아, 내가 토륨 대장조합과 자네가 필요한 원조를 해 주겠다고 약속했던가?$b$b그건 거짓말이었네.$b$b지금 당장은 인원을 내줄 수가 없네. 내가 도와주겠다고 말했던 건 알겠네. 그렇지만 지금으로서는 내가 강철 멧부리를 떠나도록 설득할 방법은 없네. 말도 안 되는 일이지.', '', '', '', '', '', '이글거리는 협곡의 강철 멧부리에 있는 돌머리와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'ptBR', 'Trapaça', 'Encontre uma forma de convencer o Senhor da Montanha Rendan a se juntar à Irmandade do Tório.', 'O quê? O que você ainda está fazendo aqui? Ah, eu prometi que eu ajudaria você e a Irmandade do Tório na batalha?$b$bEu menti.$b$bEu simplesmente não posso abrir mão dos homens agora. Sei que eu disse que ia ajudar, mas você não vai me convencer a deixar o Auge Férreo agora. De jeito nenhum.', '', '', '', '', '', 'Fale com o Lanque no Auge Férreo, que fica na Garganta Abrasadora.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'ruRU', 'Обман', 'Найдите способ убедить горного владыку Рендана присоединиться к Братству Тория.', 'Что? Почему ты все еще здесь? А, я обещал, что помогу тебе и Братству Тория в вашей битве?$b$bЯ врал.$b$bЯ просто не могу выделить бойцов. Знаю, я обещал помочь, но вы никак не убедите меня покинуть сейчас Железную вершину. Абсолютно никак.', '', '', '', '', '', 'Поговорите с Чурбаном на Железной вершине в Тлеющем ущелье.', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'zhCN', '说客', '想办法说服山丘领主伦达恩加入瑟银兄弟会。', '干嘛？你怎么还在这儿？哦，我是不是答应你还有瑟银兄弟会要帮他们打仗来着？$b$b我扯谎了。$b$b我现在分不出人手来。我知道我说过会帮忙，但你现在是不可能说服我离开铸铁峰的。绝对不可能。', '', '', '', '', '', '跟灼热峡谷铸铁峰的隆克谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (28033, 'zhTW', '詐欺', '找個方法說服山丘統領蘭登加入瑟銀兄弟會。', '什麼？你怎麼還在這兒？哦，我答應過你會幫助瑟銀兄弟會作戰？$b$b我騙了你。$b$b我現在騰不出多餘的人手。我知道我說過會幫忙，不過無論如何，這當下你是不可能說服我離開黑鐵峰。絕不可能。', '', '', '', '', '', '到灼熱峽谷的黑鐵峰找郎克說話。', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'deDE', 'Lunks Abenteuer: Rendans Schwäche', 'Sprecht mit drei Eisengipfelwachen und bittet sie, bei Eurem Tanz mitzumachen.', 'Lunk nicht klug, aber Lunk fallen Dinge auf. Heute ist Lunk aufgefallen, dass Bergzwerg es mag, Leute tanzen zu sehen.$b$bLunk mag tanzen! $n mag tanzen! Wahrscheinlich!$b$bVielleicht finden wir mehr Zwerge die tanzen mögen. Dann wir reden mit Bergzwerg und er wird $n zuhören.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'esES', 'La aventura de Ñoño: la debilidad de Rendan', 'Habla con tres guardias de Cima de Hierro y pídeles que se unan a tu baile.', 'Ñoño no ser listo, pero Ñoño darse cuenta de cosas. Hoy, Ñoño darse cuenta de que a enano de la montaña gustar ver gente bailar.$B$B¡A Ñoño gustar bailar! ¡A $n gustar bailar! ¡Tal vez!$B$BQuizás nosotros encontrar más enanos que gustar bailar. Después nosotros hablar con enano de la montaña y él escuchar a $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'esMX', 'La aventura de Ñoño: la debilidad de Rendan', 'Habla con tres guardias de Cima de Hierro y pídeles que se unan a tu baile.', 'Ñoño no ser listo, pero Ñoño darse cuenta de cosas. Hoy, Ñoño darse cuenta de que a enano de la montaña gustar ver gente bailar.$B$B¡A Ñoño gustar bailar! ¡A $n gustar bailar! ¡Tal vez!$B$BQuizás nosotros encontrar más enanos que gustar bailar. Después nosotros hablar con enano de la montaña y él escuchar a $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'frFR', 'L’aventure de Benêt : le péché mignon de Rendan', 'Parlez à trois Gardes du sommet de Fer et demandez-leur d’entrer dans la danse.', 'Benêt n’est pas intelligent, mais Benêt est observateur. Aujourd’hui, Benêt a remarqué que le chef de la montagne aime regarder les gens danser.$b$bBenêt adore danser ! $n aussi adore sûrement danser !$b$bOn devrait peut-être trouver d’autres nains qui aiment danser, puis on ira parler au chef de la montagne, et il écoutera $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'itIT', 'L\'avventura di Lunk: il punto debole di Rendan', 'Parla con tre Guardie della Vetta di Ferro e chiedi loro di unirsi alla tua danza.', 'Lunk non è intelligente, ma Lunk nota le cose. Oggi Lunk ha notato che al Nano delle Montagne piace guardare la gente ballare.$b$bA Lunk piace ballare! A $n piace ballare! Probabilmente!$b$bForse troveremo altri Nani a cui piace ballare. Allora andiamo a parlare con il Nano delle Montagne e lo convinceremo ad ascoltare $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'koKR', '돌머리의 모험: 렌단의 약점', '강철 멧부리 경비병과 대화해 춤추는 것에 합류하도록 요청해야 합니다.', '돌머리 안 똑똑하다. 그런데 그건 안다. 돌머리 오늘 그 산의 제왕 드워프가 사람들 춤추는 모습 보는 거 좋아하는 거 알았다.$b$b돌머리도 춤추는 거 좋아한다! $n도 춤추는 거 좋아한다! 그럴 거야!$b$b춤추기 좋아하는 다른 드워프들 찾자. 그리고 다시 산의 제왕 드워프에게 말해. 그러면 $n 말 들어줄 거다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'ptBR', 'A aventura do Lanque: o ponto fraco do Rendan', 'Fale com três Guardas do Auge Férreo e pergunte se eles querem participar da sua dança.', 'O Lanque não é esperto, mas o Lanque percebe as coisas. Hoje, o Lanque percebe que o anão da montanha gosta de ver gente dançar.$b$bO Lanque gosta de dançar! $gO:A; $n gosta de dançar! Deve gostar!$b$bQuem sabe a gente não encontra mais anões que gostam de dançar? Depois, a gente vai falar com o anão da montanha, e ele vai escutar $go:a; $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'ruRU', 'Приключения Чурбана: слабость Рендана', 'Поговорите с тремя стражами Железной вершины и попросите их присоединиться к танцу.', 'Чурбан не очень умный, но зато наблюдательный. Сегодня Чурбан заметил, что этому горному дворфу нравится смотреть, как танцуют.$b$bЧурбан любит танцевать! $n любит танцевать!$b$bМожет быть, мы найдем еще дворфов, которые любят танцевать. А потом пойдем к этому горному дворфу, и он послушается |3-1($n).', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'zhCN', '隆克的冒险：伦达恩的弱点', '与三名铸铁峰守卫交谈，让他们加入你的舞蹈团。', '隆克不聪明，但隆克会观察。今天，隆克观察到山丘矮人喜欢看人跳舞。$b$b隆克喜欢跳舞！$n喜欢跳舞！大概吧！$b$b或许我们可以找些喜欢跳舞的矮人来。然后我们去跟山丘矮人谈谈，他就会听$n的了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28034, 'zhTW', '郎克大冒險:蘭登的弱點', '邀請三個黑鐵峰守衛和你一起跳舞。', '郎克不聰明，但郎克會注意到一些小地方。今天，郎克注意到那個山矮人喜歡看人跳舞。$b$b郎克最愛跳舞了!$n也愛跳舞!或許吧!$b$b我們可能要找更多喜歡跳舞的矮人。然後去和那個山矮人講話，他就會聽$n的話。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'deDE', 'Die Unterstützung des Bergfürsten', 'Berichtet Aufseher Schlickfaust auf der Thoriumsspitze in der Sengenden Schlucht von Eurem Erfolg.', 'In Ordnung, in Ordnung! Wenn ich gewusst hätte, dass Ihr so tanzen könnt, dann hätte ich Euch nicht den ganzen anderen Kram machen lassen.$b$bSagt Aufseher Schlickfaust, dass ich mich Euch anschließen werde. Ich treffe Euch in der Schlackengrube, $gKumpel: Mädel;! Mit den vereinten Kräften der Thoriumsspitze und des Eisengipfels werden die Schluchtzwerge gar nicht wissen, wie ihnen geschieht!', '', '', '', '', '', 'Meldet Euch bei Aufseher Schlickfaust auf der Thoriumspitze in der Sengenden Schlucht.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'esES', 'El apoyo del Señor de la montaña', 'Informa de tu éxito al sobrestante Puñaceite en el Puesto del Torio, en La Garganta de Fuego.', '¡Vale, vale! De haber sabido que bailabas así, no te hubiera pedido antes que hicieras todo eso.$B$BDile al sobrestante Puñaceite que me uniré a vosotros. ¡Nos encontraremos en la Fosa de la Escoria, $gmuchacho:muchacha;! Con las fuerzas combinadas del Puesto del Torio y de la Cima de Hierro, ¡esos enanos de La Garganta no sabrán de dónde vienen los golpes!', '', '', '', '', '', 'Preséntate ante: Sobrestante Puñaceite. Zona: Puesto del Torio, La Garganta de Fuego.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'esMX', 'El apoyo del Señor de la montaña', 'Informa de tu éxito al sobrestante Puñaceite en el Puesto del Torio, en La Garganta de Fuego.', '¡Bien, bien! De haber sabido que bailabas así, te hubiera pedido antes que hicieras todo eso.$B$BDile al sobrestante Puñaceite que me uniré a ustedes. ¡Nos encontraremos en la Fosa de la Escoria, $gmuchacho:muchacha;! Con las fuerzas combinadas del Puesto del Torio y de la Cima de Hierro, ¡esos enanos de La Garganta no sabrán de dónde vienen los golpes!', '', '', '', '', '', 'Preséntate ante: Sobrestante Puñaceite. Zona: Puesto del Torio, La Garganta de Fuego.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'frFR', 'Le concours du seigneur de la montagne', 'Faites part du succès de votre mission au Surveillant Huilepoing à la Halte du Thorium, dans la Gorge des Vents brûlants.', 'D’accord, d’accord ! Si j’avais su que vous étiez si $gbon danseur:bonne danseuse;, je ne vous aurais pas imposé toutes ces corvées.$b$bDites au surveillant Huilepoing que je me joindrai à lui. Retrouvons-nous dans la fosse aux Scories, $gl’ami:l’amie; ! En combinant les forces de la halte du Thorium et du sommet de Fer, les nains de la gorge ne verront même pas venir le coup !', '', '', '', '', '', 'Faites votre rapport au Surveillant Huilepoing, dans la Gorge des Vents brûlants.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'itIT', 'L\'aiuto del Signore delle Montagne', 'Comunica il tuo successo al Sovrintendente Pugnograsso al Presidio di Torio nella Gorgia Rovente.', 'Va bene, va bene! Se mi avessi detto che sapevi ballare così, non ti avrei chiesto di fare tutte quelle cose.$b$bFai sapere al Sovrintendente Pugnograsso che mi unirò a voi. Ci vediamo alla Fossa delle Scorie, $gragazzo:ragazza;! Ora che le forze di Presidio del Torio e di Vetta di Ferro sono unite, i Nani della Gorgia non avranno scampo!', '', '', '', '', '', 'Fa\' rapporto al Sovrintendente Pugnograsso al Presidio di Torio nella Gorgia Rovente.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'koKR', '산의 제왕의 원조', '이글거리는 협곡의 토륨 거점에 있는 감독관 오일피스트에게 렌단을 합류시키는 것에 성공했다고 보고해야 합니다.', '알겠네, 알겠어! 자네가 이렇게 춤을 잘추는 줄 알았다면 일전에 그런 자잘한 일은 시키지 않았을 것이네.$b$b오일피스트 감독관에게 내가 합류하겠다고 전해주게. 자네는 잿가루 채석장에서 나와 만나지! 토륨 거점과 강철 멧부리의 병력을 합치면 검은무쇠 드워프놈들은 손 한 번 제대로 못 쓰고 무너질 걸세!', '', '', '', '', '', '이글거리는 협곡의 토륨 거점에 있는 감독관 오일피스트에게 보고해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'ptBR', 'O apoio do Senhor da Montanha', 'Reporte o seu sucesso ao Feitor Mão-de-graxa no Posto de Tório, que fica na Garganta Abrasadora.', 'Tudo bem, tudo bem! Se eu soubesse que tu dançava assim, eu não tinha pedido para tu fazer todas aquelas coisas.$b$bAvise ao feitor Mão-de-graxa que vou me juntar a vocês. Te encontro no fosso de lava, $grapaz:moça;! Com as forças combinadas do Posto de Tório e do Auge Férreo, aqueles anões da garganta não vão nem saber o que os acertou!', '', '', '', '', '', 'Fale com o Feitor Mão-de-graxa no Posto de Tório, que fica na Garganta Abrasadora.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'ruRU', 'Поддержка горного владыки', 'Отчитайтесь о вашем успехе надзирателю Скользоруку в лагере Братства Тория в Тлеющем ущелье.', 'Ладно, ладно! Если бы я знал, что вы так танцуете, я бы не стал вас просить делать все остальное.$b$bСкажите надзирателю Скользоруку, что я присоединюсь. Встретимся в Шлаковой яме! Когда Братство Тория объединится с Железной вершиной, эти дворфы из ущелья вообще не поймут, кто на них напал!', '', '', '', '', '', 'Поговорите с надзирателем Скользоруком в Братстве Тория, что в Тлеющем ущелье.', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'zhCN', '山丘领主的支援', '把你的成功报告给灼热峡谷瑟银哨塔的工头奥菲斯特。', '好吧，好吧！要是我知道你这么会跳舞，一开始我就不会让你去做那些杂活儿。$b$b告诉工头奥菲斯特，我会加入他。咱们熔渣之池见啦，$g小子:姑娘;！只要瑟银哨塔和铸铁峰的兵力联合起来，那些峡谷矮人死都不知道怎么死的！', '', '', '', '', '', '去找灼热峡谷瑟银哨塔的工头奥菲斯特。', 0);
INSERT INTO `quest_template_locale` VALUES (28035, 'zhTW', '山大王的協助', '到灼熱峽谷的瑟銀哨塔，向監督者奧菲斯特報告你的成果。', '好吧，好吧!要是當初我知道你舞跳得這麼棒，我就不會要求你做那些事了。$b$b告訴監督者奧菲斯特我會加入你們。我會到熔渣之池和你們會合，$g小夥子:小姑娘;!只要瑟銀哨塔和黑鐵峰通力合作，那些峽谷的矮人連自己是怎麼掛的都不會知道!', '', '', '', '', '', '到灼熱峽谷的瑟銀哨塔向監督者奧菲斯特回報。', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'deDE', 'Abenteurer gesucht: Zul\'farrak', 'Sprecht mit Tran\'rek.', 'König Anduin Wrynn fordert alle tauglichen Abenteurer auf, unverzüglich nach Zul\'farrak westlich von Gadgetzan zu reisen!$B$BGadgetzan wird von den Trollen der Sandwüter und ihrer wachsenden Armee bedroht! Falls Ihr interessiert seid, begebt Euch nach Zul\'farrak und sprecht mit Tran\'rek, um den Goblins zu helfen.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'esES', 'Se buscan aventureros: Zul\'Farrak', 'Habla con Tran\'rek.', 'El rey Anduin Wrynn ha solicitado que todos los aventureros aptos se dirijan a Zul\'Farrak, al oeste de Gadgetzan, ¡de inmediato!$b$b¡Los trols Furiarena y su creciente ejército son una amenaza para Gadgetzan! Si decides ayudar, dirígete a Zul\'Farrak y habla con Tran\'rek para ayudar a los goblins.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'esMX', 'Se buscan aventureros: Zul\'Farrak', 'Habla con Tran\'rek.', 'El rey Anduin Wrynn ha solicitado que todos los aventureros aptos se dirijan a Zul\'Farrak, al oeste de Gadgetzan, ¡de inmediato!$B$B¡Los trols Furiarena y su creciente ejército son una amenaza para Gadgetzan! Si decides ayudar, dirígete a Zul\'Farrak y habla con Tran\'rek para ayudar a los goblins.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'frFR', 'On recherche : des aventuriers pour Zul\'farrak', 'Parlez à Tran\'rek.', 'Le roi Anduin Wrynn somme tout aventurier digne de ce nom de se rendre immédiatement à Zul’farrak, à l’ouest de Gadgetzan !$B$BGadgetzan est menacée par les Trolls furie-des-sables et leur armée en expansion ! Si vous acceptez, rendez-vous à Zul’farrak et parlez à Tran’rek pour aider les gobelins à remédier à cette situation.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'itIT', 'Avventurieri cercasi: Zul\'farrak', 'Parla con Tran\'rek.', 'Re Anduin Wrynn chiede che tutti gli avventurieri idonei si dirigano immediatamente a Zul\'farrak, a ovest di Meccania.$b$bMeccania è minacciata dai Troll Sabbiafurente e dal loro esercito! Se decidete di accettare, recatevi a Zul\'farrak e parlate con Tran\'rek per aiutare i Goblin.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28036, 'ptBR', 'Procuram-se aventureiros: Zul\'farrak', 'Fale com Tran\'rek.', 'O Rei Anduin Wrynn requisita que todos os aventureiros capazes sigam imediatamente para Zul\'farrak, a oeste de Geringontzan!$B$BGeringontzan está sendo ameaçada pelos trolls Zangareia e seu cada vez mais numeroso exército! Se você assim desejar, siga para Zul\'farrak e fale com Tran\'rek para ajudar os goblins.', '', '', '', '', '', '', 0);
