INSERT INTO `quest_template_locale` VALUES (28070, 'itIT', 'Avventurieri cercasi: Bastioni di Roccianera', 'Parla con Acride.', 'Il Capoguerra chiede che tutti gli avventurieri idonei si dirigano immediatamente ai Bastioni di Roccianera, a ovest di Cimiero Ardente.$B$BLe armate di Nefarian e i seguaci di Rend Manonera minacciano la stessa esistenza dell\'Orda! Se decidete di accettare, dirigetevi ai Bastioni di Roccianera e parlate con Acride, il nostro unico contatto all\'interno che potrebbe aiutarci a fermare l\'imminente invasione.', '', '', '', '', '', '', 0);
