INSERT INTO `quest_template_locale` VALUES (28082, 'frFR', 'On recherche : des aventuriers pour la Scholomance', 'Pénétrez dans la Scholomance.', 'Le roi Anduin Wrynn somme tout aventurier digne de ce nom de se rendre immédiatement à la Scholomance, à l’est d’Andorhal !$B$BDe récentes activités nécromantiques dans les alentours semblent avoir pour auteur le sombre maître Gandling, qui cherche apparemment à former de puissants sorciers au bénéfice du Fléau. Si tel est votre choix, rendez-vous à la Scholomance pour libérer les Maleterres de ce bastion du mal.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28082, 'itIT', 'Avventurieri cercasi: Scholomance', 'Entra a Scholomance.', 'Re Anduin Wrynn chiede che tutti gli avventurieri idonei si dirigano immediatamente a Scholomance, a est di Andorhal.$B$BLe recenti attività negromantiche nell\'area sono riconducibili al Maestro Oscuro Gandling e a un rinnovato tentativo di addestrare nuovi occultisti per il Flagello! Se decidete di accettare, recatevi a Scholomance nelle Terre Infette e ripulite una volta per tutte questo bastione del male.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28082, 'ptBR', 'Procuram-se aventureiros: Scolomântia', 'Entre em Scolomântia.', 'Sua Majestade o Rei Anduin Wrynn solicita que aventureiros fisicamente capazes dirijam-se a Scolomântia, a leste de Andorhal, imediatamente!$B$BAtividade necromântica recente indica a presença do umbromestre Gandling e novos esforços para treinar feiticeiros poderosos para o flagelo. Se assim desejar, siga para Scolomântia e expurgue esse baluarte do mal das Terras Pestilentas.', '', '', '', '', '', '', 0);
