INSERT INTO `quest_template_locale` VALUES (28083, 'ruRU', 'Требуются герои: Некроситет', 'Войдите в Некроситет.', 'По повелению вождя всем отважным искателям приключений надлежит незамедлительно явиться в Некроситет, что к востоку от Андорала!$B$BПовышенная активность некромантов в тех местах указывает на темного магистра Гандлинга. По всей видимости в Некроситете по-прежнему усердно готовят могущественных колдунов для войск Плети! Если вы решите откликнуться на этот призыв, отправляйтесь в Некроситет, чтобы очистить этот бастион зла.', '', '', '', '', '', '', 0);
