INSERT INTO `quest_template_locale` VALUES (28085, 'deDE', 'Ärger in Steilhang', 'Sprecht mit Pao\'ka Bergrausch in Steilhang in Tausend Nadeln.', 'Vielen Dank für Eure Hilfe, $n. Ihr solltet Euch mit Pao\'ka Bergrausch in Steilhang unterhalten, südwestlich von hier.$B$BVielleicht weiß er, wo sich die Ältestengreisin befindet?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'esES', 'Problemas en Nido Alto', 'Habla con Pao\'ka Monte Presto en Nido Alto, en Las Mil Agujas.', 'Gracias por tu ayuda, $n. Deberías hablar con Pao\'ka Monte Presto en Nido Alto, al suroeste.$b$bA lo mejor él conoce el paradero de la bruja anciana.', '', '', '', '', '', '', 0);
