INSERT INTO `quest_template_locale` VALUES (28085, 'frFR', 'Des problèmes à Haut-Perchoir', 'Parlez à Pa\'oka Montagne-agile à Haut-Perchoir, dans les Mille pointes.', 'Merci de votre aide, $n. Vous devriez parler à Pao\'ka Montagne-agile à Haut-Perchoir, au sud-ouest.$b$bIl saura peut-être où est la Vieille mégère ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'itIT', 'Grosso guaio a Covoalto', 'Parla con Pao\'ka Monte Selvaggio a Covoalto nella regione di Millepicchi.', 'Grazie dell\'aiuto, $n. Dovresti parlare con Pao\'ka Monte Selvaggio a Covoalto, a sud-ovest.$B$BForse lui sa dove si trova la Saggia Anziana?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'koKR', '마루둥지의 문제', '버섯구름 봉우리의 마루둥지에 있는 파오카 스위프트마운틴과 대화해야 합니다.', '도와줘서 감사합니다, $n. 남서쪽의 마루둥지에 있는 파오카 스위프트마운틴과 대화해 보십시오.$B$B대모가 어디 있는지 알지도 모르니까요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'ptBR', 'Problemas em Alcândora', 'Fale com Pae\'ka Monte Veloz em Alcândora, que fica em Mil Agulhas.', 'Obrigada pela ajuda, $n. Converse com Pae\'ka Monte Veloz em Alcândora, a sudoeste daqui.$B$BTalvez ele saiba o paradeiro da Bruxa Anciã.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'ruRU', 'Беда в Скальном гнездовье', 'Поговорите с Паокой Ходячей Горой в Скальном гнездовье в Тысяче Игл.', 'Благодарю тебя за помощь, $n. Поговори с Паокой Ходячей Горой в Скальном гнездовье к юго-западу отсюда.$B$BБыть может, он знает, где скрывается мудрая старица.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'zhCN', '风巢的麻烦', '跟千针石林风巢的帕奥卡·迅山谈一谈。', '谢谢你的帮忙，$n。你应该跟西南方风巢的帕奥卡·迅山谈谈。$B$B也许他知道恐怖图腾巫婆长老的所在？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28085, 'zhTW', '風巢之災', '到千針石林的風巢找波卡‧捷山說話。', '謝謝你的幫忙，$n。你應該到西南方的風巢向波卡‧捷山說話。$B$B也許他知道年邁的長者去了哪裡？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'deDE', 'Befreit die Wyverlinge!', 'Befreit 12 Steilhangwyverlinge.', 'Die Fallensteller arbeiten daran, alle jungen Windreiter zusammenzutrommeln. $n, könnt Ihr Euch der Sache annehmen und die Wyverlinge befreien?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'esES', 'Libera a los lechigados', 'Libera 12 lechigados de Nido Alto.', 'Los tramperos están apresando a los jinetes del viento jóvenes. $n, ¿irías hasta allí para liberar a los lechigados?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'esMX', 'Libera a los lechigados', 'Libera 12 lechigados de Nido Alto.', 'Los tramperos están apresando a los jinetes del viento jóvenes. $n, ¿irías hasta allí para liberar a los lechigados?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'frFR', 'Libérer les wyverneaux', 'Libérez 12 Wyverneaux de Haut-Perchoir.', 'Les trappeurs capturent tous les jeunes coursiers du vent. $n, je vous demande d’aller là-bas et de libérer les wyverneaux !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'itIT', 'Liberare i cuccioli di viverna', 'Libera 12 Cuccioli di Viverna di Covoalto.', 'I Mastri Bracconieri stanno radunando tutte le giovani Viverne dei Venti. $n, vuoi andare a liberare i cuccioli di viverna?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'koKR', '새끼 와이번 구하기', '새끼 마루둥지 와이번 12마리를 풀어줘야 합니다.', '덫사냥꾼들이 어린 와이번을 몰아 잡고 있어요. $n 님, 당신이 그쪽으로 가서 새끼 와이번을 풀어주시겠어요?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'ptBR', 'Liberte os filhotes de mantícora', 'Liberte 12 Filhotes de Mantícora de Alcândora.', 'Coureadores estão aprisionando os filhotes de mantícora, $n. Por favor, ajude-me a libertá-los!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'ruRU', 'Свободу гордокрылам!', 'Освободите 12 детенышей гордокрыла Скального гнездовья.', 'Ловчие загоняют всех молодых ветрокрылов. Будь $gдобр:добра;, $n, освободи детенышей гордокрылов.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'zhCN', '解救傲爪幼崽', '解救12条风巢傲爪幼崽。', '捕猎者正在围捕所有年幼的双足飞龙。$n，你是否愿意去把那些傲爪幼崽救出来？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28086, 'zhTW', '解救小傢伙', '解放12隻風巢的幼小雙足飛龍。', '那些陷捕者正在捕捉所有的幼小蠍尾獅，$n，你可以去解救那些幼龍嗎?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'deDE', 'Tod allen Fallenstellern!', 'Sammelt 8 Netze der Fallensteller.', 'Für die dreckigen Fallensteller der Schattenhämmer haben wir nur eines übrig: den Tod!$B$BBringt mir ihre Netze, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'esES', '¡Muerte a todos los tramperos!', 'Reúne 8 redes de trampero.', 'Solo hay una respuesta para esos despreciables tramperos del Martillo Crepuscular: ¡la muerte!$b$bTráeme sus redes, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'esMX', '¡Muerte a todos los tramperos!', 'Reúne 8 redes de trampero.', 'Solo hay una respuesta para esos despreciables tramperos del Martillo Crepuscular: ¡la muerte!$b$bTráeme sus redes, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'frFR', 'Mort aux trappeurs !', 'Récupérez 8 Filets de trappeurs.', 'Il n’y a qu’une réponse possible aux actes de ces sales trappeurs du Marteau du crépuscule ! La mort !$b$bRamenez-moi leurs filets, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'itIT', 'Morte ai Mastri Bracconieri!', 'Recupera 8 Reti del Mastro Bracconiere.', 'Quei maledetti Mastri Bracconieri del Martello del Crepuscolo meritano una cosa sola: la morte!$B$BPortami le loro reti, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'koKR', '모든 덫사냥꾼에게 죽음을!', '덫사냥꾼 그물을 8개 모아야 합니다.', '저 고약한 황혼의 망치단 덫사냥꾼을 가르칠 방법은 단 한 가지! 바로 죽음뿐입니다!$B$B놈들의 그물을 가져오십시오, $n 님.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'ptBR', 'Morte aos coureadores!', 'Consiga 8 Redes de Coureadores.', 'Só existe uma solução para aqueles malditos coureadores do Martelo do Crepúsculo: a morte!$B$BE traga-me as redes deles, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'ruRU', 'Смерть ловчим!', 'Раздобудьте 8 ловчих сетей.', 'Только одного достойны эти мерзкие ловчие из клана Сумеречного Молота! Смерти!$B$BПринеси мне их сети, $n.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'zhCN', '诱捕者都去死！', '收集8张诱捕者之网。', '这些肮脏的暮光诱捕者只有一个结局！死！$B$B把他们的网带回来给我，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28087, 'zhTW', '所有陷捕者的死期!', '收集8張陷捕者網子。', '對付那些卑劣的暮光之錘陷捕者只有一個辦法!那就是死!$B$B把他們的網子帶來給我，$n。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'deDE', 'Die Freilassung von Klingenherz', 'Befreit Klingenherz von allen Ketten, die ihn festhalten.', 'Der Schattenhammer hält das Alphamännchen von Steilhang fest. Ihr müsst Euch nach Osten begeben und Klingenherz befreien, bevor das Rudel seinen Anführer verliert.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'esES', 'Libera a Cuorevaja', 'Libera a Cuorevaja de todas sus ataduras.', 'El Martillo Crepuscular ha encadenado al líder de la manada de Nido Alto. ¡Debes subir por la rampa que hay al este y liberar a Cuorevaja antes de que la manada pierda a su patriarca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'esMX', 'Libera a Cuorevaja', 'Libera a Cuorevaja de todas sus ataduras.', 'El Martillo Crepuscular ha encadenado al líder de la manada de Nido Alto. ¡Debes subir por la rampa que hay al este y liberar a Cuorevaja antes de que la manada pierda a su patriarca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'frFR', 'Libérer Tranchecœur', 'Libérez Tranchecœur de tous les liens qui le retiennent.', 'Le Marteau du crépuscule détient le patriarche de Haut-Perchoir. Vous devez aller vers l’est et monter la rampe pour libérer Tranchecœur avant que la meute ne perde son mâle dominant !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'itIT', 'Libera Cuoraguzzo', 'Libera Cuoraguzzo da tutti gli impedimenti che lo bloccano.', 'Il Martello del Crepuscolo ha catturato il maschio alfa di Covoalto. Devi percorrere la salita a est e liberare Cuoraguzzo prima che il branco perda il suo patriarca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'koKR', '칼날심장 풀어주기', '칼날심장을 묶어두고 있는 족쇄를 모두 풀어야 합니다.', '황혼의 망치단이 마루둥지의 우두머리를 붙잡아두고 있습니다. 이들 무리가 자신들의 우두머리를 잃어버리기 전에, 동쪽의 길을 따라가서 칼날심장을 풀어주십시오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'ptBR', 'Liberte Lamicárdio', 'Liberte Lamicárdio das cordas que o prendem.', 'O Martelo do Crepúsculo aprisionou o macho alfa de Alcândora. Suba a rampa a leste e liberte-o antes que as mantícoras percam o patriarca!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'ruRU', 'Освободите Сердцереза', 'Освободите Сердцереза от всех сдерживающих его пут.', 'Культ Сумеречного Молота захватил главного самца Скального гнездовья. Иди на восток вверх по горе и освободи Сердцереза, пока прайд не потерял своего вожака!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'zhCN', '释放锐爪飞心', '帮助锐爪飞心摆脱束缚。', '暮光之锤囚禁了风巢飞龙的族长。你必须赶往东边的斜坡搭救锐爪飞心，不然整个族群就会陷入群龙无首的混乱！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28088, 'zhTW', '釋放撕心者', '將撕心者從束縛中釋放出來。', '暮光之錘捉住了風巢的首領。你必須趕緊爬上東邊的斜坡釋放撕心者，不要讓雙足翼龍失去了一族之長。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'deDE', 'Auf Befehl des Kriegshäuptlings: Das Vorgebirge des Hügellands!', 'Meldet Euch bei Hochexekutorin Darthalia am Südwachttor im Vorgebirge des Hügellands.', 'Allen tauglichen Mitgliedern der Horde wird hiermit befohlen, sich umgehend bei Hochexekutorin Darthalia am Südwachttor im Vorgebirge des Hügellands zu melden. Die Kriegsmaschinerie der Verlassenen bewegt sich immer weiter vorwärts und sichert Lordaeron für die Horde!$B$BUm das Südwachttor zu erreichen, müsst Ihr der Straße in Richtung Süden durch den Silberwald folgen und die östliche Abzweigung nehmen, die ins Vorgebirge des Hügellands führt.$B$BVorwärts, $g Bruder:Schwester;! Das Schicksal ruft!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'esES', '¡La orden del Jefe de Guerra: Laderas de Trabalomas!', 'Preséntate ante la suma ejecutora Darthalia en la Puerta Austral, en las Laderas de Trabalomas.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante la suma ejecutora Darthalia en la Puerta Austral, en las Laderas de Trabalomas. ¡La maquinaria de guerra renegada marcha hacia el frente, para conquistar Lordaeron en nombre de la Horda!$b$bPara llegar a la Puerta Austral debes seguir el camino hacia el sur, a través del Bosque de Argénteos, y girar al este para entrar en las Laderas de Trabalomas.$b$b¡Adelante, $ghermano:hermana;! ¡El destino te aguarda!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'esMX', '¡La orden del Jefe de Guerra: Laderas de Trabalomas!', 'Preséntate ante la suma ejecutora Darthalia en la Puerta Austral, en las Laderas de Trabalomas.', 'Por la presente se ordena a todos los miembros aptos de la Horda que se presenten ante la suma ejecutora Darthalia en la Puerta Austral, en las Laderas de Trabalomas. ¡La maquinaria de guerra Renegada marcha hacia el frente, para conquistar Lordaeron en nombre de la Horda!$b$bPara llegar a la Puerta Austral debes seguir el camino hacia el sur, a través del Bosque de Argénteos, y girar al este para entrar en las Laderas de Trabalomas.$b$b¡Adelante, $ghermano:hermana;! ¡El destino te aguarda!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'frFR', 'Ordre du chef de guerre : les contreforts de Hautebrande !', 'Présentez-vous à la grande exécutrice Darthalia à la Porte de la Pointe du Midi, dans les Contreforts de Hautebrande.', 'Tous les membres de la Horde en âge de porter les armes sont sommés de se présenter à la grande exécutrice Darthalia à la porte de la Pointe du Midi, dans les contreforts de Hautebrande. Dans sa marche inexorable, la machine de guerre des Réprouvés a conquis Lordaeron pour la gloire de la Horde !$b$bPour vous rendre à la porte de la Pointe du Midi, empruntez la route qui traverse la forêt des Pins-Argentés du nord au sud, et à l’embranchement, bifurquez vers l’est pour entrer dans les contreforts de Hautebrande.$b$bEn avant, $gmon frère:ma sœur; ! Le destin vous appelle !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'itIT', 'Ordine del Capoguerra: Alture di Colletorto!', 'Parla con la Gran Esecutrice Darthalia alla Porta del Sud nelle Alture di Colletorto.', 'Tutti i membri dell\'Orda idonei hanno l\'ordine di fare rapporto alla Gran Esecutrice Darthalia alla Porta del Sud nelle Alture di Colletorto. La macchina da guerra dei Reietti continua ad avanzare, liberando Lordaeron per l\'Orda!$B$BPer raggiungere la Porta del Sud, seguite la strada a sud che attraversa Selva Pinargento. All\'incrocio, dirigetevi a est verso le Alture di Colletorto.$B$BCoraggio, fratelli e sorelle! Il destino chiama!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'koKR', '대족장의 명령: 언덕마루 구릉지!', '언덕마루 구릉지의 남부경비 관문에 있는 고위집행관 다살리아에게 보고해야 합니다.', '신체 건강한 호드 병사는 즉시 언덕마루 구릉지의 남부경비 관문에 있는 고위집행관 다살리아에게 보고하시오. 포세이큰의 전쟁 기계는 계속 전진하여, 호드를 위해 로데론을 지킬 것이오!$B$B남부경비 관문으로 가려면 남쪽의 길을 따라 은빛소나무 숲을 지나 언덕마루 구릉지로 나 있는 동쪽 갈림길로 가면 되오.$B$B행동하시오, $g형제:자매;여! 운명이 그대를 부르고 있소!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'ptBR', 'Ordens do Chefe Guerreiro: Contraforte de Eira dos Montes!', 'Fale com a Alta Executora Darthalia no Portão do Sul do Contraforte de Eira dos Montes.', 'Todos os membros da Horda em bom estado físico estão, por meio deste, convocados a se apresentarem à Alta Executora Darthalia no Portão do Sul do Contraforte de Eira dos Montes. A máquina de guerra renegada avança, assegurando Lordaeron para a Horda!$B$BPara chegar ao Portão Sul, siga a estrada para o sul através da Floresta de Pinhaprata e depois ao leste na bifurcação para Contraforte de Eira dos Montes.$B$BAvante, irmãos e irmãs! O destino chama!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'ruRU', 'Приказ вождя: предгорья Хилсбрада!', 'Поговорите с верховным палачом Дарталией у Южных врат в предгорьях Хилсбрада.', 'Всем годным к военной службе подданным Орды надлежит явиться к верховному палачу Дарталии в предгорья Хилсбрада к Южным вратам. Боевая машина Отрекшихся движется вперед и вперед, защищая Лордерон!$b$bЧтобы добраться до Южных врат, нужно идти по дороге на юг через Серебряный бор, а на развилке повернуть на восток, на дорогу, ведущую к предгорьям Хилсбрада.$b$bВперед, $gбрат:сестра;! Судьба зовет!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'zhCN', '大酋长的命令：希尔斯布莱德丘陵！', '去找希尔斯布莱德丘陵南点大门的高级执行官达萨利亚。', '所有身强力壮的部落成员，从此刻起都要向希尔斯布莱德丘陵南点大门的高级执行官达萨利亚报到。被遗忘者的战争机器正在全力开动，为部落夺取洛丹伦！$B$B要前往南点大门，就沿着南边的路穿越银松森林，然后走东边的岔路进入希尔斯布莱德丘陵。$B$B前进吧，$g兄弟:姐妹;！命运在召唤！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28089, 'zhTW', '大酋長的命令：希爾斯布萊德丘陵！', '到希爾斯布萊德丘陵的南點大門向高階執行官達薩利亞報到。', '所有身強體壯的部落成員必須聽命前往希爾斯布萊德丘陵的南點大門，向高階執行官達薩利亞報到。被遺忘者的部隊正不斷推進，為部落拿下整個羅德隆地區！$B$B要前往南點大門，順著向南的道路穿過銀松森林，再走向東延伸至希爾斯布萊德丘陵的岔路。$B$B兄弟姊妹，前進吧！命運正在召喚著你！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'deDE', 'Wertvolle Waren', 'Bergt einen Torstein der Zwielichtkarawane. Die Speerwächter sollten Schlüssel bei sich tragen, die Euch Zugang zu den Truhen mit den Torsteinen verschaffen.', 'Die Tore weiter unten im Canyon sind geschlossen. Wir haben ausgekundschaftet, dass es eine Art Stein gibt, den sie in Kisten in ihren Karawanen mitführen, und zum Öffnen der Tore benutzen.$B$BGeht dort hinunter und durchsucht die Leichen der Wachen nach einem Truhenschlüssel. Wenn Ihr ihn findet, benutzt ihn bei einer der großen Truhen auf den Karawanenwagen und bringt den Torstein zu mir zurück.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'esES', 'Bienes preciosos', 'Recupera la piedra de puerta de la caravana Crepuscular. Los guardalanzas deberían tener las llaves que abren los cofres que contienen la piedra de puerta.', 'Las puertas que hay cañón abajo están cerradas. Nuestros exploradores han descubierto que sus caravanas llevan, metida en cofres, una especie de piedra que usan para abrir la puerta.$B$BVe allí y busca la llave del cofre en los cuerpos de los guardias. Cuando la encuentres, úsala en los cofres que hay en los vagones de las caravanas y tráeme la piedra de puerta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'esMX', 'Bienes preciosos', 'Recupera la piedra de puerta de la caravana Crepuscular. Los guardalanzas deberían tener las llaves que abren los cofres que contienen la piedra de puerta.', 'Las puertas que hay cañón abajo están cerradas. Nuestros exploradores han descubierto que sus caravanas llevan, metida en cofres, una especie de piedra que usan para abrir la puerta.$B$BVe allí y busca la llave del cofre en los cuerpos de los guardias. Cuando la encuentres, úsala en los cofres que hay en los vagones de las caravanas y tráeme la piedra de puerta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'frFR', 'Une cargaison de valeur', 'Récupérez une Pierre-portail de la caravane du Crépuscule. Les Porte-lances détiennent les clés qui permettent d\'ouvrir les coffres renfermant les pierres-portails.', 'Les portes situées en bas du canyon sont fermées. D\'après nos éclaireurs, pour les ouvrir, les soldats du Crépuscule utilisent une sorte de pierre qu\'ils transportent avec eux dans leurs caravanes.$B$BDescendez donc voir si vous ne trouvez pas une clé en fouillant les corps des gardes... Quand vous l\'aurez trouvée, utilisez-la sur l\'un des coffres des chariots de la caravane, et rapportez-moi la pierre-portail.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'itIT', 'Merce preziosa', 'Recupera una Pietra Portale del Martello del Crepuscolo. I Custodi delle Lance del Crepuscolo dovrebbero avere le chiavi per aprire i bauli della Pietra Portale.', 'Le porte in fondo al canyon sono chiuse. Abbiamo visto che le loro carovane trasportano una specie di pietra che usano per aprirle. Essa è riposta in un baule.$B$BVai laggiù e cerca una chiave del baule sul corpo delle guardie. Una volta raccolta, usala su uno di questi bauli che si trovano sui vagoni della carovana e portami la Pietra Portale.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'koKR', '귀중한 물품', '황혼의 망치단 관문돌을 입수해야 합니다. 관문돌 궤짝을 여는 열쇠는 창투사들이 가지고 있을 것입니다.', '협곡 더 안쪽에 있는 관문은 닫혀 있습니다. 우리 정찰병들이 알아낸 바로는, 짐수레에 실린 궤짝 안에 관문을 여는 데 쓰이는 돌이 있다더군요.$B$B저기 내려가서 놈들의 시체에서 궤짝 열쇠를 찾아보세요. 열쇠를 찾거든, 짐수레에 실린 궤짝에 사용해서 관문돌을 찾아 제게 가져오시기 바랍니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'ptBR', 'Bens preciosos', 'Recupere uma Pedra-portal da Caravana do Crepúsculo. As chaves dos baús com a pedra-portal devem estar com os Lanceadores.', 'Os portões abaixo do cânion estão fechados. Nossas patrulhas descobriram um tipo de pedra, que eles carregam em baús nas caravanas, usada para abrir os portões.$B$BVá lá e procure uma chave de baú junto aos corpos dos guardas. Quando achar ela, use num dos baús nas carroças da caravana e traga a pedra-portal pra mim.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'ruRU', 'Ценный груз', 'Найдите камень-ключ Сумеречного каравана. Ключи от сундуков с камнями-ключами должны быть у сумеречных копьеносцев.', 'Врата, открывающие проход дальше, заперты. Разведчики сообщают, что караванщики открывают их с помощью особых камней, которые возят в сундуках.$B$BСпустись в ущелье и постарайся раздобыть ключ от такого сундука у одного из убитых охранников. Потом найди фургон с сундуком, открой сундук и принеси мне камень-ключ.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'zhCN', '珍贵的货物', '缴获一颗暮光之锤门石。存放门石的箱子钥匙应该在持矛守卫身上。', '峡谷深处的大门被关闭了。我们的侦察表明，他们的车队里载着一些箱子，里面的某种石头可以打开大门。$B$B去守卫的尸体上找一把箱子钥匙来。找到以后，就用它打开车队里的一个箱子，将门石给我带来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28090, 'zhTW', '珍貴的貨物', '尋找一塊暮光之錘門石。矛衛兵身上應該有能讓你打開箱子的鑰匙。', '在峽谷更深處的門都緊閉著。我們偵察後，發現他們放在車隊上的箱子裡的某種門石可以用來打開這些門。$B$B過去找找看守衛的屍體上有沒有箱子的鑰匙。找到後，打開車上的箱子並將門石帶回來給我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'deDE', 'Leichte Aufgabe', 'Tötet 8 Zwielichtspeerwärter.', 'Der Hinterhalt ging problemlos vonstatten. Die meisten Wachen sind tot, aber einige sitzen immer noch bei den Wagen fest.$B$BIhr könnt sie genau im Westen in dem Canyon unter uns sehen. Geht dorthin und erledigt sie, damit wir diesen Angriff ins Rollen bringen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'esES', 'Blancos fáciles', 'Mata a 8 depositarios de lanzas Crepusculares.', 'La emboscada ha ido bien. La mayoría de los guardias están muertos, pero aún quedan algunos junto a los vagones.$B$BPuedes verlos al oeste de nuestra posición, abajo, en el cañón. Ve ahí y acaba con ellos para que podamos seguir con el ataque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'esMX', 'Blancos fáciles', 'Mata a 8 depositarios de lanzas Crepusculares.', 'La emboscada ha ido bien. La mayoría de los guardias están muertos, pero aún quedan algunos junto a los vagones.$B$BPuedes verlos al oeste de nuestra posición, abajo, en el cañón. Ve ahí y acaba con ellos para que podamos seguir con el ataque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'frFR', 'Des cibles faciles', 'Tuez 8 Porte-lances du Crépuscule.', 'L\'embuscade s\'est déroulée comme prévu. La plupart des gardes sont morts, mais quelques-uns d\'entre eux sont encore immobilisés avec les chariots.$B$BIls se trouvent à l\'ouest, en bas du canyon. Entrez dans le canyon et éliminez-les afin que nous puissions mener l\'attaque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'itIT', 'Un gioco da ragazzi', 'Uccidi 8 Custodi delle Lance del Crepuscolo.', 'L\'imboscata ha avuto successo. La maggior parte delle guardie è morta, ma alcune sono ancora bloccate nei vagoni.$B$BSono a ovest di qui, nel canyon sottostante. Vai laggiù ed eliminale, così potremo proseguire l\'attacco.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'koKR', '쉬운 상대', '황혼의 창투사 8명을 처치해야 합니다.', '공격은 성공적이었습니다. 경비병 대부분이 이미 죽었지요. 하지만 아직 일부가 짐수레 주위에서 꼼짝도 안 하고 있습니다.$B$B아래에 있는 협곡에서 여기 바로 서쪽으로 놈들이 보일 겁니다. 저기 가서 놈들을 처리해 주세요. 그래야 공격의 다음 단계를 실행에 옮기지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'ptBR', 'Presas fáceis', 'Mate 8 Lanceadores do Crepúsculo.', 'A emboscada aconteceu sem grandes dificuldades. A maioria dos guardas está morta, mas alguns ainda estão pelas carroças.$B$BVocê pode ver eles a oeste daqui, cânion abaixo. Vá até lá e mande eles dessa pra melhor pra gente poder continuar o ataque.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'ruRU', 'Легкая добыча', 'Убейте 8 сумеречных копьеносцев.', 'Нападение из засады прошло, как и было задумано. Мы перебили почти всех охранников, но несколько осталось за фургонами.$B$BОни находятся в ущелье, к западу отсюда. Уничтожь их, чтобы мы могли двинуться дальше.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'zhCN', '轻松杀敌', '杀死8名暮光持矛守卫。', '伏击很顺利。大多数卫兵都死了，少数还被压制在马车那里。$B$B就在峡谷下面，你可以在西边看到他们。过去把他们都干掉，我们好发动进攻。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28091, 'zhTW', '不是對手', '殺死8個暮光矛衛兵。', '突襲進行的很順利。大部分守衛已死了，但有些還被貨運馬車壓住。$B$B你可以在我們西邊下面的峽谷裡看到他們。過去將他們都處理掉好讓我們的攻擊能繼續向前推進。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'deDE', 'Wenn der Schlüssel passt', 'Führt die Goblinschwadron zu dem geschlossenen Tor des Drachenmals.$B$BWenn Ihr die Goblinschwadron verliert, kehrt zu Kriegsfürst Krogg zurück, damit Ihr neue Truppen zugeteilt bekommt.', 'Die Wyrms haben den Kampf bereits in den Himmel über Grim Batol getragen. Jetzt ist es an uns, unsere Leute auf dem Boden dorthin zu bewegen und die Tore zu sichern.$B$BIhr sollt wissen, was auf dem Spiel steht, $C. Grim Batol ist die Quelle der Verstärkung für den Schattenhammer im ganzen Hochland. Wenn wir das hier nicht unterbinden, werden wir die Kämpfe, die vor uns liegen, nicht gewinnen können.$B$BIch schicke die Goblinschwadron mit Euch. Erreicht das Tor im Südosten und öffnet es. Flick sollte weitere Anweisungen für Euch haben, wie es ab da weiter geht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'esES', 'Si la llave encaja', 'Lleva al escuadrón goblin a la Puerta Faucedraco cerrada.$B$BSi pierdes el escuadrón goblin, vuelve con el señor de la guerra Krogg para que te asigne uno nuevo.', 'Los dragones ya han llevado la batalla a los cielos sobre Grim Batol. Ahora depende de nosotros llevar a nuestros hombres por tierra y asegurar las puertas.$B$BQuiero que sepas lo que está en juego aquí, $c. De Grim Batol salen los refuerzos del Martillo Crepuscular hacia todas las Tierras Altas. Si no les cerramos el paso aquí, no ganaremos las batallas que nos quedan.$B$BEnviaré al escuadrón goblin contigo. Ve a la puerta del sudeste y ábrela. Parche te dará las instrucciones que necesites a partir de ahora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'esMX', 'Si la llave encaja', 'Lleva al escuadrón goblin a la Puerta Faucedraco cerrada.$B$BSi pierdes el escuadrón goblin, vuelve con el señor de la guerra Krogg para que te asigne uno nuevo.', 'Los dragones ya han llevado la batalla a los cielos sobre Grim Batol. Ahora depende de nosotros llevar a nuestros hombres por tierra y asegurar las puertas.$B$BQuiero que sepas lo que está en juego aquí, $c. De Grim Batol salen los refuerzos del Martillo Crepuscular hacia todas las Tierras Altas. Si no les cerramos el paso aquí, no ganaremos las batallas que nos quedan.$B$BEnviaré al escuadrón goblin contigo. Ve a la puerta del sudeste y ábrela. Parche te dará las instrucciones que necesites a partir de ahora.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'frFR', 'Si la clé entre…', 'Menez la Brigade gob jusqu\'à la Porte des Gueules-de-dragon fermée.$B$BSi vous perdez la Brigade gob, retournez voir le Seigneur de guerre Krogg pour qu\'il vous réattribue la mission.', 'Les wyrms ont déjà commencé l\'attaque aérienne sur Grim Batol. Nous devons à tout prix faire avancer nos troupes au sol et défendre les portes.$B$BJe veux être sûr que vous compreniez bien ce qui est en jeu, $c. Grim Batol est la source des renforts du Marteau du crépuscule pour l\'ensemble des hautes-terres. Si nous n\'arrivons pas à stopper leur progression à cet endroit, il est inutile d\'espérer remporter la suite des combats !$B$BJ\'envoie la Brigade gob vous assister. Allez à la porte au sud-est et trouvez un moyen de l\'ouvrir. Rustine vous communiquera d\'autres instructions lorsque vous serez sur place.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'itIT', 'Se la chiave entra...', 'Conduci la Squadra Gob al Cancello delle Fauci di Drago chiuso.$B$BSe perdi la Squadra Gob, torna dal Signore della Guerra Krogg per una nuova assegnazione.', 'I dragoni hanno già dato battaglia nei cieli sopra Grim Batol. Tocca a noi guidare le truppe di terra e conquistare l\'ingresso.$B$BVoglio che tu sappia cosa c\'è in gioco qui, $C. Da Grim Batol provengono i rinforzi del Martello del Crepuscolo in tutte le Alture. Se non li tagliamo fuori, non potremo vincere le battaglie che ci attendono.$B$BSto inviando con te la Squadra Gob. Raggiungete il cancello a sud-est e apritelo. Asdrubal saprà fornirti le informazioni di cui avrai bisogno.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'koKR', '열쇠만 맞는다면', '고블린 분대를 닫힌 용아귀 관문으로 이끌어야 합니다.$B$B고블린 분대와 떨어지면, 장군 크로그에게 돌아가 다시 분대를 요청하십시오.', '용들은 이미 그림 바톨 위 하늘에서 전투를 시작했소. 지상으로 전진하여 관문을 확보하는 건 우리 몫이지.$B$B이 공격에 무엇이 달렸는지 알려 주겠소, $c. 황혼의 망치단은 그림 바톨에서 고원 전역에 지원군을 보내지. 이 지원군 공급을 차단하지 못하면 우리는 앞으로의 전투에서 이길 수 없소.$B$B당신에게 고블린 분대를 딸려 보내겠소. 남동쪽으로 가서 관문을 여시오. 이후에 뭘 해야 하는지는 패치가 알려 줄 거요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'ptBR', 'Se a chave servir...', 'Lidere o G.O.P.E. até o Portão Presa do Dragão fechado.$B$BCaso se perca do G.O.P.E., fale novamente com o Senhor da Guerra Krogg para recomeçar.', 'As serpes já estão lutando nos céus sobre Grim Batol. O transporte de nossos homens por terra e a proteção dos portões dependem de nós.$B$BQuero que você saiba o que está em jogo aqui, $c. Grim Batol é a fonte dos reforços do Martelo do Crepúsculo no Planalto. Se não expulsarmos eles daqui, não vamos vencer as próximas batalhas.$B$BEstou enviando o G.O.P.E. para ajudar você. Vá até o portão a sudeste daqui e abra-o. Mathiaz dará mais instruções para você lá.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'ruRU', 'Если ключ подходит...', 'Ведите подрывной отряд к закрытым вратам Драконьей Пасти.$B$BЕсли вы потеряете отряд, возвращайтесь к полководцу Кроггу за новым заданием.', 'Драконы уже вступили в бой в небе над Грим Батолом. Пришло время нам пойти вперед и захватить врата.$B$B$C, знай: ставки очень высоки. Из Грим Батола Сумеречный Молот отправляет подкрепления во все уголки Нагорий. Если мы не возьмем город, победы в войне нам не видать.$B$BЯ дам тебе подрывной отряд. Иди на юго-восток, к вратам, и захвати их. За дальнейшими распоряжениями обращайся к Патчу.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'zhCN', '只要钥匙合适', '带领地精小队前去关闭的龙喉大门。$B$B如果你失去了地精小队，就去找督军克罗格。', '巨龙已经在格瑞姆巴托的天空上展开了战斗。我们该把地面上的人派出去占领大门了。$B$B我要你知道，胜负在此一举，$c。暮光之锤在高地的所有增援都仰仗格瑞姆巴托。如果我们不把他们在这里切断，后面的战斗就无法胜利。$B$B我要派地精小队和你一起去。到东南边的大门那里，把它打开。补丁可以给你下达进一步的指示。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28092, 'zhTW', '若鑰匙吻合', '帶領哥布林戰隊到關閉的龍喉大門。$B$B若你與哥布林戰隊走散了，回去與督軍克羅格交談，請他派遣一支新的戰隊給你。', '龍群們已經在格瑞姆巴托的空中開始戰鬥了。剩下就等我們的人進去佔領大門。$B$B我想你該知道在這緊要關頭最重要的是什麼，$c。格瑞姆巴托是暮光之錘給整個高地派遣援軍的源頭。若沒有在這裡將源頭中斷，我們就無法贏得後面的戰爭。$B$B我會遣派哥布林戰隊跟你一起過去。前往東南邊的門並將它打開。派奇會給你更多需要的資訊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'deDE', 'Vordringen', 'Führt die Goblinschwadron am nördlichen Tor des Drachenmals vorbei zum Eingang von Grim Batol.$B$BWenn Ihr die Goblinschwadron verliert, kehrt zu Kriegsfürst Krogg zurück, damit Ihr neue Truppen zugeteilt bekommt.', 'Bis jetzt war das gute Arbeit. Wir sollten den Eingang zu Grim Batol direkt hinter dem nächsten Tor finden.$B$BIch habe Befehle, den Eingang zu sichern und der Verstärkung den Weg zu verstellen, während der Rest unserer Leute durch den Pfad, den wir durch den Canyon freigemacht haben, vorwärts drängt. Ich glaube, wir werden es bald geschafft haben, vielleicht kann ich Ticker dann ein wenig mehr Spaß gönnen.$B$BMachen wir uns auf den Weg hinunter in den Canyon im Südwesten - wir sollten das nächste Tor recht schnell finden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'esES', 'Haciendo presión', 'Lleva al escuadrón goblin más allá de la puerta norte Faucedraco hasta la entrada de Grim Batol.$B$BSi te separas del escuadrón goblin, reúnete con el señor de la guerra Krogg en el Recodo de Vermis para recibir órdenes.', 'Hemos hecho un buen trabajo hasta ahora. Deberíamos encontrar la entrada de Grim Batol justo detrás de la próxima puerta.$B$BMe ordenaron que asegurara la entrada y que les cerrara el paso a los refuerzos mientras el resto de nuestros hombres siguen avanzando por la ruta que hemos despejado en el cañón. Se me ocurre que si llegamos pronto, a lo mejor puedo dejar que Tictac se divierta un poco más.$B$BSigamos avanzando por el cañón hacia el sudoeste, deberíamos encontrar la siguiente puerta bastante rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'esMX', 'Haciendo presión', 'Lleva al escuadrón goblin más allá de la puerta norte Faucedraco hasta la entrada de Grim Batol.$B$BSi te separas del escuadrón goblin, vuelve con el señor de la guerra Krogg, en el Recodo de Vermis, para recibir órdenes.', 'Hemos hecho un buen trabajo hasta ahora. Deberíamos encontrar la entrada de Grim Batol justo detrás de la próxima puerta.$B$BMe ordenaron que asegurara la entrada y que les cerrara el paso a los refuerzos mientras el resto de nuestros hombres siguen avanzando por la ruta que hemos despejado en el cañón. Se me ocurre que si llegamos pronto, a lo mejor puedo dejar que Tictac se divierta un poco más.$B$BSigamos avanzando por el cañón hacia el sudoeste, deberíamos encontrar la siguiente puerta bastante rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'frFR', 'En avant !', 'Faites franchir la Porte des Gueules-de-dragon à la Brigade gob et menez cette dernière à l\'entrée de Grim Batol.$B$BSi vous êtes $gséparé:séparée; de la Brigade gob, retournez voir le Seigneur de guerre Krogg au Tournant du Wyrm pour qu\'il vous réattribue la mission.', 'De l\'excellent travail jusqu\'ici ! L\'entrée de Grim Batol doit se trouver juste après la prochaine porte.$B$BJ\'ai reçu l\'ordre de sécuriser l\'entrée et de bloquer les renforts ennemis pendant que le reste de nos hommes continue à avancer le long du canyon. Si nous arrivons là-bas suffisamment tôt, je pourrai peut-être même laisser Tocant s\'amuser un peu...$B$BEn continuant dans le canyon en direction du sud-ouest, on ne devrait pas tarder à atteindre la prochaine porte.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'itIT', 'Avanzata', 'Oltrepassa il Cancello delle Fauci di Drago con la Squadra Gob fino all\'entrata di Grim Batol.$B$BSe ti separi dalla Squadra Gob, torna dal Signore della Guerra Krogg alla China dei Dragoni per una nuova assegnazione.', 'Finora tutto bene. Dovremmo trovare l\'entrata di Grim Batol subito dopo il prossimo cancello.$B$BHo avuto l\'ordine di conquistare l\'entrata e tagliare fuori i rinforzi, mentre il resto dei nostri uomini avanza lungo il percorso che abbiamo aperto nel canyon. Penso che se arriviamo abbastanza presto, potrò far divertire Schiaffo ancora un po\'.$B$BScendiamo per il canyon a sud-ovest, dovremmo trovare presto il prossimo cancello.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'koKR', '계속해서 전진', '북쪽 용아귀 관문을 지나 그림 바톨 입구로 고블린 분대를 데려가야 합니다.$B$B고블린 편대와 떨어지면, 고룡굽이에 있는 장군 크로그에게 돌아가 다시 분대를 요청하십시오.', '지금까지 참 잘해내셨습니다. 다음 관문만 지나면 바로 그림 바톨 입구입니다.$B$B전 우리가 협곡을 지나며 뚫은 길로 나머지 병사들이 전진하는 동안, 입구를 확보하고 지원군을 차단하라는 명령을 받았지요. 만약 입구에 일찍 도착하면 티커가 좀 더 재미를 보게 놔둘 작정입니다.$B$B남서쪽으로 계속 움직이지요. 곧 다음 관문이 나올 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'ptBR', 'Adiante com a pressão', 'Leve o G.O.P.E. do Portão Presa à entrada de Grim Batol.$B$BCaso se perca do G.O.P.E., volte até o Senhor da Guerra Krogg para reapresentar-se.', 'Até agora, bom trabalho. A entrada de Grim Batol deve ser logo depois do próximo portão.$B$BMinhas ordens são vigiar a entrada e impedir a passagem de reforços enquanto o resto de nossos homens abrem caminho pelo cânion. Acho que, se a gente chegar logo lá, talvez eu deixe Tique se divertir um pouquinho mais.$B$BVamos seguir para sudoeste do cânion, assim vamos achar o próximo portão bem rápido.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'ruRU', 'Наступление', 'Проведите подрывной отряд через северные врата Драконьей Пасти к входу в Грим Батол.$B$BЕсли вы потеряете отряд, возвращайтесь в Излучину Змея к полководцу Кроггу.', 'Пока ты действуешь неплохо. За следующими вратами должен быть вход в Грим Батол. Нам нужно его найти.$B$BМне приказали захватить этот вход и обеспечивать его безопасность, пока остальные наши ребята не пройдут по расчищенному пути. Если мы поторопимся, то Тиккер сможет вдоволь порезвиться.$B$BИдем по ущелью на юго-запад. Врата должны быть где-то недалеко.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'zhCN', '前进', '带领地精小队通过北部龙喉大门，前往格瑞姆巴托入口。$B$B如果你失去了地精小队，就去找巨龙曲径的督军克罗格。', '目前为止一切顺利。过了这扇大门，我们就能找到格瑞姆巴托的入口。$B$B我收到命令，要占领入口，阻止敌人增援，其他人则穿过小道，通过被我们清理的峡谷。我想假如我们动作快的话，还能让钟摆找点乐子。$B$B我们出发前往西南边的峡谷吧——应该很快就能找到下一座大门了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28093, 'zhTW', '往前邁進', '帶領哥布林戰隊經過西南方的龍喉大門到格瑞姆巴托的入口。$B$B若你與哥布林戰隊走散了，回到龍之彎道請督軍克羅格重新指派。', '到目前為止幹得還不錯。通過下一道門，應該就會看見格瑞姆巴托的入口。$B$B我的任務是要在其他兵力沿著我們清空的峽谷進軍時，守住入口並阻擋敵方援軍。我在想如果我們能夠提早到，也許可以讓提克爾先玩樂一下。$B$B到峽谷西南方去吧!我們應該可以很快找到下一道門。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'deDE', 'Den Weg ebnen', 'Tötet 30 Kultisten des Schattenhammers entlang der Wyrmbiege, während Ihr Euch zum Eingang von Grim Batol durchkämpft.', 'Meine Befehle lauten, Euch nach Süden durch die Tore des Drachenmals zum Eingang von Grim Batol zu begleiten. Wir sollen einen Weg freimachen, damit der Rest der Männer uns folgen und den Eingang sichern kann.$B$BÜbersetzung: Tötet alles, das sich bewegt und schämt Euch nicht, wenn Ihr Spaß dabei habt.$B$BDie Jungs werden langsam unruhig. Auf geht\'s.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'esES', 'Preparando el camino', 'Mata a 30 adeptos del Martillo Crepuscular en el Recodo de Vermis mientras te abres camino hacia la entrada de Grim Batol.', 'Mis órdenes son acompañarte al sur, a través de las Puertas Faucedraco, hacia la entrada de Grim Batol. Tenemos que despejar el camino para que el resto de los hombres pueda seguirnos y asegurar la entrada.$B$BTraducción: mata todo lo que se mueva, y que no te dé vergüenza si lo encuentras divertido.$B$BLos chicos se están impacientando. Adelante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'esMX', 'Preparando el camino', 'Mata a 30 adeptos del Martillo Crepuscular en el Recodo de Vermis mientras te abres camino hacia la entrada de Grim Batol.', 'Mis órdenes son acompañarte al sur, a través de las Puertas Faucedraco, hacia la entrada de Grim Batol. Tenemos que despejar el camino para que el resto de los hombres pueda seguirnos y asegurar la entrada.$B$BTraducción: mata todo lo que se mueva, y que no te dé vergüenza si lo encuentras divertido.$B$BLos chicos se están impacientando. Adelante.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'frFR', 'Un défrichage express', 'Tuez 30 Sectateurs du Marteau du crépuscule en avançant dans le Tournant du Wyrm vers l\'entrée de Grim Batol.', 'J\'ai reçu l\'ordre de vous accompagner vers le sud, à l\'entrée de Grim Batol, en passant par les portes des Gueules-de-dragon. Nous devons ouvrir la voie au reste des troupes de manière à leur permettre de nous suivre, et sécuriser l\'entrée.$B$BTraduction : tuez tout ce qui bouge, et n\'ayez pas honte d\'y prendre plaisir !$B$BLes gars commencent à être nerveux. Allons-y !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'itIT', 'Spianare la strada', 'Uccidi 30 Cultisti del Martello del Crepuscolo alla China dei Dragoni mentre combatti per raggiungere l\'entrata di Grim Batol.', 'Ho l\'ordine di accompagnarti a sud, attraverso il Portale delle Fauci di Drago, verso l\'ingresso di Grim Batol. Dobbiamo aprire la strada in modo che il resto degli uomini possa seguirci e conquistare l\'ingresso.$B$BIn poche parole: uccidi tutto quello che si muove e non vergognarti se ti piace farlo.$B$BI ragazzi iniziano a innervosirsi. Andiamo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'koKR', '길 닦기', '고룡굽이를 뚫고 그림 바톨 입구까지 나아가면서 황혼의 망치단 이교도 30명을 처치해야 합니다.', '전 당신과 함께 용아귀 관문을 통과해 남쪽으로 그림 바톨 입구까지 전진하라는 명령을 받았지요. 나머지 군대가 우릴 따라와 입구를 장악할 수 있도록 길을 여는 게 우리 임무입니다.$B$B더 쉬운 말로 해 드릴까요? 움직이는 건 모두 처치하세요. 처치하는 게 즐겁다는 생각이 들어도 양심의 가책을 느낄 건 없습니다.$B$B제 부하들이 안달복달하는군요. 어서 가지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'ptBR', 'Abrindo caminho', 'Mate 30 sectários do Martelo do Crepúsculo ao longo da Curva das Serpes enquanto você se dirige para a entrada de Grim Batol.', 'Tenho ordens para acompanhar você ao norte pelos Portões Presa do Dragão em direção à entrada de Grim Batol. Devemos abrir caminho para que o resto dos homens sigam a gente e protejam a entrada.$B$BTraduzindo: mate tudo que se move e não tenha vergonha se você se divertir fazendo isso.$B$BOs rapazes estão ficando impacientes. Vamos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'ruRU', 'Прокладывая путь', 'Пробиваясь к входу в Грим Батол, убейте 30 служителей культа Сумеречного Молота в Излучине Змея.', 'Мне приказано отправиться вместе с тобой на юг – через врата Драконьей Пасти к входу в Грим Батол. Нам нужно расчистить путь для основных сил, которые и захватят врата.$B$BПеревожу: убивай все, что движется, а если тебе это нравится – тем лучше.$B$BПарни уже рвутся в бой, так что идем.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'zhCN', '铺路', '消灭30名暮光之锤邪教徒，沿着巨龙曲径一路杀向格瑞姆巴托的入口。', '我接受的命令是护送你前往南方，穿过龙喉大门，来到格瑞姆巴托入口处。我们已经清出了一条道路，以便其他人能够跟着我们去占领入口。$B$B我简单翻译一下吧：把会动弹的统统杀掉，要是你觉得还挺过瘾，也没什么不好意思的。$B$B小伙子们都不耐烦了。咱们走吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28094, 'zhTW', '鋪路', '在前往格瑞姆巴托入口的途中，沿著巨龍之彎殺死30個暮光之錘教徒。', '我奉令伴隨你往南，穿過龍喉小徑抵達格瑞姆巴托。我們已清出一條路讓剩下的人可以跟著上去佔領入口。$B$B以上翻譯為:將所有會動的東西殺掉，若你為了可以這樣做感到開心，不必害羞。$B$B我們的人也在蠢蠢欲動了。走吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'deDE', 'Beamtenmikado', 'Gebt 3 Quests aus.', 'Wer sich zuerst bewegt, hat verloren, $gmein Lieber:meine Liebe;. Nur so kann man es als hochrangiger Offizier der Verlassenen zu etwas bringen. Ja, das hier ist in der Tat das gute Leben und Ihr werdet es leben!$B$BIch möchte, dass Ihr diese Befehle nehmt und sie an die kleinen Anfänger ausgebt, die hier vorbeikommen und nach Arbeit suchen. Wem Ihr was gebt, ist Euch überlassen.$B$BWenn Ihr bereit seid, steigt auf mein braves Skelettross auf - damit man Euch nicht übersieht - und fangt an, das Leben zu leben! Was bedeutet, einfach hier herumzustehen und zu warten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'esES', 'Bienvenidos a la máquina', 'Asigna 3 misiones.', 'Acércate, $gamigo:querida;, toma un puro. Vas a llegar muy lejos como oficial de los Renegados. ¡Ya lo creo, esta es la buena vida y tú la vas a vivir!$b$bQuiero que cojas estas órdenes y se las entregues a los simples medianías que vengan a buscar trabajo. A quién le des el qué lo dejo enteramente a tu discreción.$b$bCuando estés $glisto:lista;, monta en mi fiel corcel esquelético (para que te vean bien) ¡y vive la vida! Es decir, quédate $gquieto:quieta; y espera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'esMX', 'Bienvenidos a la máquina', 'Asigna 3 misiones.', 'Acércate, $gamigo:querida;, toma un puro. Vas a llegar muy lejos como oficial de los Renegados. ¡Ya lo creo, esta es la buena vida y tú la vas a vivir!$b$bQuiero que cojas estas órdenes y se las entregues a los simples medianías que vengan a buscar trabajo. A quién le des el qué lo dejo enteramente a tu discreción.$b$bCuando estés $glisto:lista;, monta en mi fiel corcel esquelético (para que te vean bien) ¡y vive la vida! Es decir, quédate $gquieto:quieta; y espera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'frFR', 'Bienvenue dans le système', 'Donnez 3 Quêtes.', 'Approchez, $gmon garçon:ma fille;, prenez un cigare. Vous irez loin, je le sais, en tant qu’officier haut gradé des Réprouvés. Croyez-moi, c’est la belle vie, vous allez adorer !$B$BPrenez ces ordres de mission et distribuez-les aux va-nu-pieds qui viennent ici chercher du travail. Vous avez toute latitude pour distribuer les ordres à qui bon vous semble.$B$BEnfourchez mon fidèle destrier squelettique pour bien vous faire remarquer, et profitez de la vie ! Ce qui consiste en gros à vous poster ici et à attendre.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'itIT', 'Benvenuti nell\'ingranaggio', 'Distribuisci 3 Missioni.', 'Entra pure, $gragazzo:ragazza;, e prendi un sigaro. So già che farai strada e volerai in alto come ufficiale graduato dei Reietti. Esatto: qui si fa la bella vita... e aspetta solo te!$B$BPrendi questi ordini e distribuiscili ai miserabili che vengono qui in cerca di lavoro. Decidi tu a chi affidare ogni compito.$B$BQuando vuoi, monta in sella al mio fidato Destriero Scheletrico, così ti vedranno di sicuro, e inizia a goderti la vita! Ricapitolando: resta lì e aspetta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'koKR', '체험, 고위 장교의 삶!', '임무 3개를 나눠줘야 합니다.', '어서 오게, 친구여. 좀 쉬다 가게. 자네는 진급을 거듭하다 언젠가 포세이큰 고위 장교처럼 높은 자리에 오를 거야. 그럼 그럼. 고위 장교의 삶이야말로 좋은 삶이지. 자네에게 그 삶을 누릴 기회를 주겠네!$B$B일을 구하러 오는 하찮은 자들에게 이 임무를 나눠주게. 누구에게 어떤 일을 주는지는 전적으로 자네 재량에 맡기지.$B$B준비가 되면 내 충직한 해골 전투마에 오르게. 일을 구하러 오는 자들이 자네를 놓치지 않게 말이야. 고위 장교의 삶을 체험하게! 그냥 말 위에 앉아 기다리기만 하면 돼.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'ptBR', 'Ouro de tolo', 'Distribua 3 Missões.', 'Eu devia estar contente porque tenho um emprego, sou a dita morta-viva respeitável e ganho quatro mil ouros semana não, semana sim. Eu devia agradecer à Dama Sylvana por ter este cargo elevado, deveria estar feliz por ter comprado um corcel tão morto assim. Mas confesso, estupefata, que estou decepcionada com esses malucos que aparecem para me pedir missões. Agora eu me pergunto, e daí? E portanto você vai entregar estas ordens aos integrantes da hora, grandessíssimos idiotas, ridículos, limitados, que só usam dez por cento das cabeças animais. Eu é que não me sento no alto do corcel descarnado - vai ser você, quando estiver pronto, com a boca escancarada, cheia de dentes, esperando a morte passar. E olha, você vai esperar um tempão...', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'ruRU', 'Добро пожаловать в систему', 'Вручите 3 задания.', 'Подойди сюда, $gмилый юноша:милая девушка;. Сигару? Тебе, как высокоранговому офицеру Отрекшихся, предстоит стать птицей высокого полета. Нет, в самом деле, тебя ждет прекрасная жизнь!$b$bЯ хочу, чтобы ты $gвзял:взяла; эти задания и $gраздал:раздала; их тем слабакам, которые интересуются работой. Кого из них выбрать – решай $gсам:сама;.$b$bКогда будешь $gготов:готова;, вскакивай на мою верную костлявую конягу и вперед к новой жизни! Ну то есть встань тут и жди.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'zhCN', '任务给予者的体验', '指派3项任务。', '$g伙计:姑娘;，来根雪茄吧。你很快就会扬名立万，成为被遗忘者的高阶官员了。没错，好日子就要开始啦！$B$B我要你将这些指令分发给前来找活儿干的底层人员。把哪项任务分给谁，全凭你的决定。$B$B如果你准备好了，就骑上我忠实的骸骨战马吧——他们肯定会看到你的，然后开始体验全新的生活吧！呃，你站那里等着就成。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28096, 'zhTW', '核心機構歡迎你', '分配3項任務。', '來這裡，親愛的$g小男孩:小女孩;，抽個雪茄。作為被遺忘者的高級軍官，你將會平步青雲的。沒錯，的確，這就是你將要過上的美好生活!$B$B我要你接下這些命令，把它們丟給來找工作的那群下等人去做。你高興要誰幹嘛就幹嘛。$B$B當你準備好，騎上我可靠的骷髏戰馬，這樣他們才不會輕視你，然後開始享受人生!所謂享受人生就是待在那裡等就好。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'deDE', 'Die Tore von Grim Batol', 'Sprecht mit Kriegsfürst Krogg in der Wyrmbiege.', 'Krogg hat den Angriff begonnen, um durch den Canyon zu drängen. Er ist kurz hinter dem Tor im Südosten.$B$BIch werde die Schwadron zusammenrufen, damit wir uns dem Angriff anschließen. Wir treffen Euch dort unten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'esES', 'Las puertas de Grim Batol', 'Habla con el señor de la guerra Krogg en el Recodo de Vermis.', 'Krogg ha iniciado el ataque para avanzar por el cañón. Está justo tras la puerta que tenemos al sudeste.$B$BVoy a reunir al pelotón para que se una al ataque. Nos reuniremos contigo ahí abajo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'esMX', 'Las puertas de Grim Batol', 'Habla con el señor de la guerra Krogg en el Recodo de Vermis.', 'Krogg ha iniciado el ataque para avanzar por el cañón. Está justo tras la puerta que tenemos al sudeste.$B$BVoy a reunir al pelotón para que se una al ataque. Nos reuniremos contigo ahí abajo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'frFR', 'Les portes de Grim Batol', 'Allez voir le Seigneur de guerre Krogg au Tournant du Wyrm.', 'Krogg a lancé ses troupes à l\'assaut des ennemis présents dans le canyon. Lui et ses forces se trouvent actuellement de l\'autre côté de la porte située au sud-est d\'ici.$B$BJe vais réunir l\'escouade afin que nous puissions nous joindre à l\'attaque. Nous vous retrouverons là-bas.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'itIT', 'Le porte di Grim Batol', 'Parla con il Signore della Guerra Krogg alla China dei Dragoni.', 'Krogg ha dato il via all\'attacco nel canyon. Si trova subito dopo l\'ingresso a sud-est dalla nostra posizione.$B$BOrganizzerò la squadra per unirci all\'assalto. Ci vediamo laggiù.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'koKR', '그림 바톨 관문', '고룡굽이에 있는 장군 크로그와 대화해야 합니다.', '크로그 장군이 협곡 안쪽으로 진군을 시작했습니다. 우리 남동쪽에 있는 관문을 막 지났지요.$B$B전 고블린 분대를 이끌고 공격에 합류할 겁니다. 저 아래에서 만나지요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'ptBR', 'Os portões de Grim Batol', 'Fale com o Senhor da Guerra Krogg no Desfiladeiro da Presa do Dragão.', 'O Krogg deu início ao ataque que vai varrer o cânion. Ele acabou de passar pelo portão a sudeste daqui.$B$BVou reunir o esquadrão para a gente se juntar ao ataque. Te encontro lá embaixo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'ruRU', 'Врата Грим Батола', 'Отправляйтесь в Излучину Змея и поговорите с полководцем Кроггом.', 'Крогг уже пошел в атаку. Он к юго-востоку отсюда, сразу за вратами.$B$BЯ соберу отряд и поведу его в наступление. Увидимся там.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'zhCN', '格瑞姆巴托的大门', '跟巨龙曲径的督军克罗格谈一谈。', '克罗格对峡谷发动了进攻。他刚刚穿过东南边的大门。$B$B我要把小队集中起来，加入战斗。咱们下面见。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28097, 'zhTW', '格瑞姆巴托之門', '到巨龍之彎與督軍克羅格說話。', '克羅格開始往峽谷逼近進攻。他剛經過我們東南方的閘門。$B$B我先召集小隊準備開始攻擊。我們等等在下面會合。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'deDE', 'Der Himmelsmeister der Schatten', 'Fliegt mit Klingenherz nach Zwielichthorst und bekämpft Schattenhimmelsmeister Richtofen.', 'Richtofen der Schattenhimmelsmeister befindet sich oben und über Zwielichthorst südöstlich von hier. Es ist egal, was wir hier unten tun - wenn wir ihn nicht ausschalten, wird alle Hoffnung für Steilhang verloren sein.$B$BKlingenherz ist zwar zu schwer verletzt, um Euch im Kampf zu helfen, aber er kann Euch zum Horst fliegen. Der Rest liegt dann bei Euch, $n.$B$BIch wünsche Euch viel Glück.', '', 'Schießt ihn vom Himmel!', 'Schattenhimmelsmeister Richtofen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'esES', 'El maestro de vuelo Crepuscular', 'Ve volando con Cuorevaja hasta el Nidal Crepuscular y encárgate del maestro de vuelo Crepuscular Richtofen.', 'El maestro de vuelo Crepuscular Richtofen se encuentra en el Nidal Crepuscular, arriba y al sureste. Independientemente de lo que hagamos aquí abajo, si no nos encargamos de él no habrá esperanza para Nido Alto.$b$bAunque Cuorevaja está demasiado herido como para ayudarte en la lucha, al menos puede llevarte hasta allí. El resto es cosa tuya, $n.$b$bBuena suerte.', '', '¡Derríbalo del cielo!', 'Richtofen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'esMX', 'El maestro de vuelo Crepuscular', 'Ve volando con Cuorevaja hasta el Nidal Crepuscular y encárgate del maestro de vuelo Crepuscular Richtofen.', 'El maestro de vuelo Crepuscular Richtofen se encuentra en el Nidal Crepuscular, arriba y al sureste. Independientemente de lo que hagamos aquí abajo, si no nos encargamos de él no habrá esperanza para Nido Alto.$B$BAunque Cuorevaja está demasiado herido como para ayudarte en la lucha, al menos puede llevarte hasta allí. El resto depende de ti, $n.$B$BBuena suerte. ', '', '¡Derríbalo del cielo!', 'Richtofen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'frFR', 'Le maître du ciel du Crépuscule', 'Volez jusqu’au Nichoir du Crépuscule avec Tranchecœur, et chargez-vous du Maître du ciel du Crépuscule Richtofen.', 'Le maître du ciel du Crépuscule Richtofen se trouve au nichoir du Crépuscule, dans les hauteurs au sud-est. Quoi que nous fassions ici, si nous ne nous chargeons pas de lui, tout espoir est perdu pour Haut-Perchoir.$b$bTranchecœur est trop gravement blessé pour vous aider dans ce combat, mais au moins, il pourra vous conduire là-bas. Le reste vous regarde, $n.$b$bBonne chance.', '', 'Descendez-le !', 'Maître du ciel du Crépuscule Richtofen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'itIT', 'Maestro dei Cieli del Crepuscolo', 'Arriva in volo con Cuoraguzzo al Nido del Crepuscolo e affronta il Maestro dei Cieli del Crepuscolo Richtofen.', 'Il Maestro dei Cieli del Crepuscolo Richtofen si trova presso il Nido del Crepuscolo, sopra... a sud-est. Qualunque cosa facciamo qui sotto, finché c\'è lui non c\'è speranza per Covoalto.$B$BAnche se Cuoraguzzo è troppo ferito per aiutarti a combattere, può portarti in volo fin lassù. Il resto è nelle tue mani, $n.$B$BBuona fortuna.', '', 'Bandiscilo dai cieli!', 'Maestro dei Cieli del Crepuscolo Richtofen', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'koKR', '황혼의 창공사령관', '칼날심장을 타고 황혼의 맹금요새로 가서 황혼의 창공사령관 리히토펜을 상대해야 합니다.', '황혼의 창공사령관 리히토펜은 남동쪽에 위치한 황혼의 맹금요새에 있습니다. 우리가 여기서 무슨 일을 하던, 만약 그를 처리하지 못하면, 마루둥지에 희망은 없습니다.$B$B칼날심장이 크게 다쳐 전투에서는 당신을 돕지 못하겠지만, 그래도 저 위까지 데려다 줄 순 있을 겁니다. 그 이후는 모두 당신 손에 달렸습니다, $n 님.$B$B행운을 빌겠습니다.', '', '그를 하늘 아래로 떨어뜨리십시오!', '황혼의 창공사령관 리히토펜', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'ptBR', 'Mestre Crepuscular dos Ares', 'Consiga uma carona com Lamicárdio até o Refúgio do Crepúsculo e acabe com o Mestre Crepuscular dos Ares Korpuskein.', 'O Mestre Crepuscular dos Ares está lá em cima, a sudeste daqui, no Refúgio do Crepúsculo. De nada adiantarão nossos esforços para salvar Alcândora aqui embaixo se não acabarmos com ele.$B$BLamicárdio ainda está muito fraco para lutar, mas ele $Go:a; levará até lá. O resto é com você, $n.$B$BBoa sorte.', '', 'Derrube-o para sempre dos céus!', 'Mestre Crepuscular dos Ares Korpuskein', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'ruRU', 'Распорядитель полетов Сумеречного Молота', 'Летите на Сердцерезе в Сумеречное гнездилище и покончите с сумеречным повелителем небес Рихтофеном.', 'Наверху на юго-востоке, в Сумеречном гнездилище, расположился сумеречный повелитель небес Рихтофен. Если мы с ним не разделаемся – все наши усилия здесь внизу напрасны, надежды для Скального гнездовья нет.$B$BХоть Сердцерез пока еще слишком слаб, чтобы помочь тебе в бою, он сможет отнести тебя на вершину. Остальное я вверяю тебе, $n.$B$BУдачи.', '', 'Подстрели его с воздуха!', 'Сумеречный повелитель небес Рихтофен', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'zhCN', '暮光天空之主', '乘坐锐爪飞心抵达暮光之巢，挑战暮光天空之主里克托芬。', '暮光天空之主里克托芬就在东南方高处的暮光之巢。无论我们怎么努力，只要还没有杀掉他，风巢飞龙就没有存活的希望。$B$B虽然锐爪飞心受了重伤，不能陪你作战，但是他可以载你一程。接下来就靠你自己了，$n。$B$B祝你好运。', '', '杀死暮光天空之主里克托芬！', '暮光天空之主里克托芬', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28098, 'zhTW', '暮光飛行師', '讓撕心者載你到暮光高巢，除掉暮光飛行師瑞奇歐分。', '暮光飛行師瑞奇歐分就在東南方的暮光高巢上。不論我們在下頭做什麼，只要沒解決掉他，風巢就沒有未來。$B$B撕心者傷得太重，無法協助你戰鬥，但牠可以載你飛上那裡。剩下的就靠你自己了，$n。$B$B祝你好運。', '', '除掉暮光飛行師瑞奇歐分!', '暮光飛行師', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'deDE', 'Rasha\'krak', 'Beschafft Rasha\'kraks Armschienen der Bindung.', 'Nach den Informationen, die Ihr geliefert habt, denke ich, dass einer der Handlanger des \"Erzherzogs\" seinen Blick bereits auf die Thoriumspitze gerichtet hat.$B$BSein Name ist Rasha\'krak und er patrouilliert entlang der Lavaseen südlich von uns. Er sieht so aus wie die latenten Feuerelementare, die in der Nähe wüten. Es gibt aber einen kleinen Unterschied: die Armschienen der Bindung, die er an seinen Handgelenken trägt.$B$BFindet ihn und bringt mir diese Armschienen, $n. Ich brauche einen Beweis dafür, dass wir uns in Gefahr befinden, bevor wir uns auf Kriege mit den Elementaren einlassen.', '', 'Er patrouilliert am Eingang zur Thoriumspitze. Was will er?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'esES', 'Rasha\'krak', 'Consigue los brazales de vínculo de Rasha\'krak.', 'Según los informes que traes, creo que es posible que uno de los esbirros de ese \"archiduque\" ya haya puesto los ojos en el Puesto del Torio.$B$BSu nombre es Rasha\'krak, y patrulla por los charcos de lava que tenemos al sur. Se parece a los elementales de fuego latentes que hay por aquí, salvo por un pequeño detalle: los brazales de vínculo que lleva en las muñecas.$B$BEncuéntralo y tráeme esos brazales, $n. Necesito pruebas de que estamos en peligro antes de empezar una guerra contra elementales.', '', 'Patrulla la entrada del Puesto del Torio. ¿Qué es lo que quiere?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'esMX', 'Rasha\'krak', 'Consigue los brazales de vínculo de Rasha\'krak.', 'Según los informes que traes, creo que uno de los esbirros de ese \"archiduque\" ya haya puesto los ojos en el Puesto del Torio.$B$BSu nombre es Rasha\'krak, y patrulla por los charcos de lava que tenemos al sur. Se parece a los elementales de fuego latentes que hay por aquí, salvo por un pequeño detalle: los brazales de vínculo que lleva en las muñecas.$B$BEncuéntralo y tráeme esos brazales, $n. Necesito pruebas de que estamos en peligro antes de empezar una guerra contra elementales.', '', 'Patrulla la entrada del Puesto del Torio. ¿Qué es lo que quiere?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'frFR', 'Rasha\'krak', 'Obtenez les Brassards de lien de Rasha’krak.', 'D’après les renseignements que vous avez récoltés, je crois que l’un des sbires de cet « archiduc » a déjà jeté son dévolu sur la halte du Thorium.$b$bIl s’appelle Rasha’krak et arpente les bassins de lave, juste au sud. Il est semblable aux autres élémentaires de feu qui rôdent dans les environs, à un détail près : les brassards de lien qui cerclent ses poignets.$b$bTrouvez-le et rapportez-moi ses brassards, $n. Il me faut la preuve que nous sommes en danger avant de nous engager dans un conflit avec les élémentaires.', '', 'Il patrouille devant l’entrée de la halte du Thorium. Mais que veut-il ?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'itIT', 'Rasha\'krak', 'Prendi i Bracciali Vincolanti di Rasha\'krak.', 'In base alle informazioni che hai recuperato, credo che uno dei servitori di questo \"Arciduca\" stia già puntando gli occhi sul Presidio del Torio.$b$bSi chiama Rasha\'krak e pattuglia le pozze di lava a sud. Ha l\'aspetto di un Elementale del Fuoco latente come quelli che si aggirano in zona, fatta eccezione per un particolare: i Bracciali Vincolanti che porta ai polsi.$b$bTrovalo e portami i suoi bracciali, $n. Prima d\'immischiarci in una guerra tra elementali, voglio la prova che siamo nei guai.', '', 'Pattuglia l\'ingresso del Presidio del Torio. Cosa vorrà?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'koKR', '라샤크라크', '라샤크라크가 가진 결속의 팔보호구를 획득해야 합니다.', '자네가 가져온 정보에 의하면, 그 \"대공작\"의 부하 중 하나가 이미 토륨 거점으로 눈을 돌렸을 수도 있겠군.$b$b그 부하의 이름은 라샤크라크라고 하는데, 이곳 바로 남쪽에 있는 용암 지대를 순찰하고 있네. 그놈은 이 주변에 숨어 돌아다니는 불의 정령과 비슷하게 생겼지만, 약간 다른 점이 있다네. 바로 그의 손목을 둘러싼 결속의 팔보호구지.$b$b라샤크라크를 찾아서 처치한 다음 그 팔보호구를 내게 가져오게, $n. 정령 전쟁에 휘말리기 전에 우리가 위험한 상태에 있다는 증거가 필요하니까 말이네.', '', '토륨 거점으로 가는 입구를 돌아다니고 있군. 대체 뭘 원하는 거지?', '라샤크라크', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'ptBR', 'Rasha\'krak', 'Obtenha as Braçadeiras da Vinculação de Rasha\'krak.', 'Com base nas informações que você entregou, acredito que um dos lacaios desse \"arquiduque\" possa já ter voltado os olhos para o Posto de Tório.$b$bO nome dele é Rasha\'krak, e ele patrulha as poças de lava logo ao sul. Ele parece com os elementais de fogo latente que devastam a região, exceto por uma pequena diferença: as braçadeiras da vinculação que envolvem os punhos dele.$b$bEncontre-o e traga essas braçadeiras para mim, $n. Preciso de uma prova de que não estamos mais em perigo antes que nos envolvamos em guerras elementais.', '', 'Ele patrulha a entrada do Posto de Tório. O que ele quer?', 'Rasha\'krak', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'ruRU', 'Раша\'крак', 'Добудьте сковывающие браслеты Раша\'крака.', 'Из найденной тобой информации можно заключить, что один из приспешников этого \"эрцгерцога\" уже обратил свое внимание на лагерь Братства Тория.$b$bЕго зовут Раша\'крак, он обитает у озер лавы к югу отсюда. Он похож на тех огненных элементалей, что свирепствуют неподалеку, за одним лишь небольшим исключением: на запястьях у него сковывающие браслеты.$b$bНайди его и принеси мне эти браслеты, $n. Прежде чем мы позволим втянуть себя в войны с элементалями, я хочу быть уверенным, что нам действительно угрожает опасность.', '', 'Он наблюдает за входом в лагерь Братства Тория. Чего он хочет?', 'Раша\'крак', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'zhCN', '拉沙科拉克', '获得拉沙科拉克的缚能护腕。', '基于你送来的情报，我相信这个“大公”的爪牙已经把注意转向了瑟银哨塔。$b$b他的名字叫做拉沙科拉克，就徘徊在我们南边的熔岩池里。他看起来和潜伏在附近的火元素很像，只有一个小小的不同：戴在其手腕上的缚能护腕。$b$b找到他，把那对护腕带给我，$n。在卷入一场元素战争之前，我需要证据来证明我们确实陷入了危险。', '', '他游荡到了瑟银哨塔的入口。他想干吗？', '拉沙科拉克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28099, 'zhTW', '拉沙克拉克', '取得拉沙克拉克的地縛護腕。', '根據你的消息，我相信已經有一個「大公」的爪牙，準備對瑟銀哨塔下手。$b$b他的名字叫拉沙克拉克，就在我們南邊熔岩池附近巡邏。他看起來和附近狂怒的火元素沒兩樣，唯一的不同，在於他手上的護腕。$b$b找到他，把他的護腕帶回來給我，$n。在我們涉入元素戰爭之前，我需要證明我們已經身處危險之中。', '', '他在瑟銀哨塔入口附近巡邏。他到底想要什麼？', '拉沙克拉克', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'deDE', 'Ein sprechendes Totem', 'Sprecht mit dem Totem von Ruumbo in der Nähe des Lagers der Totenwaldfelle.', 'Das Furbolgdorf im Süden ist die Heimat der Totenwaldfelle, die Todfeinde der Holzschlundfeste sind. Sie sind verderbt und widerwärtig, wie so vieles andere. Die Fäulnis des Waldes hat sie in den Wahnsinn getrieben.$B$BNun ist allerdings etwas Außergewöhnliches in der Nähe des Lagers geschehen. Reisende, die hier vorbeikamen, haben gesagt, dass das Totem an der Straße zu ihnen gesprochen hat.$B$BIch habe noch nie etwas gehört, wenn ich mich ihm genähert habe. Vielleicht spricht es mit Euch, $n? Vielleicht könnt Ihr für mich herausfinden, was dort vor sich geht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'esES', 'Un tótem que habla', 'Habla con el tótem de Ruumbo cerca de la aldea Muertobosque.', 'El poblado fúrbolg, al sur, es el hogar de la tribu Muertobosque, los peores enemigos de los Fauces de Madera. Son corruptos y mezquinos, como tantos otros llevados hasta la locura por la corrupción de estos bosques.$B$BSin embargo, ha ocurrido algo inusual cerca del poblado. Unos viajeros que venían hacia aquí han dicho que el tótem que hay junto al camino les ha hablado.$B$BYo no he oído nada cuando me he acercado. Puede vez te hable a ti, $n. Tal vez puedas hacerme el favor de descubrir qué está ocurriendo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'esMX', 'Un tótem que habla', 'Habla con el tótem de Ruumbo cerca de la aldea Muertobosque.', 'El poblado fúrbolg, al sur, es el hogar de la tribu Muertobosque, los peores enemigos de los Fauces de Madera. Son corruptos y mezquinos, como tantos otros llevados hasta la locura por la corrupción de estos bosques.$B$BSin embargo, ha ocurrido algo inusual cerca del poblado. Unos viajeros que venían hacia aquí han dicho que el tótem que hay junto al camino les ha hablado.$B$BYo no he oído nada cuando me he acercado. Puede vez te hable a ti, $n. Tal vez puedas hacerme el favor de descubrir qué está ocurriendo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'frFR', 'Un totem parlant', 'Allez voir le Totem de Ruumbo près du Village des Mort-bois.', 'Le village furbolg au sud est le foyer de la tribu Mort-bois, les ennemis jurés des Grumegueules. Ils sont corrompus et vils comme tant d’autres, rendus fous par la souillure de ces bois.$b$bQuelque chose d’inhabituel est arrivé près du village cependant. Des voyageurs ont rapporté que le totem près de la route leur avait parlé.$b$bChaque fois que je m’en suis approché, je n’ai rien entendu. Peut-être vous parlera-t-il à vous, $n ? Pourriez-vous découvrir pour moi ce qui se passe là-bas ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'itIT', 'Il totem parlante', 'Parla con il Totem di Ruumbo vicino al Villaggio dei Legnomorto.', 'Nel villaggio dei Mezzorsi a sud vive la Tribù Legnomorto, nemici giurati dei Mordilegno. Sono creature corrotte e malvagie come tante altre, impazzite a causa della corruzione che ha colpito questa foresta.$B$BTuttavia, è successo qualcosa di strano nei pressi del loro villaggio. I viandanti hanno riferito che il totem vicino alla strada gli ha parlato.$B$BIo non ho mai sentito niente tutte le volte che mi sono avvicinato. Forse a te parlerà, $n. Perché non cerchi di scoprire cosa sta succedendo?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'koKR', '말하는 토템', '마른가지 마을 근처에 있는 룸보의 토템과 대화해야 합니다.', '남쪽에 있는 펄볼그 마을에는 나무구렁 일족의 원수인 마른가지 일족이 살고 있소. 이들은 이 숲의 타락에 물들어 미쳐버린 다른 부족처럼 타락하고 더럽혀졌지.$B$B최근 이 마을 근처에서 뭔가 수상한 일이 벌어졌소. 이곳에 오는 여행자들이 그러던데, 길가에 있는 토템이 말을 걸었다더군.$B$B내가 다가갔을 땐 아무 소리도 못 들었는데 말이오. 토템이 당신에겐 말을 걸지도 모르오, $n. 무슨 일이 일어나는 중인지 알아보시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'ptBR', 'Um totem falante', 'Fale com o Totem de Ruumbo, próximo à Vila da Lenha Morta.', 'A vila dos pelursos ao sul é o lar da tribo Lenha Morta, inimigos declarados dos Presamatos. Eles são tão sórdidos e torpes como tantos outros ensandecidos pela mácula desta floresta.$B$BAlgo incomum aconteceu próximo à aldeia, contudo. Uns viajantes que vinham para cá disseram que o totem próximo à estrada falou com eles.$B$BJamais ouvi coisa alguma ao aproximar-me dele. Será que ele falará com você, $n? Talvez você consiga descobrir o que está acontecendo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'ruRU', 'Говорящий тотем', 'Поговорите с тотемом Роумбо у Деревни Мертвого Леса.', 'Деревня фурболгов, что к югу отсюда, – это дом племени Мертвого Леса, заклятых врагов племени Древобрюхов. Как и на многих других, на них тоже подействовала порча этих лесов, приведя их к безумию.$b$bВозле этой деревни происходит что-то необычное. Проходившие там путники рассказывали, что видели у дороги тотем, который разговаривал с ними.$b$bНо я ничего не слышал, хотя много раз проходил мимо. Может быть, этот тотем захочет поговорить с тобой, $n? Может быть, тебе удастся выяснить, в чем дело?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'zhCN', '说话的图腾', '跟死木村附近卢姆波的图腾谈一谈。', '南边的熊怪村是死木部族的家，他们也是木喉的宿敌。他们像许多其他熊怪一样堕落而污秽，这片森林的污染使他们发狂。$B$B不过，村子附近发生了不同寻常的事情。来这里的旅行者们说路边的图腾对他们开口说话了。$B$B每次我接近的时候却什么都没有听到。也许它会跟你说话，$n？或许你可以帮我查清楚到底发生了什么事。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28100, 'zhTW', '會說話的圖騰', '在死木村附近與魯姆波圖騰交談。', '在這裡南邊的熊怪村莊是木喉的死敵死木部落的家園。他們和各種其他生物一樣被腐化了，受到這座森林的污染影響而變得瘋狂。$B$B然而在村莊的附近發生了一些不尋常的事。根據來到這裡的旅人們的描述，在路邊的圖騰曾經和他們說話。$B$B然而每次我靠近它都沒有聽到任何東西。也許它會和你說話，$n？也許你可以幫我找出到底發生了什麼事。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'deDE', 'Mathias\' Befehl', 'Sprecht mit Meister Mathias Shaw am Drachenmalpass.', 'Die Ereignisse in letzter Zeit machen es noch offensichtlicher, dass es zur Absicherung unseres Volkes nicht ausreicht, hier einfach nur auszuharren und sporadische Angriffe auf Grim Batol auszuführen - geschweige denn, dass wir Grim Batol zurückerobern könnten. Wir müssen aggressiver sein und sie mitten ins Herz treffen.$B$BIch habe zugestimmt, Eurem Kommandanten Drachen zur Unterstützung zu senden. Sie hatten ohnehin schon über einem Plan gebrütet, wie man auf Grim Batol losschlagen kann, also solltet Ihr Eure Aufmerksamkeit zunächst einmal darauf konzentrieren.$B$BIhr solltet Mathias Shaw am Drachenmalpass im Canyon südlich unter uns finden.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'esES', 'La orden de Mathias', 'Habla con Mathias Shaw en el Paso Faucedraco.', 'Los acontecimientos recientes han dejado aún más claro que esconderse aquí y lanzar ataques esporádicos contra Grim Batol no basta para mantener a salvo a los nuestros, y mucho menos para recuperar Grim Batol. Tenemos que ser más agresivos, tenemos que apuntar al corazón.$B$BHe aceptado enviar dragones para ayudar a tus comandantes. Ya estaban elaborando un plan para atacar Grim Batol, así que deberías empezar por ahí.$B$BMathias Shaw debería estar en el Paso Faucedraco, en el cañón que hay debajo de nosotros, hacia el sur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'esMX', 'La orden de Mathias', 'Habla con Mathias Shaw en el Paso Faucedraco.', 'Los acontecimientos recientes han dejado aún más claro que esconderse aquí y lanzar ataques esporádicos contra Grim Batol no basta para mantener a salvo a los nuestros, y mucho menos para recuperar Grim Batol. Tenemos que ser más agresivos, tenemos que apuntar al corazón.$B$BHe aceptado enviar dragones para ayudar a tus comandantes. Ya estaban elaborando un plan para atacar Grim Batol, así que deberías empezar por ahí.$B$BMathias Shaw debería estar en el Paso Faucedraco, en el cañón que hay debajo de nosotros, hacia el sur.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'frFR', 'Aux ordres de Mathias', 'Allez voir Maître Mathias Shaw dans la Passe des Gueules-de-dragon.', 'Les récents évènements ont montré de manière claire qu\'il ne suffisait plus de tenir nos positions et de lancer des attaques sporadiques sur Grim Batol pour assurer la sécurité de notre peuple, et encore moins pour reprendre Grim Batol à l\'ennemi. Nous devons nous montrer plus agressifs et frapper là où cela leur fera le plus mal.$B$BJ\'ai envoyé plusieurs dragons pour aider vos commandants. Ils étaient déjà en train de réfléchir à un plan d\'attaque de Grim Batol, je vous suggère donc de concentrer vos efforts sur le même objectif.$B$BVous trouverez Mathias Shaw dans la passe des Gueules-de-dragon, dans le canyon situé au sud d\'ici.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'itIT', 'Ordine di Mathias', 'Parla con il Maestro Mathias Shaw al Passo delle Fauci di Drago.', 'Gli ultimi eventi hanno reso ancora più palese che rintanarsi qui e sferrare qualche attacco sporadico a Grim Batol non basta a tenere al sicuro la nostra razza, men che meno a riconquistare Grim Batol. Dobbiamo essere più aggressivi, colpirli al cuore.$B$BHo acconsentito a inviare dei draghi in aiuto ai tuoi comandanti. Stavano già organizzando un piano per colpire Grim Batol, quindi per prima cosa dovresti concentrarti lì.$B$BTrova Mathias Shaw al Passo delle Fauci di Drago, nel canyon sotto di noi, a sud.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'koKR', '마티아스가 지휘한다', '용아귀 고개에 있는 단장 마티아스 쇼와 대화해야 합니다.', '최근의 사건들을 통해 더 확실해진 게 하나 있습니다. 여기를 지키며 이따금 적을 공격하는 정도로는 그림 바톨을 되찾을 수 없음은 물론이고, 우리 자신도 제대로 지킬 수 없다는 것이죠. 좀 더 공격적으로 나가야겠습니다. 놈들의 심장부를 치는 겁니다.$B$B저는 그대들의 사령관을 돕는 데 동의하고 용들을 보냈습니다. 그들은 이미 그림 바톨 공격 계획을 의논하고 있으니, 그대도 먼저 그쪽에 주의를 기울이는 게 좋겠습니다.$B$B남쪽으로 우리 발아래 있는 협곡에 용아귀 고개가 있습니다. 거기서 마티아스 쇼를 찾으실 수 있을 겁니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'ptBR', 'O comando de Mathias', 'Fale com o Mestre Mathias Shaw no Desfiladeiro da Presa do Dragão.', 'Acontecimentos recentes tornam mais evidente que se esconder por aqui e realizar ataques esporádicos a Grim Batol não é o suficiente para manter nossa espécie a salvo, muito menos para retomar Grim Batol. Precisamos ser mais agressivos e atacá-los direto no coração.$B$BEu concordei em mandar dragões para ajudar seus comandantes. Eles já estavam planejando um ataque a Grim Batol e você deveria concentrar sua atenção nesse lugar.$B$BEncontre Mathias Shaw no Desfiladeiro da Presa do Dragão, no cânion a sul daqui.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'ruRU', 'Под началом Матиаса', 'Отправляйтесь в ущелье Драконьей Пасти и поговорите с мастером Матиасом Шоу.', 'Недавние события показали, что для обеспечения безопасности и тем более для освобождения Грим Батола одиночных вылазок недостаточно. Нам нужно действовать более агрессивно – необходимо нанести удар в самое сердце врага.$B$BМы отправим драконов к вашим командирам. Они уже разрабатывают план операции по штурму Грим Батола, так что тебе тоже стоит уделить этому внимание.$B$BМатиас Шоу находится на тропе Драконьей Пасти, что к югу отсюда.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'zhCN', '马迪亚斯的命令', '跟龙喉小径的马迪亚斯·肖尔大师谈一谈。', '最近的事件表明，坚守阵地，并对格瑞姆巴托展开零星的进攻根本不足以保护我们的人，更别提将其收复了。我们需要更具进攻性，我们得直击他们的要害。$B$B我同意派巨龙去协助你的指挥官们。他们已经制定了一份攻打格瑞姆巴托的计划，你最好先把注意力集中在那里。$B$B你应该可以在龙喉小径找到马迪亚斯·肖尔，那地方就在我们下面南方的峡谷里。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28101, 'zhTW', '馬迪亞斯的指示', '到龍喉小徑找馬迪亞斯‧肖爾說話。', '最近幾件事讓我更加明白，一直躲在這兒，對格瑞姆巴托發動幾波零星的攻擊，並不是保全我族的長遠之計，更別說是奪回格瑞姆巴托了。我們必須更積極些，朝他們的心臟進攻。$B$B我已經答應派幾隻龍協助你的指揮官。他們正在策動一波針對格瑞姆巴托的攻擊，這應該是你的當務之急。$B$B你可以在龍喉小徑找到馬迪亞斯‧肖爾，就在我們下方的峽谷南邊。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'deDE', 'Bekämpft die Macht', 'Tötet 8 Jadefeuerschurken oder Jadefeuerschattenpirscher in den Ruinen von Constellas.', 'Es ist Zeit für den entscheidenden Schlag gegen die Jadefeuer!$B$BWenn Ihr bereit seid, durch ihre verderbte Barriere zu brechen und ihrem Anführer den Kampf anzusagen, würde ich Euch auch darum bitten, uns gegen die niederen Satyrn zu unterstützen. Unsere Druiden kämpfen erbittert, aber Eure Stärke könnte helfen, die Schlacht zu unseren Gunsten zu wenden.$B$BZeigt den Jadefeuersatyrn, dass Ihre Macht nicht länger die Vorherrschaft über diese Lande hat!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'esES', 'Enfréntate al poder', 'Mata a 8 pícaros Fuego de Jade o acechasombras Fuego de Jade en las Ruinas de Constellas.', '¡Ha llegado la hora de la última ofensiva contra los Fuego de Jade!$B$BSi estás $glisto:lista; para atravesar su asquerosa barrera y llevar la lucha hasta su líder, te pediré también que nos ayudes con los sátiros inferiores. Nuestros druidas luchan ferozmente, pero tu fuerza puede hacer que cambien las tornas.$B$B¡Demuestra a los Fuego de Jade que estas tierras ya no estarán sometidas a su poder!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'esMX', 'Enfréntate al poder', 'Mata a 8 pícaros Fuego de Jade o acechasombras Fuego de Jade en las Ruinas de Constellas.', '¡Ha llegado la hora de la última ofensiva contra los Fuego de Jade!$B$BSi estás $glisto:lista; para atravesar su asquerosa barrera y llevar la lucha hasta su líder, te pediré también que nos ayudes con los sátiros inferiores. Nuestros druidas luchan ferozmente, pero tu fuerza puede hacer que cambien las tornas.$B$B¡Demuestra a los Fuego de Jade que estas tierras ya no estarán sometidas a su poder!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'frFR', 'Combattre le pouvoir', 'Tuez 8 Voleurs jadefeu ou Liges d’ombre jadefeu dans les Ruines de Constellas.', 'Il est temps de lancer l’assaut final contre les Jadefeu !$b$bSi vous êtes $gprêt:prête; à franchir leur vile barrière et à combattre leur chef, je vous demanderai également de vous occuper des autres Satyres. Nos druides se battent férocement, mais votre force peut nous aider à renverser la bataille.$b$bMontrez aux Jadefeu qu’ils ne régneront plus sur ces contrées !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'itIT', 'Combattere il potere', 'Uccidi 8 Ladri Giadafulgida o Inseguitori dell\'Ombra Giadafulgida nelle Rovine di Constellas.', 'È giunto il momento di sferrare l\'attacco finale contro i Giadafulgida!$B$BSe sei $gpronto:pronta; a penetrare la loro barriera e ad affrontare il loro capo, vorrei che ti occupassi anche dei Satiri Minori. I nostri Druidi si battono con ardore, ma la tua forza può aiutarli a rovesciare le sorti dello scontro.$B$BFa\' vedere ai Giadafulgida che il loro potere non terrà più sotto scacco queste terre!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'koKR', '힘에 대항하라', '콘스텔라스 폐허에 있는 비취불꽃 도적이나 비취불꽃 그림자추적자를 8마리 처치해야 합니다.', '비취불꽃 놈들에게 최후의 공세를 펼칠 시간이에요!$B$B만약 타락한 방벽을 뚫고, 놈들의 우두머리와 싸울 준비가 되셨다면, 하급 사티로스도 쓰러뜨려 주세요. 우리 드루이드들도 굉장히 맹렬하게 싸우지만, 그대의 힘은 전세를 뒤집을 수 있을 거예요.$B$B비취불꽃 녀석들에게 놈들의 힘으로는 이제 이 땅을 지배할 순 없다는 걸 보여주세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'ptBR', 'Faça a coisa certa', 'Mate 8 Desgarrados Flamejade ou Assombrantes Flamejade nas Ruínas de Constellas.', 'É chegada a hora do derradeiro golpe contra os Flamejade!$B$BJá que você está $Gpreparado:preparada; para atravessar a barreira infame e enfrentar o líder deles, eu também lhe pedirei ajuda contra os sátiros inferiores. Nossos druidas lutam com violência, mas a sua força pode ajudar a mudar o curso da batalha.$B$BMostre aos Flamejade que eles não mais dominarão estas terras!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'ruRU', 'Борись с властью', 'Убейте 8 разбойников или тенеловов из племени Нефритового Пламени в руинах Констелласа.', 'Пришло время для заключительного удара по племени Нефритового Пламени!$b$bЕсли ты $gготов:готова; пройти через их грязный барьер и сразиться с их вожаком, я хочу попросить тебя заодно убить и их младших сатиров. Наши друиды яростно сражаются, но твоя сила не будет лишней.$b$bДай сатирам понять, что им больше не придется властвовать в этих землях!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'zhCN', '挑战支配权', '在克斯特拉斯废墟杀死8名碧火盗贼或碧火捕猎者。', '是时候对碧火萨特进行最后一次进攻了！$B$B假如你做好准备，要冲破他们肮脏的屏障，对付他们首领的话，那么我还要求你帮我们处理掉那些低级的萨特。我们的德鲁伊作战很凶猛，但有了你的力量，战局就能被扭转过来。$B$B让那些碧火萨特知道，这片土地不再处于他们的权力支配之下！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28102, 'zhTW', '打擊力量', '在克斯特拉斯廢墟殺死8名碧火盜賊或是碧火巡影者。', '該是給那些碧火薩特最後一擊的時候了!$B$B如果你已準備好突破他們邪惡的魔法障壁，並且與他們的領導者進行決戰，我打算同時要求你幫忙對付那些較次級的薩特。我們的德魯伊勇猛的戰鬥著，但你的力量可以有效的幫我們扭轉戰局。$B$B讓那些碧火薩特知道他們的力量再也不能控制這些土地!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'deDE', 'Leichte Aufgabe', 'Tötet 8 Zwielichtspeerwärter.', 'Der Hinterhalt ging problemlos vonstatten. Die meisten Wachen sind tot, aber einige sitzen immer noch bei den Wagen fest.$B$BGeht dorthin und erledigt sie, damit wir diesen Angriff ins Rollen bringen können.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'esES', 'Blancos fáciles', 'Mata a 8 depositarios de lanzas Crepusculares.', 'La emboscada ha ido bien. La mayoría de los guardias están muertos, pero aún quedan algunos junto a los vagones.$B$BVe ahí y acaba con ellos para que podamos seguir avanzando por el cañón.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'esMX', 'Blancos fáciles', 'Mata a 8 depositarios de lanzas Crepusculares.', 'La emboscada ha ido bien. La mayoría de los guardias están muertos, pero aún quedan algunos junto a los vagones.$B$BVe ahí y acaba con ellos para que podamos seguir avanzando por el cañón.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'frFR', 'Des cibles faciles', 'Tuez 8 Porte-lances du Crépuscule.', 'L\'embuscade s\'est déroulée comme prévu. La plupart des gardes sont morts, mais quelques-uns d\'entre eux sont encore immobilisés avec les chariots.$B$BEntrez dans le canyon et éliminez-les afin que nous puissions poursuivre notre progression.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'itIT', 'Un gioco da ragazzi', 'Uccidi 8 Custodi delle Lance del Crepuscolo.', 'L\'imboscata ha avuto successo. La maggior parte delle guardie è morta, ma alcune sono ancora bloccate nei vagoni.$B$BVai laggiù ed eliminale, così potremo addentrarci nel canyon.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'koKR', '쉬운 상대', '황혼의 창투사 8명을 처치해야 합니다.', '공격은 성공적이었습니다. 경비병 대부분이 이미 죽었지요. 하지만 아직 일부가 짐수레 주위에서 꼼짝도 안 하고 있습니다.$B$B가서 놈들을 처리해 주십시오. 그래야 우리 군대가 협곡 깊숙이 나아갈 수 있습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'ptBR', 'Presas fáceis', 'Mate 8 Lanceadores do Crepúsculo.', 'A emboscada aconteceu sem grandes dificuldades. A maioria dos guardas está morta, mas alguns ainda estão nas carroças.$B$BVá até lá e elimine-os para que possamos avançar pelos cânions.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'ruRU', 'Легкая добыча', 'Убейте 8 сумеречных копьеносцев.', 'Нападение из засады прошло, как и было задумано. Мы перебили почти всех охранников, но несколько осталось за фургонами.$B$BУничтожь их, чтобы мы могли двинуться дальше.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'zhCN', '轻松杀敌', '杀死8名暮光持矛守卫。', '伏击很顺利。大多数卫兵都死了，少数还被压制在马车那里。$B$B就在峡谷下面，你可以在西边看到他们。过去把他们都干掉，我们朝峡谷进发。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28103, 'zhTW', '軟柿子', '殺死8個暮光持矛衛兵。', '埋伏進行得很順利。大部份的守衛都死光了，只剩下一些還死守貨車作困獸之鬥。$B$B到那裡去清光他們，我們才能繼續朝峽谷邁進。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'deDE', 'Wertvolle Waren', 'Bergt einen Torstein der Zwielichtkarawane. Die Speerwächter sollten Schlüssel bei sich tragen, die Euch Zugang zu den Truhen mit den Torsteinen verschaffen.', 'Die Tore weiter unten im Canyon sind geschlossen. Wir haben ausgekundschaftet, dass es eine Art Stein gibt, den sie in Kisten in ihren Karawanen mitführen, und zum Öffnen der Tore benutzen.$B$BGeht dort hinunter und durchsucht die Leichen der Wachen nach einem Truhenschlüssel. Wenn Ihr ihn findet, benutzt ihn bei einer der großen Truhen auf den Karawanenwagen und bringt den Torstein zu mir zurück.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'esES', 'Bienes preciosos', 'Recupera la piedra de puerta de la caravana Crepuscular. Los guardalanzas deberían tener las llaves que abren los cofres que contienen la piedra de puerta.', 'Las puertas que hay cañón abajo están cerradas. Nuestros exploradores descubrieron que sus caravanas llevan, metida en cofres, una piedra que usaron para abrir la puerta.$B$BVe allí y busca la llave del cofre en los cuerpos de los guardias. Cuando la encuentres, úsala en los cofres que hay en los vagones de las caravanas y tráeme la piedra de puerta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'esMX', 'Bienes preciosos', 'Recupera la piedra de puerta de la caravana Crepuscular. Los guardalanzas deberían tener las llaves que abren los cofres que contienen la piedra de puerta.', 'Las puertas que hay cañón abajo están cerradas. Nuestros exploradores descubrieron que sus caravanas llevan, metida en cofres, una piedra que usaron para abrir la puerta.$B$BVe allí y busca la llave del cofre en los cuerpos de los guardias. Cuando la encuentres, úsala en los cofres que hay en los vagones de las caravanas y tráeme la piedra de puerta.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'frFR', 'Une cargaison de valeur', 'Récupérez une Pierre-portail de la caravane du Crépuscule. Les Porte-lances détiennent les clés qui permettent d\'ouvrir les coffres renfermant les pierres-portails.', 'Les portes situées en bas du canyon sont fermées. D\'après nos éclaireurs, pour les ouvrir, les soldats du Crépuscule utilisent une sorte de pierre qu\'ils transportent avec eux dans leurs caravanes.$B$BDescendez donc voir si vous ne trouvez pas une clé en fouillant les corps des gardes... Quand vous l\'aurez trouvée, utilisez-la sur l\'un des coffres des chariots de la caravane, et rapportez-moi la pierre-portail.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'itIT', 'Merce preziosa', 'Recupera una Pietra Portale del Martello del Crepuscolo. I Custodi delle Lance del Crepuscolo dovrebbero avere le chiavi per aprire i bauli della Pietra Portale.', 'Le porte in fondo al canyon sono chiuse. Abbiamo visto che le loro carovane trasportano una pietra che usano per aprirle. Essa è riposta in un baule.$B$BVai laggiù e cerca una chiave del baule sul corpo delle guardie. Una volta raccolta, usala su uno di questi bauli che si trovano sui vagoni della carovana e portami la Pietra Portale.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'koKR', '귀중한 물품', '황혼의 망치단 관문돌을 입수해야 합니다. 관문돌 궤짝을 여는 열쇠는 창투사들이 가지고 있을 것입니다.', '협곡 더 안쪽에 있는 관문은 닫혀 있습니다. 우리 정찰병들이 알아낸 바로는, 짐마차에 실린 궤짝 안에 놈들이 관문을 여는 데 쓰이는 돌이 있다더군요.$B$B저기 내려가서 놈들의 시체에서 궤짝 열쇠를 찾아보십시오. 열쇠를 찾거든, 짐마차에 실린 궤짝에 사용해서 관문돌을 찾아 제게 가져오십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'ptBR', 'Bens preciosos', 'Recupere uma Pedra-portal da Caravana do Crepúsculo. As chaves dos baús com a pedra-portal devem estar com os Lanceadores.', 'Os portões abaixo do cânion estão fechados. Nossas patrulhas descobriram uma pedra que eles carregavam nas caravanas, que é usada para abrir os portões.$B$BDesça lá e procure uma chave de baú junto aos corpos dos guardas. Quando achá-la, use num dos baús das carroças da caravana e traga a pedra-portal para mim.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'ruRU', 'Ценный груз', 'Найдите камень-ключ Сумеречного каравана. Ключи от сундуков с камнями-ключами должны быть у сумеречных копьеносцев.', 'Врата, открывающие проход дальше, заперты. Разведчики сообщают, что караванщики открывают их с помощью особых камней, которые возят в сундуках.$B$BСпустись в ущелье и постарайся раздобыть ключ от такого сундука у одного из убитых охранников. Потом найди фургон с сундуком, открой сундук и принеси мне камень-ключ.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'zhCN', '珍贵的货物', '缴获一颗暮光之锤门石。存放门石的箱子钥匙应该在持矛守卫身上。', '峡谷深处的大门被关闭了。我们的侦察表明，他们的车队里载着一些箱子，里面的某种石头可以打开大门。$B$B去守卫的尸体上找一把箱子钥匙来。找到以后，就用它打开车队里的一个箱子，将门石给我带来。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28104, 'zhTW', '珍貴的貨物', '取得暮光商隊門石。那些持矛衛兵身上應該有一把鑰匙，能開啟裝著石頭的箱子。', '敵人關閉了峽谷前方的大門。根據斥候回報，他們的商隊在箱子中攜帶了一顆石頭，是他們用來開啟大門的工具。$B$B到那裡去搜尋守衛的屍體，找到打開箱子的鑰匙。找到後，就用它打開商隊貨車上的某個箱子，再把門石帶回來給我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'deDE', 'Kavem der Gefühllose', 'Beschafft den Schlüssel aus Neferset von Kavem dem Gefühllosen.', 'Kavem war schon grausam genug, bevor die Neferset sich Todesschwinge verschrieben haben. Sein Vergnügen, anderen Schmerzen zuzufügen, grenzt inzwischen an Wahnsinn.$B$BEr hat jetzt die Schlüssel für unsere Fesseln.$B$BEr haust in dem Gebäude neben uns zu meiner Rechten.$B$BFindet ihn und nehmt seinen Schlüssel an Euch. Zögert nicht und erweist ihm keine Gnade. Er würde Euch die Kehle aufschlitzen und sich nichts dabei denken.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'esES', 'Kavem el Inconmovible', 'Consigue la llave Neferset de Kavem el Inconmovible.', 'Kavem ya era bastante cruel antes de que los Neferset se aliasen con Alamuerte. Ahora su placer por causar dolor roza lo macabro.$B$BÉl es el que tiene las llaves de nuestros grilletes.$B$BSe encuentra en el edificio que está a nuestro lado, a mi derecha.$B$BEncuéntralo y quítale la llave. No dudes ni le demuestres compasión. Él te cortará el cuello sin pensarlo siquiera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'esMX', 'Kavem el Inconmovible', 'Consigue la llave Neferset de Kavem el Inconmovible.', 'Kavem ya era bastante cruel antes de que los Neferset se aliasen con Alamuerte. Ahora su placer por causar dolor roza lo macabro.$B$BÉl es el que tiene las llaves de nuestros grilletes.$B$BSe encuentra en el edificio que está a nuestro lado, a mi derecha.$B$BEncuéntralo y quítale la llave. No dudes ni le demuestres compasión. Él te cortará el cuello sin pensarlo siquiera.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'frFR', 'Kavem le Cruel', 'Prenez la Clé de Neferset à Kavem le Cruel.', 'Kavem a fait preuve de cruauté avant que Neferset ne pactise avec Aile de mort. Le plaisir qu\'il tire de la douleur frise à présent la démence.$B$BC\'est lui qui détient actuellement la clé de nos chaînes.$B$BIl réside dans le bâtiment à côté de nous, à ma droite.$B$BAllez le chercher et prenez-lui la clé. N\'ayez pas d\'hésitation et ne lui montrez aucune pitié. Il vous trancherait la gorge sans aucun remords.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'itIT', 'Kavem l\'Irriducibile', 'Recupera la Chiave dei Neferset da Kavem l\'Irriducibile.', 'Kavem era già abbastanza crudele prima che i Neferset stringessero un patto con Alamorte, ma ora il gusto che dimostra nel far soffrire gli altri ha superato i limiti della follia.$b$bLui ha le chiavi delle nostre catene.$b$bAbita nell\'edificio accanto a questo, alla mia destra.$b$bTrovalo e sottraigli la chiave. Non mostrare pietà o esitazione, altrimenti ti troverai con la gola squartata.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'koKR', '무자비한 카벰', '무자비한 카벰에게서 네페르세트 열쇠를 입수해야 합니다.', '네페르세트가 데스윙과 한편이 되기 전에도 카벰은 악귀처럼 잔인한 존재였소. 적에게 고통을 가하면서 기쁨을 느끼는 놈의 행동은 이제 미쳤다고밖에는 표현할 말이 없는 수준이오.$B$B하지만 이제 우리 운명의 열쇠를 지닌 사람도 바로 그 녀석이오.$B$B우리 옆, 바로 내 오른쪽에 있는 건물에 가보면 찾을 수 있소.$B$B그를 찾아내어 열쇠를 빼앗아 오시오. 녀석에게 자비를 베풀 생각은 마시오. 눈 깜짝할 새 당신 목을 베고도 아무렇지도 않아 할 그런 녀석이니 말이오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'ptBR', 'Kavem, o Caloso', 'Roube a Chave de Neferset de Kavem, o Caloso.', 'Kavem já era cruel o bastante antes dos Neferset entrarem em acordo com Asa da Morte. Seu prazer em deflagrar a dor agora beira a loucura.$B$BÉ ele quem guarda as chaves de nossos grilhões.$B$BEle habita o edifício próximo a nós, à direita.$B$BEncontre-o e pegue as chaves. Não hesite nem tenha piedade. Ele cortará sua garganta sem pestanejar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'ruRU', 'Кавем Бессердечный', 'Заберите неферсетский ключ у Кавема Бессердечного.', 'Кавем отличался жестокостью еще до объединения неферсетов со Смертокрылом – теперь же его садизм граничит с безумием.$B$BЭто он хранит ключи от наших оков.$B$BНайти его можно в соседнем здании, справа.$B$BРазыщи его и забери у него ключ. Не колеблись и не проявляй сострадания – он при первом удобном случае перерезал бы тебе горло без каких-либо раздумий.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'zhCN', '无情者卡文', '从无情者卡文身上获得尼斐塞特之匙。', '在尼斐塞特人同死亡之翼签订协议之前，卡文就以残忍著称。他以别人的痛苦为乐，如今更是几近疯狂。$B$B他掌握着解开我们枷锁的钥匙。$B$B他就居住在我右边的这幢建筑里。$B$B找到他，把钥匙拿走。不要心慈手软。他可是杀人不眨眼的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28105, 'zhTW', '無情的卡維姆', '從無情的卡維姆身上取得奈斐賽特鑰匙。', '在奈斐賽特受雇於死亡之翼之前，卡維姆就已經夠殘忍的了。他從別人的痛苦中得到快樂的行為已經近乎瘋狂。$B$B他握有我們身上鐐銬的鑰匙。$B$B他住在我們旁邊的建築物裡，就是我右手邊那座。$B$B找到他並從他身上取得鑰匙。不要猶豫或是憐憫他。他會甚麼都不想的切開你的喉嚨。', '', '', '', '', '', '', 0);
