INSERT INTO `quest_template_locale` VALUES (28258, 'koKR', '시그닐다 하드포지 만나기', '스톰윈드의 훈련장에 있는 시그닐다 하드포지와 대화해야 합니다.', '시그닐다 하드포지가 전언을 보냈습니다. 최대한 빨리 스톰윈드 구 시가지에 있는 훈련장으로 자기를 만나러 와달라더군요. 무척 중요한 일인 것 같았습니다, $n 님.', '', '', '', '', '', '스톰윈드의 훈련장에 있는 시그닐다 하드포지와 대화해야 합니다.', 0);
