INSERT INTO `quest_template_locale` VALUES (28267, 'frFR', 'Le peloton d\'exécution', 'Parlez au commandant Schnottz.', 'Fenez avec moi, $R.$B$BCh\'aimerais que fous foyiez quelque chose.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'itIT', 'Plotone d\'esecuzione', 'Parla con il Comandante Schnottz.', 'Fieni fuori kon me, $R.$b$bZ\'è qvalcosa che defi federe.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'koKR', '총살', '사령관 슈노츠와 대화해야 합니다.', '나와 함께 저 밖으로 나가지, $r.$b$b자네에게 보여줄 게 있어.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'ptBR', 'Esquadrão de fuzilamento', 'Fale com o Comandante Schnottz.', 'Vem comigo, $r.$b$bEcziste algo que querro mostrarr parra você.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'ruRU', 'Расстрельная команда', 'Поговорите с командиром Шнотцем.', 'Комм цу мир, |3-6($r).$B$BХотеть кой-что показайт.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'zhCN', '执行队', '同元首司克诺兹交谈。', '跟我出去，$r。$b$b我有样东西要给你看。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28267, 'zhTW', '行刑隊', '和指揮官舒諾茲說話。', '跟我一起回到外頭去，$r。$b$b我想讓你看樣東西。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'deDE', 'Trefft Euch mit Ezul\'aan', 'Sprecht mit Ezul\'aan in der Übungshalle von Sturmwind.', 'Ezul\'aan aus der Übungshalle in Sturmwind hat Euch eine Nachricht geschickt. Er möchte mit Euch sprechen, wenn Ihr das nächste Mal in der Stadt seid. Die Übungshalle befindet sich in Sturmwinds Altstadt.', '', '', '', '', '', 'Sprecht mit Ezul\'aan in der Übungshalle von Sturmwind.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'esES', 'Reunión con Ezul\'aan', 'Habla con Ezul\'aan en la Cámara de Entrenamiento de Ventormenta.', 'Ezul\'aan, de la Cámara de Entrenamiento de Ventormenta, ha enviado un mensaje en el que pregunta por ti. Quiere hablar contigo la próxima vez que pases por la ciudad. La Cámara de Entrenamiento se encuentra en el Casco Antiguo de Ventormenta.', '', '', '', '', '', 'Habla con Ezul\'aan en la Cámara de Entrenamiento de Ventormenta.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'esMX', 'Reúnete con Ezul\'aan', 'Habla con Ezul\'aan en el Salón de Entrenamiento de Ventormenta.', 'Ezul\'aan, del Salón de Entrenamiento de Ventormenta, te ha enviado un mensaje. Quiere hablar contigo la próxima vez que viajes a la ciudad. El Salón de Entrenamiento se encuentra en el Casco Antiguo de Ventormenta.', '', '', '', '', '', 'Habla con Ezul\'aan en el Salón de Entrenamiento de Ventormenta.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'frFR', 'Rencontre avec Ezul’aan', 'Parlez à Ezul’aan à la Salle d’entraînement de Hurlevent.', 'Ezul’aan a envoyé un message à votre intention. Il veut que vous alliez lui parler, la prochaine fois que vous vous rendrez à Hurlevent. Vous le trouverez à la salle d’entraînement de la Vieille ville.', '', '', '', '', '', 'Parlez à Ezul’aan à la salle d’entraînement de Hurlevent.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'itIT', 'Incontro con Ezul\'aan', 'Parla con Ezul\'aan nella Sala di Addestramento a Roccavento.', 'Ezul\'aan dalla Sala di Addestramento a Roccavento ti ha inviato un messaggio. Vuole parlare con te la prossima volta che sei in città. La Sala di Addestramento si trova nella Città Vecchia di Roccavento.', '', '', '', '', '', 'Parla con Ezul\'aan nella Sala di Addestramento a Roccavento.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'koKR', '에줄라안 만나기', '스톰윈드의 훈련장에 있는 에줄라안과 대화해야 합니다.', '스톰윈드 훈련장에 있는 에줄라안 님이 전언을 보냈습니다. 다음에 스톰윈드에 들르게 되면 꼭 만나보고 싶다고 하셨죠. 훈련장은 구 시가지에 있습니다.', '', '', '', '', '', '스톰윈드의 훈련장에 있는 에줄라안과 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'ptBR', 'Encontro com Ezul\'aan', 'Fale com Ezul\'aan no Salão de Treinamento em Ventobravo.', 'Ezul\'aan, do Salão de Treinamento de Ventobravo, enviou uma mensagem para você. Ele pediu para conversar da próxima vez que você estiver na cidade. O Salão de Treinamento fica na Cidade Velha de Ventobravo.', '', '', '', '', '', 'Fale com Ezul\'aan no Salão de Treinamento em Ventobravo.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'ruRU', 'Встреча с Изул\'ааном', 'Поговорите с Изул\'ааном в тренировочном зале Штормграда.', 'Изул\'аан прислал сообщение. Он хочет поговорить с тобой в тренировочном зале Штормграда, когда ты в следующий раз будешь в городе. Тренировочный зал находится в Старом городе.', '', '', '', '', '', 'Поговорите с Изул\'ааном в тренировочном зале Штормграда.', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'zhCN', '与艾祖尔安碰面', '跟暴风城训练大厅的艾祖尔安谈一谈。', '暴风城训练大厅的艾祖尔安捎来口信。他要你下次前往暴风城时去拜访他。训练大厅就在暴风城的旧城区。', '', '', '', '', '', '跟暴风城训练大厅的艾祖尔安谈一谈。', 0);
INSERT INTO `quest_template_locale` VALUES (28268, 'zhTW', '與艾祖蘭會面', '到暴風城的訓練大廳與艾祖蘭交談。', '艾祖蘭從暴風城的訓練大廳捎來訊息給你。在你下一次到暴風城時，他希望跟你談話。訓練大廳位在暴風城的舊城區。', '', '', '', '', '', '到暴風城的訓練大廳與艾祖蘭交談。', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'deDE', 'Trefft mich in Vir\'sar', 'Sprecht mit Harrison Jones in der Oase Vir\'sar.', 'Hört mir gut zu, $n.$B$BIch wusste, dass die Obelisken etwas mit der Lade der Verheißung zu tun haben, aber das ist noch nicht alles.$B$BEs scheint, dass es mit der Lade etwas sehr Wichtiges auf sich hat - etwas, das die Schöpfer die Hallen des Ursprungs nennen.$B$BNun, da wir wissen, dass dieser Schnottz und seine Truppe mit Todesschwinge im Bunde sind, ist es nur noch dringlicher, dass wir die Lade zuerst bekommen.$B$BIch werde nördlich von hier in der Oase Vir\'sar auf Euch warten.$B$BBeeilt Euch! Sie haben bereits einen Vorsprung!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'esES', 'Encuéntrate conmigo en Vir\'sar', 'Habla con Harrison Jones en el Oasis de Vir\'sar.', 'Escucha esto, $n.$b$bSabía que estos obeliscos estaban ligados al Arca de la Promesa, pero aún hay más.$b$bParece haber algo muy importante respecto al arca: algo relacionado con un lugar que los Creadores llamaron las Cámaras de los Orígenes.$b$bAhora que sabemos que Schnottz y su cuadrilla están aliados con Alamuerte, es mucho más urgente que lleguemos allí antes que ellos.$b$bTe esperaré al norte de aquí, en el Oasis de Vir\'sar$b$b¡Date prisa! ¡Nos llevan ventaja!|n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'esMX', 'Encuéntrate conmigo en Vir\'sar', 'Habla con Harrison Jones en el Oasis de Vir\'sar.', 'Escucha esto, $n.$b$bSabía que estos obeliscos estaban ligados al Arca de la promesa, pero aún hay más.$b$bParece haber algo muy importante respecto al arca: algo relacionado con un lugar que los Creadores llamaron las Cámaras de los Orígenes.$b$bAhora que sabemos que Schnottz y su cuadrilla están aliados con Alamuerte, es mucho más urgente que lleguemos allí antes que ellos.$b$bTe esperaré al norte de aquí, en el Oasis de Vir\'sar$b$b¡Date prisa! ¡Nos llevan ventaja!|n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'frFR', 'Rendez-vous à Vir\'sar', 'Harrison Jones veut que vous le retrouviez à l\'Oasis de Vir\'sar.', 'Écoutez un peu ça, $n.$B$BJe savais que ces obélisques étaient liés au coffre des merveilles, mais il y a plus que ça.$B$BIl y a quelque chose de très important au sujet du coffre... Quelque chose se rapportant à un endroit que les faiseurs appellent les salles de l\'Origine.$B$BMaintenant que nous savons que ce Schnottz et son équipe sont de mèche avec Aile de mort, il est d\'autant plus urgent que nous y arrivions les premiers.$B$BJe vous attendrai au nord d\'ici, à l\'oasis de Vir\'sar.$B$BMais dépêchez-vous ! Ils ont une longueur d\'avance sur nous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'itIT', 'Incontriamoci a Vir\'sar', 'Parla con Harrison Jones all\'Oasi di Vir\'sar.', 'Ascoltami, $n.$b$bHo scoperto che non solo gli obelischi sono collegati all\'Arca della Promessa, ma c\'è di più.$b$bSembra che l\'arca sia legata a un luogo importantissimo chiamato dai Creatori le Sale della Creazione.$b$bOra che sappiamo che Schnottz e i suoi sono in combutta con Alamorte, urge ancora di più arrivare all\'Arca prima di loro.$b$bTi aspetterò all\'Oasi di Vir\'sar, che si trova a nord di qui.$b$bCerca di sbrigarti, perché sono in vantaggio su di noi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'koKR', '비르사르에서 만납시다', '비르사르 오아시스에서 해리슨 존스와 대화해야 합니다.', '자, 들어 보시오, $n.$b$b이 방첨탑은 약속의 성궤와 관련되어 있소. 하지만 그게 다가 아니오.$b$b약속의 성궤에는 아주 중요한 비밀이 숨겨져 있소. 창조주들이 시초의 전당이라고 불렀던 장소와 관련된 어떤 비밀 말이오.$b$b이제 슈노츠와 병사들이 데스윙과 한패라는 사실을 알았으니, 더더욱 우리가 그 상자를 먼저 차지해야 하오.$b$b난 북쪽의 비르사르 오아시스에서 기다리겠소. 서두르시오! 그들이 우리보다 먼저 출발했으니, 어서 빨리 따라잡아야 하오!\n', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'ptBR', 'Encontre-me em Vir\'sar', 'Fale com Harrison Jones no Oásis de Vir\'sar.', 'Escute, $n.$B$BEu sabia que esses obeliscos estavam conectados ao Baú da Promessa, mas o buraco é mais embaixo.$b$bParece haver alguma coisa importante a respeito desse baú – algo relativo a um lugar que os criadores chamavam de Salões Primordiais.$b$bAgora que sabemos que esse tal de Schnottz e sua tripulação estão de conluio com Asa da Morte, precisamos chegar lá antes deles.$b$bEsperarei a norte daqui, no Oásis de Vir\'sar.$b$BRápido! Eles estão à nossa frente!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'ruRU', 'Встретимся в Вир\'саре', 'Поговорите с Харрисоном Джонсом в Вир\'саре.', 'Так, слушай, $n.$B$BЯ знал, что эти обелиски соединены с саркофагом Предвестья, но оказывается, это еще не все.$B$BСаркофаг – и это очень важно – каким-то образом связан с местом под названием Чертоги Созидания.$B$BТеперь, когда нам известно, что этот Шнотц в сговоре со Смертокрылом, мы никак не можем допустить, чтобы он нас опередил.$B$BЯ буду ждать тебя к северу отсюда, в оазисе Вир\'сара.$B$BТоропись, в этой гонке они уже ведут!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'zhCN', '去维尔萨尔找我', '哈里森·琼斯要你去维尔萨尔绿洲找他。', '那么，听好了，$n。$b$b我知道这些方尖石碑同应许约柜有联系，但还不止这些。$b$b这些柜子似乎有某种重要之处——一个关于造物主称之为起源大厅的地方。$b$b现在我们知道这个司克诺兹和他的人同死亡之翼勾搭上了，这件事迫在眉睫，我们得先处理。$b$b我会到北边的维尔萨尔绿洲去等你，要赶快！他们已经赶在我们的前面了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28269, 'zhTW', '到韋爾薩爾綠洲見我', '到韋爾薩爾綠洲和哈里遜‧瓊斯談話。', '來，聽好，$n。$b$b我知道這些尖塔與應許法櫃有關聯，不過事實不只如此。$b$b這個應許法櫃似乎極為重要，它與一個被造物主稱為起源大廳的地方密切相關。$b$b既然舒諾茲這傢伙和死亡之翼是一伙的，我們勢必得先他們一步到那個地方去。$b$b我會在北邊的韋爾薩爾綠洲等你，快點!他們已經領先了!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'esES', '¡Escopeta de montar!', 'Acompaña a Harrison al Oasis de Vir\'sar.', 'De acuerdo, $n, estoy a punto de averiguar cómo activar el obelisco final.$b$bSolo me falta un poco de información, y con un poco de suerte, la encontraremos en el yacimiento.$b$bLos suministros están empaquetados. Avísame cuando estés $glisto:lista;.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'frFR', 'En voiture, Simone !', 'Accompagnez Harrison jusqu\'à l\'Oasis de Vir\'sar.', 'Bien, $n, je suis sur le point d\'apprendre comment activer le dernier obélisque.$B$BSeulement quelques informations manquent et avec un peu de chance, nous serons en mesure de les trouver sur le site.$B$BL\'équipement est empaqueté. Dites-moi lorsque vous serez $gprêt:prête; à partir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'koKR', '총알차를 타고!', '해리슨과 함께 비르사르 오아시스로 가야 합니다.', '좋소, $n. 마지막 방첨탑을 어떻게 작동시키는지, 이제 대충 알 것 같소.$b$b사소한 몇 가지 정보가 빠졌는데, 운이 나쁘지 않다면 현장에서 알아낼 수 있을 거요.$b$b내가 짐은 모두 챙겨 두었으니, 출발할 준비가 되면 내게 다시 알려 주시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'ptBR', 'Copiloto!', 'Acompanhe Harrison até o Oásis de Vir\'sar.', 'Tudo bem, $n, estou muito perto de aprender como ativar o último obelisco.$b$bFaltam apenas algumas poucas informações e, com um pouco de sorte, conseguiremos encontrá-las no local.$b$bOs suprimentos estão todos empacotados. Avise-me quando você estiver pront$go:a; para ir.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'ruRU', 'Верхом на ракете!', 'Сопроводите Харрисона к оазису Вир\'сара.', 'Значит, так, $n. Я уже близок к тому, чтобы понять, как активировать последний обелиск.$B$BНужна дополнительная информация – и если нам повезет, мы ее раздобудем на месте.$B$BУ меня все готово. Мы можем выйти в любую минуту. Да хоть прямо сейчас!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'zhCN', '驾驶猎枪！', '与哈里森一起前往维尔萨尔绿洲。', '好吧，$n，我就快要搞清楚如何激活这最后一座方尖碑了。$b$b只缺最后一点信息，如果我们走运的话，应该可以在现场找到它们。$b$b补给都已经准备就绪。等你准备好了就告诉我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28270, 'zhTW', '駕馭獵槍！', '陪伴哈里遜前往韋爾薩爾綠洲。', '好的，$n，我快知道怎麼啟動最後的方尖碑了。$b$b只缺少一些片段資訊，運氣好的話，我們就能在這裡找到。$b$b補給品都打包好了。你準備好出發時就告訴我。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'deDE', 'Produktivitätseinbußen', 'Tötet 15 Ausgräber von Schnottz oder Aufseher von Schnottz.', 'Also, $n. So viel konnte ich herausfinden.$B$BAn einem Ort namens Hallen des Ursprungs gibt es ein Gerät der Titanen. Es soll eine Art Notfallschalter sein, der alles Leben in Azeroth zurücksetzt. Wenn er aktiviert wird, wird jedes Lebewesen auf der ganzen Welt vernichtet!$B$BNatürlich ist es nur für den absoluten Notfall gedacht, aber wenn es Todesschwinge gelingt, die Titanenhüter zu verderben, kann er den Inhalt der Lade benutzen, um das Gerät auszulösen.$B$BGeht zum Obelisken des Mondes, $n. Haltet sie auf!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'esES', 'Productividad reducida', 'Mata a 15 excavadores de Schnottz o sobrestantes de Schnottz.', 'Vale, $n, esto es lo que he descubierto.$b$bHay un pequeño dispositivo titánico en las profundidades de un lugar llamado las Cámaras de los Orígenes. Se trata de un interruptor de regeneración de toda la vida existente en Azeroth. ¡Si se activa, destruirá a todo ser viviente del mundo!$b$bSe supone que solo debe usarse en condiciones extremas, pero si Alamuerte consigue corromper a los vigías de los titanes, podría utilizar el contenido del arca para activar el dispositivo.$b$bDirígete al Obelisco de la Luna, $n. ¡Frena su avance!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'esMX', 'Productividad reducida', 'Mata a 15 excavadores de Schnottz o sobrestantes de Schnottz.', 'Vale, $n, esto es lo que he descubierto.$b$bHay un pequeño dispositivo titánico en las profundidades de un lugar llamado las Cámaras de los Orígenes. Se trata de un interruptor de regeneración de toda la vida existente en Azeroth. ¡Si se activa, destruirá a todo ser viviente del mundo!$b$bSe supone que solo debe usarse en condiciones extremas, pero si Alamuerte consigue corromper a los vigías titánicos, podría utilizar el contenido del arca para activar el dispositivo.$b$bDirígete al Obelisco de la Luna, $n. ¡Frena su avance!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'frFR', 'Une productivité réduite', 'Tuez 15 Excavateurs de Schnottz ou Surveillants de Schnottz.', 'Très bien, $n, voilà ce que j\'ai appris.$B$BIl y a un appareil des titans au cœur d\'un lieu appelé les salles de l\'Origine. C\'est censé être un interrupteur de réinitialisation d\'urgence pour toute forme de vie sur Azeroth. S\'il est activé, il détruira toute forme de vie dans le monde !$B$BÀ présent, on est censé l\'utiliser que sous certaines conditions extrêmes, mais Aile de mort est capable de corrompre les gardiens des Titans et pourrait utiliser le contenu du coffre pour enclencher l\'appareil.$B$BAllez à l\'Obélisque de la Lune, $n. Ralentissez-les !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'itIT', 'Produttività in calo', 'Uccidi 15 Scavatori Schnottz o Sovrintendenti di Schnottz.', 'Bene, $n. Ecco quello che ho appreso.$b$bNelle profondità di un luogo chiamato Sale della Creazione è sepolto un dispositivo dei Titani concepito per azzerare la vita su Azeroth. Attivandolo, verrebbero distrutte tutte le forme di vita del pianeta!$b$bOvviamente è pensato per essere usato solo in situazioni di estrema necessità, ma nel caso Alamorte riuscisse a corrompere le Sentinelle Titaniche, potrebbe usare il contenuto dell\'Arca per attivarlo.$b$b$n, vai all\'Obelisco della Luna e rallenta i loro piani!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'koKR', '생산성 감소', '슈노츠 발굴인부 또는 슈노츠 감시자 15명을 처치해야 합니다.', '좋소, $n. 내가 알아낸 정보는 이렇소.$b$b시초의 전당이라는 장소 깊은 곳에 티탄의 장치가 있소. 아제로스의 모든 생명체를 최초의 상태로 되돌리는 비상 단추라고 하더군. 이 장치를 가동시키면, 이 세상의 모든 생명체가 사라지고 말 것이오!$b$b물론, 이 장치는 아주 극단적인 상황에서만 사용되는 것이긴 하지만, 데스윙이 티탄의 감시자들을 타락시킬 수 있다면, 상자 안의 내용물을 이용하여 이 장치를 활성화시킬 수도 있을 것이오.$b$b어서 달의 방첨탑으로 가시오. 놈들을 막아야 하오!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'ptBR', 'Produtividade reduzida', 'Mate 15 Escavadores de Schnottz ou Feitores de Schnottz.', 'Tudo bem, $n, aqui vai o que aprendi.$b$bHá um dispositivo titânico incrustado em um lugar chamado Salões Primordiais. É feito para funcionar como uma reinicialização emergencial de toda a vida em Azeroth. Se for ativado, destruirá tudo que vive neste mundo!$b$bEle foi feito para ser usado em determinadas condições extremas, mas, se o Asa da Morte conseguir corromper os vigias titânicos, ele poderá usar o conteúdo do baú para ativar o dispositivo.$b$bVá ao Obelisco da Lua, $n. Atrase o serviço deles!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'ruRU', 'Сниженная продуктивность', 'Убейте 15 землечерпов или надзирателей Шнотца.', 'Слушай, $n. Вот что мне удалось узнать.$B$BЕсть такое место – Чертоги Созидания, и там стоит древнее устройство титанов. Своего рода кнопка экстренной жизне-перезагрузки для Азерота. Если его активировать, все живое в мире погибнет!$B$BЕго строили на крайний случай, но если Смертокрыл способен вывести из строя стражей титанов, он наверняка разберется, как воспользоваться содержимым саркофага, чтобы запустить это устройство.$B$BОтправляйся к Обелиску Луны, $n. И задержи их любой ценой!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'zhCN', '降低产量', '杀死15名司克诺兹挖掘者或司克诺兹监督者。', '好了，$n，以下是我了解到的情况。$b$b在一个名叫起源大厅的地方的深处，有一台泰坦造的装置。它的用途是在紧急情况下重置艾泽拉斯的所有生命。如果它被激活的话，就会摧毁这个世界上的所有活物！$b$b理论上，它只能在特定的极端情况下使用，但是如果死亡之翼可以腐蚀泰坦的监护者，就可以用箱子中的东西激活那台装置。$b$b到月亮方尖碑去，$n。阻止他们！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28271, 'zhTW', '生產力降低', '殺死15個舒諾茲挖掘者或舒諾茲監督者。', '好的，$n，我解讀到的是這樣。$b$b有一種泰坦裝置藏在一個叫起源大廳的地方深處。那是艾澤拉斯一切生命的緊急重置開關。要是啟動了，它就會摧毀世界上所有的生物!$b$b現在，這只能在某種非常情態下使用，但要是死亡之翼腐化了泰坦看守者，他就能用箱子裡的東西啟動裝置。$b$b到月亮方尖碑去，$n。拖延他們的速度!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'deDE', 'Fehlende Stücke', 'Sammelt 7 uralte Kupferschriftrollen.', 'Mir fehlen nur noch ein paar spezielle Informationen.$B$BSobald diese Lücken geschlossen sind, werden wir bereit sein, den letzten Obelisken zu aktivieren. Dann beginnt der Wettlauf um die Lade der Verheißung gegen Schnottz und seine Handlanger.$B$BDie Narren graben diesen Ort so schnell sie können um. Sie richten mehr Schaden an, als sie finden.$B$BGeht zum Obelisken und durchsucht ihre Beute, $n.$B$BSchaut, ob Ihr unsere fehlenden Puzzleteile finden könnt.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'esES', 'Las piezas que faltan', 'Reúne 7 pergaminos de cobre antiguo.', 'Solo me faltan unas piezas del rompecabezas.$b$bUna vez que las consigamos, podremos activar el obelisco final. Entonces la cosa se convertirá en una carrera contra Schnottz y sus compinches para localizar el Arca de la Promesa.$b$bEsos estúpidos están excavando este lugar a toda velocidad. ¡Corren más que saquean!$b$bVe al obelisco y busca entre su botín, $n.$b$bMira a ver si encuentras las piezas que nos faltan.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'esMX', 'Las piezas que faltan', 'Reúne 7 pergaminos de cobre antiguo.', 'Solo me faltan unas piezas del rompecabezas.$b$bUna vez que las consigamos, podremos activar el obelisco final. Entonces la cosa se convertirá en una carrera contra Schnottz y sus compinches para localizar el Arca de la promesa.$b$bEsos estúpidos están excavando este lugar a toda velocidad. ¡Corren más que saquean!$b$bVe al obelisco y busca entre su botín, $n.$b$bMira a ver si encuentras las piezas que nos faltan.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'frFR', 'Éléments d\'information manquants', 'Récupérez 7 Parchemins antiques en cuivre.', 'Il me manque quelques éléments d\'information.$B$BUne fois que ces blancs seront remplis, nous serons prêts à activer le dernier obélisque, ensuite ce sera la course pour découvrir le coffre des merveilles avant Schnottz et ses camarades.$B$BCes imbéciles fouillent ce site aussi vite qu\'ils le peuvent. Ils détruisent plus qu\'ils ne trouvent quoi que ce soit !$B$BAllez à l\'obélisque et farfouillez dans leur butin, $n.$B$BVoyez si vous pouvez trouver les informations qui nous manquent.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'itIT', 'I pezzi mancanti', 'Recupera 7 Antiche Pergamene di Rame.', 'Mi mancano soltanto alcuni brandelli di informazioni.$b$bQuando gli spazi bianchi saranno riempiti, potremo attivare l\'ultimo obelisco. Quindi dovremo provare ad arrivare all\'Arca della Promessa prima di Schnottz e dei suoi scagnozzi.$b$bQuei maledetti stanno scavando come forsennati, distruggendo più di quanto riescano a rubare!$b$b$n, vai all\'obelisco e fruga nel loro bottino.$b$bVedi se riesci a scoprire i pezzi mancanti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'koKR', '잃어버린 조각', '고대 구리 두루마리 7개를 모아야 합니다.', '이제 몇 가지 정보만 알아내면 되오.$b$b그렇게 빠진 정보를 채워 넣으면 마지막 방첨탑을 활성화시킬 수 있을 것이오. 그 후에는 약속의 성궤를 발견할 때까지 슈노츠와 그의 부하들과 본격적인 경주를 펼치게 될 거요.$b$b그 바보들 역시 온 힘을 다해서 여기 이 유적을 파헤치고 있소. 훔쳐내는 것보다 오히려 망쳐놓는 게 더 많지만 말이오!$b$b방첨탑으로 가서 놈들의 발굴품을 간단히 살펴봐 주시오, $n.우리에게 빠진 정보를 찾아낼 수 없을지 알아보시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'ptBR', 'Peças faltantes', 'Colete 7 Pergaminhos de Cobre Antigos.', 'Somente algumas informações específicas que me faltam.$b$bUma vez que essas lacunas estiverem preenchidas, estaremos prontos para reativar o último obelisco. Depois, será uma corrida para descobrirmos o Baú da Promessa antes do Schnottz e seus camaradas.$b$bAqueles tolos estão escavando o mais rápido que podem. Eles estão arruinando mais do que saqueando!$b$bVá ao obelisco e inspecione o butim deles, $n.$b$bVeja se você consegue encontrar as peças que faltam.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'ruRU', 'Недостающие части', 'Соберите 7 древних медных свитков.', 'Я почти все понял, но мне не хватает некоторых данных.$B$BКак только мы соберем всю информацию и заполним пробелы, то активируем последний обелиск, а потом сразу отправимся на поиски саркофага Предвестья, пока его не нашел Шнотц со своими дружками.$B$BЭти убогие недоумки все здесь перерыли. Торопятся, да. Как будто не раскопки ведут, а картошку копают... Больше портят, чем грабят!$B$BИди к обелиску и обыщи их пожитки. Проверь, что они там нахватали, $n.$B$BМожет, как раз и удастся найти недостающие части.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'zhCN', '遗失的拼图', '收集7张古老的镀铜卷轴。', '只剩下最后一点特定的信息没收集到了。$b$b在填上这些空之后，我们就可以激活最后一座方尖碑，然后，形势就会变成一场与司克诺兹一伙人比赛谁先找到应许约柜的竞赛。$b$b那些蠢货在玩命挖掘这一地区。他们毁掉的东西比他们挖出来的还多！$b$b到方尖碑去搜刮一下他们的战利品，$n。$b$b也许你可以找到我们需要的最后几块拼图呢。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28272, 'zhTW', '缺失的線索', '收集7個古老鍍銅卷軸。', '我還缺少幾個特定的線索。$b$b一旦填補了這些空缺，我們就能啟動最後的方尖碑。接著，我們要比舒諾茲那群人更快發現應許法櫃。$b$b那些傻子正加緊趕工挖掘這處遺址。他們保存文物的速度還不及他們破壞的效率!$b$b到方尖碑去搜索他們的戰利品，$n。$b$b看能不能找到我們需要的線索。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'deDE', 'Der Freund eines Freundes', 'Harrison möchte, dass Ihr mit Sullah in der Oase Vir\'sar sprecht.', 'Ich muss auspacken und die Gegend ein wenig auskundschaften. In der Zwischenzeit möchte ich, dass Ihr einen alten Freund von mir trefft.$B$BSullah und ich kennen uns seit Ewigkeiten. Man kann ihm vertrauen, und das ist schon selten heutzutage.$B$BWarum geht Ihr nicht zu ihm und begrüßt ihn?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'esES', 'Un amigo de un amigo', 'Harrison quiere que hables con Sullah en el Oasis de Vir\'sar.', 'Tengo que deshacer la maleta y recorrer un poco la zona. Mientras tanto, quiero que conozcas a un viejo amigo.$b$bSullah y yo nos conocemos desde hace mucho tiempo. Es un tipo en quien se puede confiar; y eso no es nada habitual hoy en día.$b$b¿Por qué no vas y te presentas?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'esMX', 'Un amigo de un amigo', 'Harrison quiere que hables con Sullah en el Oasis de Vir\'sar.', 'Tengo que deshacer la maleta y recorrer un poco la zona. Mientras tanto, quiero que conozcas a un viejo amigo.$b$bSullah y yo nos conocemos desde hace mucho tiempo. Es un tipo en quien se puede confiar; y eso no es nada habitual hoy en día.$b$b¿Por qué no vas y te presentas?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'frFR', 'L\'ami d\'un ami', 'Harrison veut que vous alliez voir Sullah à l\'Oasis de Vir\'sar.', 'Je dois défaire mes bagages et explorer un peu la zone. En attendant, j\'ai un vieil ami que j\'aimerais que vous rencontriez.$B$BSullah et moi nous connaissons depuis des lustres. C\'est un homme de confiance ce qui est une chose bien rare de nos jours.$B$BEt si vous alliez vous présenter à lui ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'itIT', 'L\'amico di un amico', 'Harrison ti ha chiesto di andare a parlare con Sullah all\'Oasi di Vir\'sar.', 'Devo disfare i bagagli e perlustrare un po\' i dintorni. Nel frattempo, vorrei che incontrassi un mio vecchio amico.$b$bLa mia amicizia con Sullah risale a molti anni fa ed è un uomo di cui ci si può fidare: cosa rara di questi tempi.$b$bPerché non vai a conoscerlo?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'koKR', '친구의 친구', '비르사르 오아시스에 있는 술라에게 가야 합니다.', '이제 짐을 풀고 주변을 좀 돌아봐야겠소. 그동안 당신이 내 옛 친구를 한 번 만나줬으면 좋겠소.$b$b술라와 나는 꽤 오래전부터 알던 사이라오. 요새는 정말 찾기 어려운 믿음직스러운 친구지.$b$b가서 한 번 만나 인사나 나누면 어떻겠소?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'ptBR', 'Amigo de um amigo', 'Harrison quer você fale com Sullah no Oásis de Vir\'sar.', 'Preciso desempacotar as minhas coisas e investigar um pouco a área. Nesse meio-tempo, eu gostaria que você conhecesse um velho amigo meu.$b$bO Sullah e eu nos conhecemos há muito tempo. Ele é um homem em que se pode confiar, e isso é bastante raro hoje em dia.$b$bPor que você não vai lá e se apresenta?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'ruRU', 'Друг моего друга', 'По настоянию Харрисона поговорите с Суллой у оазиса Вир\'сара.', 'Мне надо распаковать вещи и посмотреть, что здесь и как. А ты пока познакомься с моим старым приятелем.$B$BСулла – отличный мужик и настоящий друг. Из тех, кому можно доверять. А в наши дни это большая редкость.$B$BНу, давай. Ты иди и знакомься, а я пока займусь делом.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'zhCN', '朋友的朋友', '哈里森要你去跟维尔萨尔绿洲的苏拉赫谈一谈。', '我得打开行李，四下瞧瞧。与此同时，我想让你去见我的一个老朋友。$b$b苏拉赫跟我是老交情了。他是一个值得信赖的人，这种人如今不多了。$b$b你为什么不去找他谈谈呢。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28273, 'zhTW', '朋友的朋友', '哈里遜要你和韋爾薩爾綠洲的蘇拉談話。', '我要解裝看一下這個區域。同時，我有個老朋友希望你見見。$b$b蘇拉和我認識超久。他是個值得信任的傢伙，這種人最近越來越少了。$b$b上前去自我介紹一下。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'deDE', 'Zwei Zelte', 'Begleitet Sullah zu Sullahs Fahrendem Zirkus.', 'Seht Euch an! Das Abenteuer steht Euch förmlich ins Gesicht geschrieben!$B$BIhr müsst Euch aber auch ein wenig erholen. Ihr seht angespannt aus und das macht einen schnell müde.$B$BVertraut mir, mit Erholung kenne ich mich aus!$B$BA propos Erholung: Jonesy hat mir anscheinend die Zelte gebracht, um die ich ihn gebeten habe.$B$BKommt. Reitet mit mir!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'esES', 'Dos tiendas', 'Acompaña a Sullah hasta el Puesto de Sullah.', 'Mírate, $gamigo mío:amiga mía;. ¡Esta gran aventura tuya te tiene $ghecho:hecha; polvo!$b$bTienes que relajarte un poco. Estás demasiado $gtenso:tensa; y así solo vas a conseguir cansarte.$b$bConfía en mí, ¡sé lo que es la relajación!$b$bY hablando de tiendas, veo que el pequeño Jones me trajo las que pedí.$b$bVen, $gamigo:amiga;. ¡Únete a mí!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'esMX', 'Dos tiendas', 'Acompaña a Sullah hasta el Puesto de Sullah.', 'Mírate, $gamigo mío:amiga mía;. ¡Esta gran aventura tuya te tiene $ghecho:hecha; polvo!$b$bTienes que relajarte un poco. Estás demasiado $gtenso:tensa; y así solo vas a conseguir cansarte.$b$bConfía en mí, ¡sé lo que es la relajación!$b$bY hablando de tiendas, veo que el pequeño Jones me trajo las que pedí.$b$bVen, $gamigo:amiga;. ¡Únete a mí!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'frFR', 'Les deux tentes', 'Accompagnez Sullah au Stand de Sullah.', 'Regardez-vous, mon $gami:amie;. Votre grande aventure vous a vraiment $gperturbé:perturbée; !$B$BVous devez vous relaxer et relâcher la pression. Vous êtes trop $gtendu:tendue; et ça ne va faire que vous fatiguer.$B$BFaites-moi confiance, je m\'y connais en relaxation !$B$BEn parlant de tentes, je vois que Jonesy m\'a apporté celles que je lui avais demandées.$B$BVenez, mon $gami:amie;. Montez avec moi !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'itIT', 'Le due tende', 'Accompagna Sullah alla Postazione di Sullah.', 'Guardati, $gamico mio:amica mia;, sei $gteso:tesa; come una corda di violino!$b$bDevi rilassarti un po\' se non vuoi scoppiare come un petardo.$b$bFidati, io sono un maestro del rilassamento!$b$bParlando di tende, ho visto che Jonsy mi ha portato quelle che gli avevo chiesto.$b$bSeguimi, $gamico mio:amica mia;, ti do un passaggio!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'koKR', '두 개의 천막', '술라와 함께 술라의 소공연장으로 가야 합니다.', '아이고, 저런. 당신 앞에 닥친 엄청난 모험 때문에 걱정이 많으시군요!$b$b그럴수록 느긋한 마음으로 긴장을 풀어줘야 합니다. 그렇게 계속 신경을 곤두세우고 있다가는 그냥 지쳐 떨어져 버릴 거라고요.$b$b절 믿으세요. 푹 쉬는 일이라면 제 전문 분야니까요!$b$b말이 나와서 그러는데, 제가 부탁한 천막을 존스 박사가 가져왔더군요.$b$b자요, 저와 함께 가시죠.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'ptBR', 'Duas tendas', 'Acompanhe o Sullah até o Espetáculo de Sullah.', 'Olhe para você, amig$go:a;. Esta sua grande aventura está deixando você dur$go:a;!$b$bVocê precisa relaxar e se soltar. Você está tens$go:a; demais, dá para notar só de olhar.$b$bConfie em mim, eu entendo de relaxamento!$b$bFalando em tendas, vejo que o Jones me trouxe as que pedi.$b$bVamos, amig$go:a;. Venha comigo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'ruRU', 'Два шатра', 'Отправляйтесь вместе с Суллой к цирку Суллы.', 'Ты посмотри на себя, $gдружище:подруга;! Что ж ты $gтакой напряженный:такая напряженная;?! Сплошные великие приключения – это, конечно, неплохо. Жизнь становится интереснее, насыщеннее, да... Но ведь надо когда-то же и отдыхать!$B$BА то если вечно ходить как кол проглотил, эдак очень скоро скопытишься. И будет обидно.$B$BНу, ничего. Ты – в надежных руках. Уж я-то тебя научу правильно отдыхать!$B$BКстати об отдыхе. Смотрю, Джонси привез шатры, которые я просил.$B$BЛадно, $gдружище:дружок;, давай-ка за мной!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'zhCN', '一对帐篷', '陪苏拉赫前往苏拉赫的表演场。', '瞧瞧你，我的朋友。大冒险让你的神经都绷在了一起！$b$b你需要放松放松。你太紧张了，这样会累垮的。$b$b相信我，我对放松很拿手。$b$b说到帐篷，我记得我管琼斯要过一对，他带来了。$b$b来吧，朋友。跟我一起过去！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28274, 'zhTW', '兩頂帳篷', '陪伴蘇拉前往蘇拉的雜耍帳篷。', '看看你，我的朋友。你這場大冒險讓你全身打結了!$b$b你需要休息放鬆。你太緊繃了，這樣會害你累翻。$b$b相信我，我知道該怎麼放鬆!$b$b說到帳篷，看來瓊仔把我跟他要的帳篷送到了。$B$B來吧，朋友，和我一起去!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'deDE', 'Bom-ben-los!', 'Zerstört 10 scheußliche Versorgungsboote.', 'Wellsons Werft empfängt dauernd Vorräte von den Küstengewässern aus und das muss aufhören. Sie haben Kanonen an den Docks aufgestellt, um sich zu verteidigen, aber wenn Ihr zu ihnen durchbrechen könnt, können wir mit ihnen die Versorgungsschiffe zerstören.$B$BBegebt Euch dorthin und seht mal, was Ihr tun könnt. Erstattet mir über Eure Fortschritte Bericht.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'esES', '¡Bombas fuera!', 'Destruye 10 barcos de suministros espantosos.', 'El astillero de Wellson está recibiendo suministros constantemente por mar y tenemos que acabar con eso. Han colocado cañones a lo largo del embarcadero para defenderse, pero si consigues llegar hasta ellos, podremos usarlos para destruir sus barcos de suministros.$B$BDirígete hasta allí y averigua lo que puedes hacer. Infórmame de tus avances.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'esMX', '¡Bombas fuera!', 'Destruye 10 barcos de suministros espantosos.', 'El astillero de Wellson está recibiendo suministros constantemente por mar y tenemos que acabar con eso. Han colocado cañones a lo largo del embarcadero para defenderse, pero si consigues llegar hasta ellos, podremos usarlos para destruir sus barcos de suministros.$B$BDirígete hasta allí y averigua lo que puedes hacer. Infórmame de tus avances.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'frFR', 'Touché coulé !', 'Détruisez 10 Bateaux de ravitaillement de Wellson.', 'Le chantier naval de Wellson reçoit constamment des cargaisons par voie de mer. Il faut absolument y mettre un terme. Ils ont positionné des canons en batterie le long des quais pour se défendre, mais si vous parvenez à vous emparer d’un canon, vous pourrez l’utiliser pour couler leurs bateaux de ravitaillement.$B$BAllez sur place et voyez ce que vous pouvez faire. Revenez me voir quand vous aurez fait le boulot.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'itIT', 'Bombardamento a tappeto', 'Distruggi 10 Barche di Rifornimento di Wellson.', 'Il Cantiere Navale di Wellson riceve di continuo rifornimenti provenienti dal mare aperto: dobbiamo far cessare tutto questo. Hanno piazzato dei cannoni sui moli come difesa, ma se riesci a impadronirtene, potremo usarli per distruggere le loro navi con i rifornimenti.$B$BVai laggiù e vedi cosa riesci a fare. Torna da me per informarmi dei tuoi progressi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'koKR', '폭탄 발사!', '웰슨 보급선 10척을 파괴해야 합니다.', '바다에서 끊임없이 자원을 공급받는 웰슨 조선소에 압박을 가해야겠습니다. 놈들은 방어를 위해 부두에 대포를 설치해두었는데, 대포를 차지할 수만 있다면 놈들의 보급선을 쉽게 파괴할 수 있을 겁니다.$B$B부두로 가서 보급선을 파괴하고 제게 돌아오십시오.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'ptBR', 'Lançar bombas!', 'Destrua 10 Barcos de Suprimentos Medonhos.', 'O Estaleiro Bonfilho sempre recebe carregamentos pelo mar, e precisamos acabar com isso. Há vários canhões defensivos nas docas e, se você conseguir chegar até eles, poderá usá-los para destruir os barcos de suprimentos.$B$BVá lá e veja o que pode fazer. Depois me informe como está o andamento da missão.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'ruRU', 'Пушки к бою!', 'Уничтожьте 10 мрачных суден снабжения.', 'На Верфь Веллсона по морю постоянно поступают припасы, и нужно положить этому конец. Враг расставил пушки по периметру верфи в целях защиты, но мы обернем это против него: воспользуемся пушками, чтобы уничтожить суда снабжения.$B$BОтправляйся туда и посмотри, что можно сделать. Доложи мне о результатах операции.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'zhCN', '炮弹发射！', '摧毁10艘维尔松补给船。', '维尔松船坞不断从海上收到补给品，我们必须切断他们的补给线。他们在码头一带部署了防御火炮，不过如果你能控制火炮的话，我们就可以用炮弹摧毁他们的补给船。$B$B到那边去看看你能做些什么吧。记得向我汇报你的进展。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28275, 'zhTW', '盡情轟炸!', '摧毀10艘恐怖的補給艇。', '維爾森船廠不斷從近海接收補給品，我們必須阻止。他們沿著甲板裝設了加農砲作為防禦，但要是你能接近大砲，我們就可以用大砲來毀掉他們的補給船。$B$B去看看你能做些什麼。回來向我報告你的進度。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'deDE', 'Salhets Geheimnis', 'Findet Salhet am südlichen Ende des Sonnenbehütergrats in Uldum.', 'Hört gut zu, $n. Bevor Ihr Euer Vertrauen in mich verliert, würde ich Euch gerne noch etwas zeigen.$B$BWie Ihr wisst, bin ich kein Kämpfer. Meine Koordination ist nicht gut.$B$BIch sehe mich eher als Taktiker an.$B$BTrefft mich am südlichen Ende des Grats und ich werde Euch zeigen, was ich meine.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'esES', 'El secreto de Salhet', 'Encuéntrate con Salhet en el extremo sur del Monte del Vigía, en Uldum.', 'Escucha, $n. Antes de que te des por $gvencido:vencida; conmigo, quiero enseñarte algo.$B$BVerás, no soy un gran luchador. No tengo muy buena coordinación.$B$BSoy más bien una mente táctica.$B$BEncuéntrate conmigo en el límite sur del monte y te lo enseñaré.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'esMX', 'El secreto de Salhet', 'Encuéntrate con Salhet en el extremo sur del Monte del Vigía, en Uldum.', 'Escucha, $n. Antes de que te des por $gvencido:vencida; conmigo, quiero enseñarte algo.$B$BVerás, no soy un gran luchador. No tengo muy buena coordinación.$B$BSoy más bien una mente táctica.$B$BEncuéntrate conmigo en el límite sur del monte y te lo enseñaré.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'frFR', 'Le secret de Salhet', 'Trouvez Salhet à l\'extrémité sud de la Crête du Guette-soleil en Uldum.', 'Écoutez, $n. Avant que vous n\'arrêtiez de croire en moi, j\'aimerais vous montrer quelque chose.$B$BVous voyez, je n\'ai pas vraiment l\'âme d\'un combattant. Je suis assez maladroit.$B$BJ\'ai plutôt un esprit tactique.$B$BRetrouvez-moi à l\'extrémité sud de la crête et je vous montrerai.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'itIT', 'Il segreto di Salhet', 'Incontra Salhet all\'Estremità meridionale della Cresta di Mirasole nella regione di Uldum.', 'Ascolta, $n. Prima che tu te ne vada, voglio mostrarti qualcosa.$b$bAvrai capito che non sono un grande guerriero. Mi manca la coordinazione.$b$bLa mia è una mente tattica.$b$bIncontriamoci all\'estremità meridionale della cresta e ti mostrerò cosa voglio dire.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'koKR', '살헤트의 비밀', '울둠의 태양감시자 마루 남쪽 끝에 있는 살헤트를 찾아야 합니다.', '제 말 좀 들어 보세요, $n 님. 당신이 절 아예 포기해 버리기 전에 한 가지 보여 드릴 게 있어요.$B$B보시다시피, 제겐 싸움꾼으로서의 재능이 없어요. 솜씨가 별로 좋지 않다고요.$B$B그보다는 전략을 꾸리는 데 더 소질이 있어요.$B$B태양감시자 마루 남쪽 끝에서 다시 만나죠. 제가 뭔가 보여 드리겠습니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'ptBR', 'O segredo de Sal-het', 'Encontre Sal-het no Extremo Sul do Cume de Solarguarda em Uldum.', 'Olha, $n. Antes que desista de mim, há algo que quero lhe mostrar.$B$BVeja bem, não sou muito de luta. Não tenho muita coordenação.$B$BTenho uma mente mais tática.$B$BEncontre-me no extremo sul do cume e lhe mostrarei.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'ruRU', 'Салхетов секрет', 'Разыщите Салхета у южной оконечности Хребта Стражей Солнца в Ульдуме.', 'Послушай, $n. Прежде чем ты махнешь на меня рукой, хочу тебе кое-что показать.$B$BВидишь ли, воин из меня плохой. С координацией беда.$B$BЯ больше по части тактики специалист.$B$BВстретимся у южной оконечности хребта – я тебе продемонстрирую.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'zhCN', '萨赫特的秘密', '去找奥丹姆观日者山岭南端的萨赫特。', '听着，$n。在你对我彻底失望之前，我有样东西要给你看。$B$B你瞧，我算不上是个战士。我协调性不好。$B$B我更擅长的是战术思考。$B$B去山岭南边找我，我演示给你看。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28276, 'zhTW', '薩赫特的秘密', '到奧丹姆的日衛者山脊南端找薩赫特。', '聽著，$n。在你放棄對我的希望之前，我有東西想給你看看。$B$B你知道，我是個不怎麼樣的戰士。我協調性不是很好。$B$B我比較像是一個謀略家。$B$B到南邊的山脊旁邊來跟我碰頭，我會展示給你看。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'deDE', 'Salhet der Taktiker', 'Sprecht mit Salhet und tötet 30 Murrblutaasfresser.', 'Schon als ich ein Junges war, wollte ich unbedingt ein General werden.$B$BIch wollte unbedingt der Armee beitreten. Aber wie Ihr gesehen habt, ist es um meine Kampfkünste schlecht bestellt.$B$BIch wurde schon früher zum Hyänentöten geschickt. Ich wurde schon so oft hierher geschickt, dass ich mich inzwischen mit den Löwen angefreundet und sie trainiert habe, das zu machen, was ich will.$B$BSie sind meine Soldaten, folgen meinen Befehlen und töten massenweise Hyänen. Seht selbst!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'esES', 'Salhet el estratega', 'Habla con Salhet y asesina a 30 carroñeros Gruñido sangriento.', 'Desde que era un cachorro, siempre he querido ser general.$B$BEstaba deseando alistarme en el ejército. Pero como has podido comprobar, mis habilidades de combate no son precisamente brillantes.$B$BEsta no es la primera vez que me mandan a cazar hienas. De hecho, me envían aquí tan a menudo, que me he hecho amigo de estos leones y los he entrenado para que hagan lo que yo les mando.$B$BSon mis soldados, y bajo mi mando matarán a legiones de hienas. ¡Observa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'esMX', 'Salhet el estratega', 'Habla con Salhet y mata a 30 carroñeros Gruñido Sangriento.', 'Desde que era un cachorro, siempre he querido ser general.$B$BEstaba deseando alistarme en el ejército. Pero como has podido comprobar, mis habilidades de combate no son precisamente brillantes.$B$BEsta no es la primera vez que me mandan a cazar hienas. De hecho, me envían aquí tan a menudo, que me he hecho amigo de estos leones y los he entrenado para que hagan lo que yo les mando.$B$BSon mis soldados, y bajo mi mando matarán a legiones de hienas. ¡Observa!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'frFR', 'Salhet le tacticien', 'Parlez à Salhet et tuez 30 Charognards gronde-sang.', 'Depuis tout petit, j\'ai toujours voulu être général.$B$BJ\'avais envie de rejoindre l\'armée. Mais comme vous avez pu le constater, mes compétences de combat me font défaut.$B$BCe n\'est pas la première fois que l\'on m\'envoie tuer des hyènes. En fait, on m\'y envoie si souvent que je suis devenu ami avec ces lions et que je les ai entraînés à m\'obéir.$B$BCe sont mes soldats et à ma demande, ils tuent des légions de hyènes. Regardez !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'itIT', 'Salhet il tattico', 'Parla con Salhet e uccidi 30 Saprofagi Ringhiorosso.', 'Sin da quando ero un cucciolo, ho sempre sognato di diventare Generale.$b$bFui fiero di unirmi all\'esercito, ma, come hai potuto verificare tu $gstesso:stessa;, non sono abilissimo in combattimento.$b$bNon è la prima volta che sono stato incaricato di uccidere le iene. A dirla tutta, sono stato inviato qui così tante volte che mi sono fatto amici e ho addomesticato quei leoni.$b$bOra sono i miei soldati e basta un mio ordine per fargli attaccare le iene. Stai a guardare!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'koKR', '전술가 살헤트', '살헤트와 대화하고 피으르렁 청소부 30마리를 처치해야 합니다.', '저는 어린 시절부터 계속 장군이 되기를 꿈꿔왔습니다.$B$B그래서 군에 정말 입대하고 싶었지만, 보시다시피 전투 기술이 그다지 뛰어난 편이 아니었어요.$B$B제게 하이에나를 처치하라는 명령이 떨어진 건 이번이 처음이 아닙니다. 사실 여기 얼마나 자주 왔던지, 이 사자들과 친해질 정도였고, 결국 제 지시를 알아듣게 훈련까지 시켰더랬죠.$B$B이 사자는 제 명령을 따르는 병사들이나 마찬가지라고요. 이들이 하이에나 무리를 처치해 줄 거예요. 보세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'ptBR', 'Sal-het, o Tático', 'Fale com Sal-het e mate 30 Catadores Rosnassangue.', 'Desde que eu era pequenininho, sempre quis ser um general.$B$BAnsiava por entrar no exército. Mas, como pode ver, minhas habilidades para combate não são as melhores.$B$BNão é a primeira vez que sou enviado para matar hienas. De fato, já me mandaram tantas vezes que fiz amizade com estes leões e os treinei para executarem minha tarefa.$B$BEles são meus soldados e, sob meu comando, matarão muitas hienas. Observe!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'ruRU', 'Салхет-тактик', 'Поговорите с Салхетом и убейте 30 кровавых падальщиков.', 'Я с младых лет мечтал стать генералом.$B$BИ с готовностью записался в армию. Но, как ты $gмог:могла; заметить, мои боевые навыки оставляют желать много лучшего.$B$BМеня уже не первый раз посылают убивать гиен. На самом деле мне приходится бывать тут так часто, что я уже успел подружиться со здешними львами и научил их выполнять за меня всю работу.$B$BЭто мои солдаты, и по моей команде они готовы убивать гиен целыми стаями. Смотри!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'zhCN', '战术家萨赫特', '跟萨赫特谈一谈，杀掉30头血吠食腐者。', '我还是童子军的时候，就想成为一名将军。$B$B我渴望加入军队。但你也看见了，我的战斗技巧实在是不怎么样。$B$B这不是我第一次被派去杀土狼了。事实上，我经常被派到这里来，逐渐和这些狮子成了好朋友，训练它们帮我完成任务。$B$B它们是我的士兵，我会命令它们去消灭土狼军团。看好！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28277, 'zhTW', '『謀士』薩赫特', '與薩赫特交談，並殺死30隻血咆食腐者。', '從我還是個小毛頭的時候，我就一直夢想成為一個將軍。$B$B我非常渴望能加入軍隊。但如你所見，我的格鬥技術並不是很到家。$B$B這不是我第一次被派來殺土狼。事實上，我經常被派到這裡來，這讓我跟這些獅子變得很親近，而且訓練牠們去執行我的命令。$B$B牠們是我的士兵，在我的指揮下，牠們會殺死一大票的土狼。仔細看著!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'deDE', 'Ich bin das Gesetz und dessen starker Arm', 'Benutzt den Disziplinarknüppel des Schwarzfels bei 20 Einheiten in den Feldern der Ehre. Wenn jemand merkt, wer Ihr seid, so erschlagt ihn, bevor er Eure Maskierung aufheben kann.', 'Ich war auf der Suche nach $geinem starken Soldaten:einer starken Soldatin; wie Euch. Ich brauche jemanden, der dafür sorgt, dass meine Truppen sich benehmen.$B$BAußerhalb der Festung bereiten sich die Orcs, Oger und Trolle auf einen Angriff auf das Rotkammgebirge vor. Meine Unteroffiziere halten sie zwar auf Trab, aber sie brauchen Hilfe.$B$BNehmt diesen Knüppel und diszipliniert ein paar meiner Soldaten.$B$BEs ist mir egal, ob sie in Reih und Glied stehen oder sich fehlverhalten - ich will nur, dass sie eine Tracht Prügel bekommen. Ich will, dass sie sich konzentrieren, auf die Schlacht vorbereitet und abgehärtet sind, und gefolgsam.$B$BGeht nun.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'esES', 'Yo soy la ley y yo soy el látigo', 'Usa la cayada de disciplina Roca Negra con 20 soldados de asalto en los Campos de Honor. Si uno de los soldados descubre tu disfraz, mátalo a golpes antes de que pueda desvelar tu identidad.', 'Llevo tiempo buscando a $gun buen:una buena; soldado fuerte como tú. Necesito a alguien que mantenga a raya a mis tropas.$b$bEn el exterior del bastión encontrarás orcos, ogros y trols que se preparan para atacar Crestagrana. Mis sargentos los mantienen a raya, pero me gustaría que los ayudases.$b$bCoge esta cayada y disciplina a algunos de mis soldados. Me da igual si se estaban comportando mal o si estaban perfectamente formados; quiero que los golpees. Quiero que se concentren, que se curtan en la batalla y que sean obedientes.$b$bY ahora, vete.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'esMX', 'Yo soy la ley y yo soy el látigo', 'Usa la cayada de disciplina Roca Negra con 20 soldados de asalto en los Campos de Honor. Si uno de los soldados descubre tu disfraz, mátalo a golpes antes de que pueda desvelar tu identidad.', 'Llevo tiempo buscando a $gun buen:una buena; soldado fuerte como tú. Necesito a alguien que mantenga a raya a mis tropas.$b$bEn el exterior del bastión encontrarás orcos, ogros y trols que se preparan para atacar Crestagrana. Mis sargentos los mantienen a raya, pero me gustaría que los ayudases.$b$bCoge esta cayada y disciplina a algunos de mis soldados. Me da igual si se estaban comportando mal o si estaban perfectamente formados; quiero que los golpees. Quiero que se concentren, que se curtan en la batalla y que sean obedientes.$b$bY ahora, vete.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'frFR', 'La loi, c’est moi (et le fouet aussi)', 'Utilisez le Casse-tête de discipline rochenoire sur 20 soldats aux Champs d’Honneur. Si l’un des soldats remarque votre déguisement, tabassez-le à mort avant qu’il ne puisse vous démasquer.', 'Je recherche justement un soldat costaud comme vous pour maintenir la discipline parmi mes troupes.$b$bDevant le bastion, vous trouverez des Orcs, des Ogres et des Trolls en pleins préparatifs de l’assaut sur les Carmines. Mes sergents s’occupent déjà de les diriger, mais ils ont besoin d’un coup de main.$b$bPrenez ce casse-tête et corrigez quelques-uns de mes soldats. Peu importe qu’ils soient désobéissants ou pas, mettez-leur une bonne raclée. Je veux qu’ils restent concentrés, qu’ils soient aguerris et qu’ils respectent les ordres.$b$bDébarrassez-moi le plancher, maintenant.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'itIT', 'Ammazza che mazza', 'Usa la Mazza Ferrata Punitiva dei Roccianera su 20 soldati presenti nei Campi dell\'Onore. Se uno dei soldati scopre il tuo travestimento, uccidilo prima che possa smascherarti.', 'Cercavo giusto un soldato forte e abile come te. Mi serve qualcuno che metta in riga le mie truppe.$b$bAll\'esterno della fortezza troverai Orchi, Ogre e Troll che preparano l\'assalto a Crestarossa. Sono tenuti in riga dai miei sergenti, ma vorrei che dessi loro una mano.$b$bPrendi questa mazza ferrata e punisci alcuni dei miei soldati. Non importa se a torto o a ragione, l\'importante è che li punisci. Li voglio concentrati, tosti e obbedienti.$b$bOra va\'.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'koKR', '나는 법이고 나는 채찍이다', '명예의 들판에 있는 20명의 병사에게 검은바위 규율의 곤봉을 사용해야 합니다. 병사 중 누군가 당신의 변장을 발견하면, 당신의 정체가 발각되기 전에 그를 곤봉으로 처치하십시오.', '난 자네와 같이 괜찮고 강한 병사를 찾고 있었네. 내 군대에 규칙을 준수하게 할 누군가가 필요하다네.$b$b요새 밖에서 붉은마루 공격을 준비하는 오크, 오우거, 트롤들을 찾을 수 있을 걸세. 내 하사관들이 그들에게 규칙을 준수하게 하고 있네만, 자네가 그들을 지원해줬으면 하네.$b$b곤봉을 가져가서 내 병사 몇 명에게 규율이 무엇인지 가르쳐주게. 병사들이 못된 짓을 하든 완벽하게 일렬로 서 있든 상관없네. 그저 녀석들을 두들겨 패고 싶을 뿐이야. 녀석들이 정신 바짝 차리고, 실전에 단련되고 복종하길 바라네.$b$b가 보게.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'ptBR', 'Eu sou a lei e o açoite', 'Use a Borduna da Disciplina de Rocha Negra em 20 soldados nos Campos de Honra. Se um deles descobrir o seu disfarce, bata nele com a borduna até a morte, antes que ele possa desmascarar você.', 'Eu estava procurando $gum:uma; soldado $gbom:boa; e forte como você. Preciso de alguém que mantenha minhas tropas na linha.$B$BFora da fortaleza, você encontrará orcs, ogros e trolls se preparando para o ataque a Cristarrubra. Meus sargentos os mantém na linha, mas eu gostaria que você os ajudasse.$B$BLeve esta clava e discipline alguns dos meus soldados. Estejam eles se comportando mal ou perfeitamente alinhados, quero que apanhem. Quero que fiquem concentrados, prontos para a batalha e obedientes.$B$BAgora, vá.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'ruRU', 'Я – закон и наказание', 'Примените дисциплинирующую дубинку Черной горы на 20 воинах на Полях Славы. Если какой-то солдат раскусит, что вы не орк, забейте его до смерти.', 'Давно искал такого хорошего и сильного солдата как ты. Мне нужен кто-то для поддержания дисциплины в армии.$b$bПеред крепостью находятся орки, огры и тролли, готовящиеся к нападению на Красногорье. За их дисциплину отвечают мои сержанты, но и твоя помощь не помешает.$b$bВозьми-ка эту дубину и проучи парочку моих солдат. Мне безразлично, отлынивают ли они от службы или стоят по струнке. Просто побей их. Хочу, чтобы они были твердыми, исполнительными и готовыми к бою.$b$bЗа дело!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'zhCN', '我就是法律，我就是鞭子', '对荣耀之地的20名军人使用黑石训诫棒。如果某位军人识破了你的伪装，你必须在他揭发你之前杀死他。', '我一直在找像你这样优秀而强壮的士兵。我需要有人维持军队的秩序。$b$b兽人、食人魔和巨魔大军正在要塞外操练，准备进攻赤脊山。我的中士们负责监督他们，但是我希望你能参与协助。$b$b带上这根棍棒，用它教训那些不听话的士兵。我才不关心他们有没有犯错或是队伍没站整齐呢，我就是要让他们挨几下打。我要他们集中精神，训练有素并且绝对服从命令。$b$b去吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28278, 'zhTW', '軍紀如鐵', '在榮譽原野的20名士兵身上使用黑石戒律鬥棍。如果其中一名士兵發現了你的偽裝，在他有機會揭發你之前將他毆打至死。', '我一直在找像你這樣優秀、堅強的士兵。我需要有人幫我維持部隊的秩序。$b$b在堡壘外面，獸人、巨魔和食人妖們準備著對赤脊山的襲擊。我的士官正在負責管理，但我希望你能去協助他們。$b$b帶著這根棍子，整頓一下我的士兵。我不在乎他們是破壞規矩了還是乖乖站在隊伍裡，我要他們挨揍。我要他們精神專注、經得起磨練，而且服從。$b$b去吧。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'deDE', 'Machtmissbrauch', 'Tötet 5 Feldwebel des Schwarzfels mit dem Disziplinarknüppel des Schwarzfels. Besiegt sie schnell, bevor sie Eure Maskierung aufheben können.', '$n? Trottmann hier.$B$BWie es scheint, habt Ihr Thorg\'izogs Vertrauen gewonnen, denn er hat Euch für die Disziplinierung eingeteilt. Das ist ein Glück.$B$BWir müssen diese Feldwebel loswerden. Ihr findet sie außerhalb der Schwarzfelsfestung in den Feldern der Ehre auf Wölfen reitend. Benutzt den Knüppel so oft wie nötig, bis sie sterben. Ihr müsst schnell handeln, denn wenn sie merken, dass Ihr $gkeiner:keine; von ihnen seid, dann werden sie versuchen, Eure Maskierung aufzuheben. Seid schnell und zügellos, $n.$B$BTrottmann aus.', '', 'Ich hoffe nur, dass diese Verkleidung hält...', 'Oberst Trottmann', 'Hahaha! Erstklassig!', 'Oberst Trottmann', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'esES', 'Abuso de poder', 'Mata a 5 sargentos Roca Negra con la cayada de disciplina Roca Negra. Derrótalos rápidamente; antes de que puedan desenmascararte.', '¿$n? Aquí Troteman.$b$bParece que te has ganado la confianza de Thorg\'izog; y que te ha asignado una tarea de disciplina. Eso está muy bien.$b$bNecesitamos que acabes con sus sargentos. Los encontrarás a lomos de sus lobos, en el exterior del Bastión de Roca Negra en los Campos de Honor. Usa la cayada con ellos, varias veces, hasta que mueran. Tendrás que actuar con rapidez porque si se dan cuenta de que no eres uno de ellos, podrían desenmascararte. Golpea rápida y ferozmente, $n.$b$bTroteman, corto y cierro.', '', 'Espero que ese disfraz aguante...', 'Coronel Troteman', '¡Ja, ja, ja! ¡Buen trabajo!', 'Coronel Troteman', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'esMX', 'Abuso de poder', 'Mata a 5 sargentos Roca Negra con la cayada de disciplina Roca Negra. Derrótalos rápidamente; antes de que puedan desenmascararte.', '¿$n? Aquí Troteman.$b$bParece que te has ganado la confianza de Thorg\'izog; y que te ha asignado una tarea de disciplina. Eso está muy bien.$b$bNecesitamos que acabes con sus sargentos. Los encontrarás a lomos de sus lobos, en el exterior del Bastión de Roca Negra en los Campos de Honor. Usa la cayada con ellos, varias veces, hasta que mueran. Tendrás que actuar con rapidez porque si se dan cuenta de que no eres uno de ellos, podrían desenmascararte. Golpea rápida y ferozmente, $n.$b$bTroteman, corto y cierro.', '', 'Espero que ese disfraz aguante...', 'Coronel Troteman', '¡Ja, ja, ja! ¡Buen trabajo!', 'Coronel Troteman', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'frFR', 'Abus de pouvoir', 'Tuez 5 Sergents rochenoires en vous servant du Casse-tête de discipline rochenoire. Éliminez-les rapidement avant qu’ils n’aient une chance de vous démasquer.', '$n ? Ici Troteman.$b$bIl semblerait que vous ayez gagné la confiance de Thorg’izog puisqu’il vous a $gassigné:assignée; à la discipline de son armée. C’est une chance pour nous.$b$bIl faut en profiter pour éliminer ses sergents. Ils circulent à dos de loup à l’extérieur du bastion Rochenoire, dans les champs d’Honneur. Utilisez ce casse-tête sur eux, à plusieurs reprises, jusqu’à ce que mort s’ensuive. Il va falloir que vous agissiez rapidement, parce que s’ils se rendent compte que vous n’êtes pas $gl’un:l’une; d’entre eux, ils vous démasqueront. Frappez vite et avec férocité, $n.$b$bIci Troteman, terminé.', '', 'J\'espère que ce déguisement va tenir le coup…', 'Colonel Troteman', 'Ah, ah, ah ! Joli travail !', 'Colonel Troteman', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'itIT', 'Abuso di potere', 'Uccidi 5 Sergenti Roccianera utilizzando la Mazza Ferrata Punitiva dei Roccianera. Eliminali rapidamente, prima che possano smascherarti.', '$n? Sono Troteman.$b$bA quanto pare ti sei guadagnato la fiducia di Thorg\'izog e ti ha dato un incarico di natura disciplinare. Benissimo.$b$bOra devi eliminare i suoi sergenti. Li troverai sopra ai loro lupi, all\'esterno della Fortezza Roccianera nei Campi dell\'Onore. Usa la mazza ferrata su di loro, più e più volte, fino a ucciderli. Dovrai agire in fretta, $n, perché se scoprissero il tuo travestimento potrebbero smascherarti. Colpisci con rapidità e ferocia.$b$bPasso e chiudo.', '', 'Spero che il travestimento regga...', 'Colonnello Troteman', 'Ah ah ah! Ottimo lavoro!', 'Colonnello Troteman', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'koKR', '권력 남용', '검은바위 규율의 곤봉을 사용하여 검은바위 하사관 5명을 처치해야 합니다. 그들이 당신의 정체를 밝혀내기 전에 빨리 처치해야 합니다.', '$n 님? 트로트먼입니다.$b$b토르그이조그의 신임을 얻으신 것 같군요. 게다가 그가 당신에게 규율 임무를 맡겼다니 운이 좋습니다.$b$b당신이 장군의 하사관들을 제거해 주셔야겠습니다. 명예의 들판에 있는 검은바위 요새의 바깥쪽 늑대 소굴에서 그들을 찾을 수 있을 겁니다. 그 곤봉을 그들이 죽을 때까지 여러 차례 사용하십시오. 빨리 서둘러야 할 겁니다. 당신이 그들의 일원이 아닌 걸 알아채면, 그들이 당신의 정체를 밝혀낼 테니까요. 재빠르고 맹렬하게 공격하십시오, $n 님.$b$b이상 트로트먼이었습니다.', '', '그 변장이 안 망가지길 바랍니다...', '대령 트로트먼', '하하하! 잘하셨습니다!', '대령 트로트먼', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'ptBR', 'Abuso de poder', 'Mate 5 Sargentos Rocha Negra usando a Borduna da Disciplina de Rocha Negra. Derrote-os rapidamente antes que eles tenham uma chance de desmascarar você.', '$n? Aqui é o Troteman.$B$BParece que você conquistou a confiança de Thorg\'izog, e ele confiou a você a disciplina das tropas. Isso foi muita sorte.$B$BPrecisamos que você acabe com os sargentos dele. Você os encontrará montados em lobos, do lado de fora da Fortaleza Rocha Negra, nos Campos de Honra. Use a borduna neles várias vezes, até que morram. Você terá que agir rapidamente. Se eles perceberem que você não é um deles, eles podem $Gdesmascará-lo:desmascará-la;. Ataque com rapidez e violência, $n.$B$BTroteman, câmbio e desligo.', '', 'Espero que esse disfarce fique inteiro.', 'Coronel Troteman', 'Hahaha! Bom trabalho!', 'Coronel Troteman', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'ruRU', 'Злоупотребление полномочиями', 'Перебейте 5 старшин Черной горы при помощи дисциплинирующей дубинки Черной горы. Старайтесь убивать их быстро, пока те вас не раскусили.', '$n, прием! Тротман на связи.$b$bПохоже, ты $gвтерся:втерлась; в доверие к Торг\'изогу, и он поручил тебе следить за дисциплиной в армии. Нам это на руку.$b$bНужно будет убрать его старшин. Их можно встретить разъезжающими верхом на волке за стенами крепости Черной горы на Полях Славы. Дубась их, пока те не испустят дух. Действуй стремительно, потому что они могут раскусить тебя в этом облачении. Бей молниеносно и безжалостно, $n.$b$bКонец связи.', '', 'Надеюсь, костюм плотно сшит…', 'Полковник Тротман', 'Ха-ха-ха! Просто чудесно!', 'Полковник Тротман', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'zhCN', '滥用权力', '使用黑石训诫棒杀死5名黑石中士。速战速决，在你暴露身份之前杀死他们。', '$n？是我，托德曼。$b$b看来你已经赢得了索基佐格的信任，他连维持军队秩序的这样的重任都交给了你。我们真是幸运得很。$b$b杀掉他手下的中士吧。他们骑着座狼，在荣耀之地的黑石要塞外围巡逻。用那根棍棒猛敲他们，多敲几下，敲死为止。动作要快，一旦他们发现你不是黑石兽人，他们就会揭穿你的真面目。速战速决，下手要狠，$n。$b$b完毕。', '', '希望伪装不会暴露……', '托德曼上校', '哈哈哈！干得好！', '托德曼上校', '', 0);
INSERT INTO `quest_template_locale` VALUES (28279, 'zhTW', '權力濫用', '用黑石戒律鬥棍殺死5名黑石士官。在他們有機會揭發你之前迅速擊敗他們。', '$n？我是卓特曼。$b$b聽起來你贏得索伊佐格將軍的信任，讓他將維持軍紀的工作交給了你。這可真是幸運。$b$b我們需要你把他們的士官處理掉。你會在榮譽原野的黑石要塞外面找到他們。將棍棒用在他們身上，用個幾次，直到他們死掉。你得動作快點，因為若他們理解到你不是他們的一員，他們就能揭發你。快速而兇狠地出擊，$n。$b$B通話完畢。', '', '我希望這些偽裝不要散掉...', '卓特曼上校', '哈哈哈!幹得好!', '卓特曼上校', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'deDE', 'Reißt sie vom Himmel!', 'Benutzt den tragbaren Drachentöter, um 8 Schwarzdrachen des Drachenmals zu töten.', 'Um Himmels willen! Sie sind hier, $n. Sie sind hier!$B$BIch hatte gehofft, eine ganze Armee mit meinen tragbaren Drachenfäusten ausrüsten zu können, aber ich habe nur einen einzigen extrem teuren und viel zu wenig getesteten Prototyp. Hier, nehmt ihn!$B$BSchießt auf die Schwarzdrachen des Drachenmals, die um Donnermar kreisen, und lasst sie vom Himmel stürzen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'esES', 'Hazlos desaparecer del cielo', 'Usa el derribadracos cargado a hombros para matar a 8 dracos negros Faucedraco.', '¡Ah! ¡Ya están aquí, $n, ya están aquí!$B$BEsperaba poder equipar a todo un ejército con mi derribadracos cargado a hombros, pero solo tengo un prototipo sin probar extremadamente caro. Ten, ¡cógelo!$B$B¡Dispara a los dracos negros Faucedraco que están sobrevolando Bramal y hazlos caer del cielo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'esMX', 'Hazlos desaparecer del cielo', 'Usa el derribadracos cargado a hombros para matar a 8 dracos negros Faucedraco.', '¡Ah! ¡Ya están aquí, $n, ya están aquí!$B$BEsperaba poder equipar a todo un ejército con mi derribadracos cargado a hombros, pero solo tengo un prototipo sin probar extremadamente caro. Ten, ¡cógelo!$B$B¡Dispara a los dracos negros Faucedraco que están sobrevolando Bramal y hazlos caer del cielo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'frFR', 'Des drakes comme s\'il en pleuvait !', 'Utilisez le Perce-drake d\'épaule pour tuer 8 Drakes noirs gueules-de-dragon.', 'Hé ! Ils sont là, $n. Ils sont là !$B$BJe comptais équiper toute une armée avec mes perce-drake d\'épaule, mais tout ce que j\'ai c\'est un prototype extrêmement cher que je n\'ai pas complètement testé. Tenez, prenez-le !$B$BTirez sur les drakes noirs gueules-de-dragon qui tournent au-dessus de Tonnemar et faites-les tomber !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'itIT', 'Abbattili!', 'Usa l\'Impaladraghi a Spalla per uccidere 8 Drachi Neri delle Fauci di Drago.', 'Egah! Sono qui, $n. Sono qui!$B$BSperavo di dotare un intero esercito dei miei Impaladraghi a Spalla, ma ho un solo prototipo, molto costoso e assolutamente non testato. Tieni, prendilo!$B$BColpisci i Drachi Neri delle Fauci di Drago che sorvolano Tuonocupo e abbattili!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'koKR', '창공의 적을 처치하라!', '어깨걸이 탑승비룡 격추포를 사용해 용아귀 검은 비룡 8마리를 처치해야 합니다.', '으악! 놈들이에요, $n 님. 놈들이 여기 쳐들어왔다고요!$B$B어깨걸이 탑승비룡 격추포 부대를 구성하고 싶었는데, 안타깝게도 지금 가진 무기는 하나뿐이군요. 엄청나게 비싸고 시험을 전혀 거치지 않은 초기작 하나뿐이라고요. 자, 받으세요!$B$B이걸로 썬더마 위를 선회하는 용아귀 검은 비룡을 쏴서, 하늘에서 떨어뜨리세요!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'ptBR', 'Chuva de dracos que caem sem parar!', 'Use o Atira-lança Cai-cai-dragão para matar 8 Dracos Sombrios Presa do Dragão.', 'Eca! Eles estão aqui, $n. Eles estão aqui!$B$BEu queria equipar um exército inteirinho com meus Atira-lanças Cai-cai-dragão, mas tudo o que tenho é um protótipo caríssimo e ainda não testado. Aqui, tome ele!$B$BAtire nos dracos sombrios Presa do Dragão que sobrevoam Trondamar e derrube eles do céu!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'ruRU', 'Сбей их!', 'Убейте 8 черных драконов клана Драконьей Пасти с помощью переносного дракобоя.', 'Они уже здесь, $n. Они уже здесь!$B$BЯ надеялся оснастить своими переносными дракобоями целую армию, но все, что у меня есть – это один, невероятно дорогой и ни разу не испытанный образец. Вот, держи!$B$BСтреляй в черных драконов клана Драконьей Пасти, которые кружат над Громтаром! Стреляй и сбивай их!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'zhCN', '把它们从天上打下来！', '用肩扛式猎龙炮消灭8头龙喉黑色幼龙。', '呃啊！他们来了，$n。他们来了！$B$B我本来指望给整支军队配备肩扛式猎龙炮的，可现在我只有一杆极端昂贵、完全没有经过测试的原型。给，交给你了！$B$B把盘旋在桑德玛尔的龙喉黑色幼龙从天上打下来！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28280, 'zhTW', '將他們擊落!', '使用肩射式飛龍擊落裝置殺死8條龍喉黑龍。', '呃啊!他們在這裡，$n。他們來了!$B$B我本來希望能為整支軍隊裝備我的肩射式飛龍擊落裝置，但我所擁有的只是一個貴到不行而且從未測試過的原型機。來，拿去!$B$B射擊盤旋在桑德瑪上空的龍喉黑龍，讓他們從空中墜落!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'deDE', 'Letztes Gefecht in Donnermar', 'Tötet 8 Himmelskrallen des Drachenmalklans ohne Reittiere.', 'Ich überlasse es Euch und Euren Freunden von der Allianz. Ihr habt es dem Drachenmalklan bisher schon gut gezeigt!$B$BViele ihrer berittenen Kämpfer sind bereits ihre Reittiere los, aber sie kommen immer noch in unsere Richtung und meine Verteidiger kommen ohne Hilfe nicht zurecht.$B$BTötet die Himmelskrallen des Drachenmalklans ohne Reittiere und Donnermar wird für immer in Eurer Schuld stehen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'esES', 'La última batalla de Bramal', 'Mata a 8 garras del cielo Faucedraco que vayan a pie.', 'Tengo que reconoceros el mérito a ti y a tus amigos de la Alianza. ¡Les habéis dado una paliza a los Faucedraco!$B$BMuchos de sus jinetes han sido derribados pero siguen viniendo contra nosotros, y, sin ayuda, mis defensores se verán superados.$B$B¡Mata a los garras del cielo Faucedraco que vayan a pie y Bramal estará eternamente en deuda contigo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'esMX', 'La última batalla de Bramal', 'Mata a 8 garras del cielo Faucedraco que vayan a pie.', 'Tengo que reconocerte el mérito a ti y a tus amigos de la Alianza. ¡Les han dado una paliza a los Faucedraco!$B$BMuchos de sus jinetes han sido derribados pero siguen viniendo contra nosotros, y, sin ayuda, mis defensores se verán superados.$B$B¡Mata a los garras del cielo Faucedraco que vayan a pie y Bramal estará eternamente en deuda contigo!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'frFR', 'Bataille finale à Tonnemar', 'Tuez 8 Griffes-du-ciel gueules-de-dragon à pied.', 'J\'vous dois toute ma reconnaissance à vous et vos amis de l\'Alliance. Vous avez mis le paquet pour terrasser ces Gueules-de-dragon !$B$BDe nombreux cavaliers ont été désarçonnés mais ils continuent à nous assaillir. Mes défenseurs risquent d\'être vite dépassés sans aide.$B$BTuez les griffes-du-ciel gueules-de-dragon à pied et Tonnemar vous sera à jamais reconnaissant !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'itIT', 'L\'ultimo tentativo disperato a Tuonocupo', 'Uccidi 8 Cielartigli delle Fauci di Drago.', 'Devo riconoscerlo, tu e i tuoi amici dell\'Alleanza avete colpito davvero duro le Fauci di Drago!$B$BMolti dei loro cavalieri sono stati disarcionati, ma ci stanno attaccando e, senza un aiuto, i miei Difensori saranno sopraffatti.$B$BUccidi i Cielartigli delle Fauci di Drago appiedati e Tuonocupo sarà per sempre in debito con te!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'koKR', '썬더마 최후의 항전', '땅에 떨어진 용아귀 하늘발톱 8명을 처치해야 합니다.', '당신과 당신의 얼라이언스 친구들에게 박수를 보냅니다. 용아귀 놈들에게 본때를 보여주셨어요!$B$B놈들의 기수들이 땅에 많이 떨어졌습니다만, 놈들은 땅에서도 우리에게 달려들고 있습니다. 누가 도와주지 않으면 우리 수호병들도 결국엔 당하고 말 겁니다.$B$B땅에 떨어진 용아귀 하늘발톱 놈들을 처치해 주십시오. 그러면 썬더마는 당신에게 영원히 감사할 것입니다!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'ptBR', 'Resistência em Trondamar', 'Mate 8 Garracéus Presa do Dragão que estejam a pé.', 'Vou dar um crédito pra ti e teus amigos da Aliança. Vocês deram mesmo um jeito nos Presa do Dragão!$B$BMuitos dos cavaleiros estão sem montaria, mas continuam vindo para nos atacar e nossos soldados vão precisar de ajuda.$B$BMate os Garracéus Presa do Dragão que estão a pé e Trondamar será eternamente grata a ti.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'ruRU', 'Решающая битва за Громтар', 'Убейте 8 пеших небесных когтей из клана Драконьей Пасти.', 'Я поручаю это тебе и твоим друзьям из Альянса. Ты здорово $gпотрепал:потрепала; клан Драконьей Пасти!$B$BМногим из них пришлось спешиться, но они все еще наступают, и без твоей помощи мои воины долго не продержатся.$B$BУбей пеших небесных когтей из клана Драконьей Пасти, и весь Громтар будет в вечном долгу перед тобой!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'zhCN', '桑德玛尔背水一战', '徒步杀死8名龙喉掠天战士。', '一切就交给你和你的联盟朋友了。你把龙喉打得好惨！$B$B他们的不少骑士都掉了下来，可还在朝我们进发，没有人帮忙的话，我的防御者是抵挡不住的。$B$B徒步杀死龙喉掠天战士，桑德玛尔会永远记住你的恩情！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28281, 'zhTW', '桑德瑪的最後防線', '殺死8名步行的龍喉天爪。', '我得讚揚你和你聯盟朋友們的功勞。你們真的把龍喉部族打得潰不成軍!$B$B儘管他們許多騎士墜落到了地上，但他們還是不斷朝我們進攻。若沒有人幫忙，我的防禦者們將會被擊敗。$B$B殺死步行的龍喉天爪，桑德瑪氏族將永遠欠你大恩情!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'deDE', 'Narkrall, der Drachenzähmer', 'Tötet Narkrall Harkenklaue. Sprecht mit Colin Donnermar, wenn Ihr bereit seid.', 'Narkrall vom Drachenmalklan wird auch der \"Drachenzähmer\" genannt. Ich weiß nicht, wie er die Macht erlangt hat, diese Bestien zu befehligen, aber der schwarze Terror, den er reitet, ist der grausigste Drache, der in den ganzen Hochländern zu finden ist.$B$B$n, Narkrall ist ein Feind, der das Leben meines Vaters genommen und Donnermars Schicksal in meine Hände gelegt hat.$B$BIch kann mich ihm nicht alleine stellen, aber ich habe gesehen, zu was Ihr in der Lage seid, $C. Vielleicht können wir diesem Schrecken gemeinsam ein Ende bereiten.$B$BLasst mich wissen, wenn Ihr bereit seid, und wir werden Narkrall gemeinsam bekämpfen!', '', 'Sprecht mit mir, wenn Ihr bereit seid. Ich hole ihn von seinem Drachen herunter, aber ich werde Eure Hilfe benötgen, um ihm den Garaus zu machen...', 'Narkrall Harkenklaue', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'esES', 'Narkrall, el domador de dracos', 'Mata a Narkrall Rasgarra. Habla con Colin Bramal cuando estés $glisto:lista;.', 'Hay un Faucedraco en este grupo, aquel al que llaman \"Narkrall: el domador de dracos.\" No sé de dónde saca el poder que tiene sobre esas bestias pero el terror negro que monta es una mala bestia como no se había visto nunca en los cielos que cubren las Tierras Altas.$B$B$n, Narkrall es el villano que le quitó la vida a mi padre y dejó Bramal en mis manos.$B$BNo puedo enfrentarme a él solo, pero he visto de lo que eres capaz, $c. Tal vez juntos podamos ponerle fin a esto.$B$BAvísame cuando estés $glisto:lista; ¡y lucharemos contra Narkrall juntos!', '', 'Habla conmigo cuando te hayas preparado. Haré que se baje del draco, pero necesitaré tu ayuda para acabar con él...', 'Narkrall Rasgarra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'esMX', 'Narkrall, el domador de dracos', 'Mata a Narkrall Rasgarra. Habla con Colin Bramal cuando estés $glisto:lista;.', 'Hay un Faucedraco en este grupo, aquel al que llaman \"Narkrall: el domador de dracos.\" No sé de dónde saca el poder que tiene sobre esas bestias pero el terror negro que monta es una mala bestia como no se había visto nunca en los cielos que cubren las Tierras Altas.$B$B$n, Narkrall es el villano que le quitó la vida a mi padre y dejó Bramal en mis manos.$B$BNo puedo enfrentarme a él solo, pero he visto de lo que eres capaz, $c. Tal vez juntos podamos ponerle fin a esto.$B$BAvísame cuando estés $glisto:lista; ¡y lucharemos contra Narkrall juntos!', '', 'Habla conmigo cuando te hayas preparado. Haré que se baje del draco, pero necesitaré tu ayuda para acabar con él...', 'Narkrall Rasgarra', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'frFR', 'Narkrall le dompteur de drakes', 'Tuez Narkrall Raclegriffe. Allez voir Colin Tonnemar quand vous serez $gprêt:prête;.', 'Parmi ces Gueules-de-dragon, y en a un qu\'ils appellent « Narkrall, le dompteur de drakes ». Je n\'sais pas d\'où lui vient ce pouvoir qu\'il a sur ces bêtes, mais la terreur noire qu\'il chevauche a été d\'une brutalité sans nom quand il a pris le ciel des hautes-terres.$B$B$n, c\'est ce monstre de Narkrall qui a pris la vie de mon père et m\'a laissé Tonnemar entre les mains.$B$BSeul, j\'arriverai jamais à le vaincre, mais j\'ai vu ce dont vous êtes capable, $c. Ensemble, on pourrait peut-être mettre fin à tout ça.$B$BDites-moi quand vous s\'rez $gprêt:prête; et ensemble, on vaincra Narkrall !', '', 'Venez me voir quand on pourra y aller. Je le ferai tomber de son drake, mais j\'aurai besoin de vous pour l\'achever…', 'Narkrall Raclegriffe', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'itIT', 'Narkrall il Domadrachi', 'Uccidi Narkrall Graffiazanna. Parla con Colin Tuonocupo quando vuoi cominciare.', 'C\'è un membro delle Fauci di Drago in particolare... che chiamano \"Narkrall il Domadrachi\". Non so da dove tragga il suo potere su quelle creature, ma il terrore nero che cavalca è la cosa più orribile che abbia mai sorvolato le Alture.$B$B$n, Narkrall è il demonio che ha ucciso mio padre e ha lasciato Tuonocupo nelle mie mani.$B$BNon posso affrontarlo da solo, $C, ma ho visto quello di cui sei capace. Forse insieme possiamo mettere fine a tutto questo.$B$BFammi sapere quando sei $gpronto:pronta; e affronteremo Narkrall insieme!', '', 'Parla con me quando vuoi partire. Io lo disarcionerò dal suo draco, ma avrò bisogno del tuo aiuto per finirlo...', 'Narkrall Graffiazanna', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'koKR', '비룡 조련사 나크랄', '나크랄 레이크클로를 처치해야 합니다. 준비되면 콜린 썬더마와 이야기하십시오.', '이놈들 중에 특별한 용아귀 오크가 하나 있습니다. \"비룡 조련사 나크랄\"이라고 하지요. 놈이 야수를 지배하는 힘을 어떻게 얻었는지는 모르나, 어쨌든 놈이 타는 검은 비룡은 지금껏 고원의 하늘을 가른 그 어떤 날짐승보다도 끔찍합니다.$B$B$n 님, 저희 아버지가 돌아가시고 제가 썬더마를 맡게 된 것도 다 저 마귀 같은 나크랄 때문입니다.$B$B저 혼자서는 놈을 상대할 수 없지만, 당신처럼 능력이 좋은 $c|1이;가; 여기 있잖습니까. 함께라면 놈을 끝낼 수 있을지도 모릅니다.$B$B준비되면 알려 주십시오. 함께 나크랄과 싸우는 겁니다!', '', '준비되면 제게 말씀하십시오. 제가 놈을 비룡에서 끌어내겠습니다. 하지만 놈을 끝장내려면 당신의 도움이 필요합니다.', '나크랄 레이크클로', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'ptBR', 'Narkrall, o domadracos', 'Mate Narkrall Garrasgo. Fale com Colin Trondamar quando estiver $gpronto:pronta;.', 'Tem um Presa do Dragão neste lugar, aquele que eles chamam \"Narkrall, o domadracos.\" Não sei como ele exerce poder sobre essas criaturas, mas o terror sombrio que ele cavalga é tão sórdido e bruto como nunca se viu no céu sobre o Planalto.$B$B$n, Narkrall é o fanático que tirou a vida do meu pai e deixou Trondamar em minhas mãos.$B$BNão consigo lidar com ele sozinho, mas sei do que você é capaz, $c. Talvez juntos possamos dar um fim a isso.$B$BQuando estiver $Gpronto:pronta;, vamos juntos combater Narkrall!', '', 'Vou derrubá-lo do draco, mas precisarei da sua ajuda para acabar com ele...', 'Narkrall Garrasgo', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'ruRU', 'Наркралл Укротитель Драконов', 'Убейте Наркралла Кривого Когтя. Когда будете готовы, поговорите с Колином Громтаром.', 'Среди всех орков из клана Драконьей Пасти особенно выделяется один – тот, которого все зовут \"Наркралл Укротитель Драконов\". Я не знаю, как ему удается укрощать драконов, но он действительно летает верхом на ужасной черной твари.$B$B$n, Наркралл – тот самый негодяй, который убил моего отца.$B$B$C, я не справлюсь с ним в одиночку – а вот твои таланты мне известны. Возможно, вместе нам удастся его уничтожить.$B$BКогда будешь $gготов:готова;, дай мне знать – и тогда мы вместе сразимся с Наркраллом!', '', 'Сообщи мне о своей готовности. Я заставлю его спешиться с дракона, но мне понадобится твоя помощь в схватке с ним…', 'Наркралл Кривой Коготь', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'zhCN', '驯龙者纳克拉尔', '杀死纳克拉尔·掠爪。做好准备后就去跟科林·桑德玛尔谈一谈。', '龙喉兽人的部队中有一个叫做“纳克拉尔：驯龙者”的。我不知道他是怎么有能力控制这些畜生的，但他胯下的黑色恐怖生物是高地的天空中最残忍恶毒的。$B$B$n，就是纳克拉尔这个恶魔夺走了我父亲的生命，让我肩负起了桑德玛尔的命运。$B$B我一个人无法将其击败，但我见过你的能耐，$c。也许我们可以一起把这事做个了结。$B$B你准备好了就告诉我，我们合力对付纳克拉尔！', '', '你准备好了就告诉我。我会把他从幼龙身上拽下来，但你得帮忙将他干掉……', '纳克拉尔·掠爪', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (28282, 'zhTW', '『馴龍師』納克拉爾', '殺死納克拉爾‧耙爪。準備好時和柯林‧桑德瑪談話。', '在這群人之中有一個龍喉獸人，他們稱之為「馴龍師納克拉爾」。我不知道他從哪裡取得駕馭那些野獸的力量，但他所騎乘的黑色恐怖，是有史以來出現在高原上空的生物中最難纏的。$B$B$n，納克拉爾就是奪走我父親性命、讓我肩負起桑德瑪這項責任的惡魔。$B$B我無法獨力撂倒他，但我已經了解你的能力，$c。也許我們能協力了結這件事。$B$B準備好時讓我知道，我們會一起和納克拉爾作戰!', '', '準備好時和我說話。我會把他弄下龍來，但我需要你幫忙一起解決他...', '納克拉爾‧耙爪', '', '', '', 0);
