INSERT INTO `quest_template_locale` VALUES (28285, 'itIT', 'Incontro con Patrice Lancaster', 'Parla con Patrice Lancaster nella Sala di Addestramento a Roccavento.', '$n, ho appena ricevuto una pergamena dalla Sala di Addestramento. Patrice Lancaster ti ha mandato a chiamare. Dovresti andare a parlarle il prima possibile.', '', '', '', '', '', 'Parla con Patrice Lancaster nella Sala di Addestramento a Roccavento.', 0);
INSERT INTO `quest_template_locale` VALUES (28285, 'koKR', '파트리스 랭커스터 만나기', '스톰윈드의 훈련장에 있는 파트리스 랭커스터와 대화해야 합니다.', '방금 훈련장에서 두루마리가 도착했습니다, $n. 파트리스 랭커스터 님이 당신을 만나보고 싶어 한답니다. 최대한 빨리 그분을 찾아가 보세요.', '', '', '', '', '', '스톰윈드의 훈련장에 있는 파트리스 랭커스터와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28285, 'ptBR', 'Encontro com Patrícia Lancaster', 'Fale com Patrícia Lancaster no Salão de Treinamento em Ventobravo.', 'Acabo de receber um pergaminho do Salão de Treinamento, $n, informando que a Patrícia Lancaster deseja conversar contigo. Apressa-te, pois ela $Go:a; aguarda.', '', '', '', '', '', 'Fale com Patrícia Lancaster no Salão de Treinamento em Ventobravo.', 0);
INSERT INTO `quest_template_locale` VALUES (28285, 'ruRU', 'Встреча с Патрисией Ланкастер', 'Поговорите с Патрисией Ланкастер в тренировочном зале Штормграда.', 'Я только что получил свиток из тренировочного зала, $n. Патрисия Ланкастер пишет, что хочет поговорить с тобой. Тебе нужно встретиться с ней как можно скорее.', '', '', '', '', '', 'Поговорите с Патрисией Ланкастер в тренировочном зале Штормграда.', 0);
INSERT INTO `quest_template_locale` VALUES (28285, 'zhCN', '与帕崔斯·兰卡斯特碰面', '跟暴风城训练大厅的帕崔斯·兰卡斯特谈一谈。', '我收到一份来自训练大厅的卷轴，$n。帕崔斯·兰卡斯特说她想跟你谈谈。你最好尽快去见她。', '', '', '', '', '', '跟暴风城训练大厅的帕崔斯·兰卡斯特谈一谈。', 0);
