INSERT INTO `quest_template_locale` VALUES (28289, 'ruRU', 'Лунная поляна зовет', 'Отправляйтесь в Лунную поляну и поговорите с Логанааром в Ночной Гавани.', 'Я только что узнала, что одному из наших братьев на Лунной поляне нужна твоя помощь, $n. Отправляйся туда и поговори с Логанааром. Его можно найти в Ночной Гавани. Он расскажет тебе все, что нужно.', '', '', '', '', '', 'Отправляйтесь на Лунную поляну и поговорите с Логанааром в Ночной Гавани.', 0);
