INSERT INTO `quest_template_locale` VALUES (28297, 'deDE', 'Trefft Euch mit Tamanji', 'Sprecht mit Tamanji in der Kaserne in Orgrimmar.', 'Ich habe eine Schriftrolle von Tamanji aus Orgrimmar erhalten. Sie möchte, dass Ihr Euch auf den Weg zur Kaserne im Tal der Ehre macht und sie so schnell wie möglich aufsucht. Es klang sehr dringlich, $n.', '', '', '', '', '', 'Sprecht mit Tamanji in der Kaserne in Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'esES', 'Reunión con Tamanji', 'Habla con Tamanji en el cuartel de Orgrimmar.', 'He recibido un pergamino para ti de Tamanji, de Orgrimmar. Quiere que te dirijas al cuartel del Valle del Honor y hables con ella en cuanto puedas. Suena urgente, $n.', '', '', '', '', '', 'Habla con Tamanji en el cuartel de Orgrimmar.', 0);
