INSERT INTO `quest_template_locale` VALUES (28297, 'frFR', 'Rencontre avec Tamanji', 'Parlez à Tamanji à la Caserne d’Orgrimmar.', 'J’ai reçu un pli pour vous de la part de Tamanji, d’Orgrimmar. Elle veut que vous alliez lui parler dès que possible à la caserne de la vallée de l’Honneur. Ça avait l’air urgent, $n.', '', '', '', '', '', 'Parlez à Tamanji à la caserne d’Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'itIT', 'Incontro con Tamanji', 'Parla con Tamanji nella Caserma a Orgrimmar.', 'Tamanji di Orgrimmar ha inviato questa pergamena per te. Vuole che ti rechi il prima possibile alla Caserma nella Valle dell\'Onore per incontrarla. Sembrava urgente, $n.', '', '', '', '', '', 'Parla con Tamanji nella Caserma a Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'koKR', '타만지 만나기', '오그리마의 병영에 있는 타만지와 대화해야 합니다.', '오그리마에 있는 타만지가 당신에게 두루마리를 보내왔습니다. 가능하면 빨리 오그리마 명예의 골짜기의 병영으로 자기를 찾아와 달라는군요. 무척 급한 일인 것 같습니다, $n.', '', '', '', '', '', '오그리마의 병영에 있는 타만지와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'ptBR', 'Encontro com Tamanji', 'Fale com Tamanji no Quartel em Orgrimmar.', 'Recebi um pergaminho de Tamanji, de Orgrimmar. Ela pediu para que você vá ao Vale da Honra encontrá-la no Quartel o mais rápido possível. Parecia ser urgente, $n.', '', '', '', '', '', 'Fale com Tamanji no Quartel em Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'ruRU', 'Встреча с Таманджи', 'Поговорите с Таманджи в казармах Оргриммара.', 'Таманджи из Оргриммара прислала свиток. Она просит тебя как можно быстрее прибыть в казармы на аллею Чести и поговорить с ней. Похоже, дело срочное, $n.', '', '', '', '', '', 'Поговорите с Таманджи в казармах Оргриммара.', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'zhCN', '与塔曼吉碰面', '与奥格瑞玛兵营的塔曼吉交谈。', '我收到一份来自奥格瑞玛的塔曼吉的卷轴。她要你尽快前往荣誉谷的兵营里跟她谈谈。看来是件紧急的事情，$n。', '', '', '', '', '', '与奥格瑞玛兵营的塔曼吉交谈。', 0);
INSERT INTO `quest_template_locale` VALUES (28297, 'zhTW', '與塔瑪基會面', '到奧格瑪的兵營與塔瑪基交談。', '我收到奧格瑪的塔瑪基寄來給你的卷軸。她希望你盡快啟程前往榮譽谷的兵營找她談話。聽起來很急迫，$n。', '', '', '', '', '', '到奧格瑪的兵營與塔瑪基交談。', 0);
