INSERT INTO `quest_template_locale` VALUES (28298, 'esES', 'Reunión con Thega Hojatumba', 'Habla con Thega Hojatumba en el cuartel de Orgrimmar.', 'Thega Hojatumba, la instructora jefa de pícaros de la Mano Destrozada en Orgrimmar, desea reunirse contigo. Ve rápidamente a Orgrimmar, $n, y búscala en el cuartel del Valle del Honor. Debe de tener una misión importante para ti.', '', '', '', '', '', 'Habla con Thega Hojatumba en el cuartel de Orgrimmar.', 0);
