INSERT INTO `quest_template_locale` VALUES (28298, 'frFR', 'Rencontre avec Théga Tombelame', 'Parlez à Théga Tombelame à la Caserne d’Orgrimmar.', 'Théga Tombelame, le maître des voleurs de la Main-Brisée à Orgrimmar, souhaite vous parler. Rendez-vous sur place dès que possible, $n. Vous la trouverez à la caserne de la vallée de l’Honneur. Elle doit avoir une importante mission à vous confier.', '', '', '', '', '', 'Parlez à Théga Tombelame à la caserne d’Orgrimmar.', 0);
