INSERT INTO `quest_template_locale` VALUES (28298, 'koKR', '테가 그레이브블레이드 만나기', '오그리마의 병영에 있는 테가 그레이브블레이드와 대화해야 합니다.', '오그리마 으스러진 손의 상급 도적 테가 그레이브블레이드가 당신을 만나고 싶어 합니다. 빨리 오그리마로 가서 명예의 골짜기 병영에 있는 테가를 찾아보십시오, $n 님. 뭔가 중요한 임무를 맡기려는 것 같습니다.', '', '', '', '', '', '오그리마의 병영에 있는 테가 그레이브블레이드와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28298, 'ptBR', 'Encontro com Tega Lamitumba', 'Fale com Tega Lamitumba no Quartel em Orgrimmar.', 'Tega Lamitumba, a experiente instrutora de ladinos da Mão Despedaçada de Orgrimmar, quer falar com você. Vá para Orgrimmar o mais rápido possível, $n, para encontrá-la no Quartel do Vale da Honra. Creio que ela tenha uma missão importante para você.', '', '', '', '', '', 'Fale com Tega Lamitumba no Quartel em Orgrimmar.', 0);
