INSERT INTO `quest_template_locale` VALUES (28298, 'zhCN', '与瑟佳·墓刃交谈', '与奥格瑞玛兵营的瑟佳·墓刃交谈。', '奥格瑞玛的瑟佳·墓刃是碎手氏族的资深潜行者训练师，她希望与你会面。快去奥格瑞玛找她吧，$n。兵营就在荣誉谷中，她肯定有重要任务要交托给你。', '', '', '', '', '', '与奥格瑞玛兵营的瑟佳·墓刃交谈。', 0);
