INSERT INTO `quest_template_locale` VALUES (28299, 'deDE', 'Trefft Euch mit Kazak Dunkelschrei', 'Sprecht mit Kazak Dunkelschrei in der Kaserne in Orgrimmar.', 'Ich habe soeben eine Nachricht von Kazak Dunkelschrei erhalten, Orgrimmars mächtigstem Lehrer für Hexenmeister. Er wünscht, dass Ihr nach Orgrimmar reist und ihn in der Kaserne im Tal der Ehre trefft. Es klang, als habe er eine wichtige Aufgabe für Euch.', '', '', '', '', '', 'Sprecht mit Kazak Dunkelschrei in der Kaserne in Orgrimmar.', 0);
