INSERT INTO `quest_template_locale` VALUES (28304, 'koKR', '오그리마로의 여행', '오그리마의 병영에 있는 마틴 굿차일드와 대화해야 합니다.', '전령이 당신에게 초대를 전해달라 부탁하더군요. 오그리마 병영에 머무르시는 상급 사제 마틴 굿차일드 님이 보내신 겁니다.$B$B가능한 한 이른 시일 내로 조속히 와달라고 하셨습니다. 병영은 명예의 골짜기에 있으니 가보시지요.', '', '', '', '', '', '오그리마의 병영에 있는 마틴 굿차일드와 대화해야 합니다.', 0);
INSERT INTO `quest_template_locale` VALUES (28304, 'ptBR', 'Jornada para Orgrimmar', 'Fale com Martim Boninfante no Quartel em Orgrimmar.', 'Um mensageiro acabou de deixar este convite para você. Foi mandado pelo instrutor de sacerdotes Martim Boninfante, que está no Quartel em Orgrimmar.$B$BEle quer falar com você assim que possível. O Quartel fica no Vale da Honra.', '', '', '', '', '', 'Fale com Martim Boninfante no Quartel em Orgrimmar.', 0);
