INSERT INTO `quest_template_locale` VALUES (28308, 'ruRU', 'Путь в Оргриммар', 'Поговорите с Мартином Гудчайлдом в казармах Оргриммара.', 'Тебя вызывают в Оргриммар. Мартин Гудчайлд просит тебя как можно скорее явиться к нему в казармы. В сообщении не сказано о причинах спешки, но, похоже, речь о чем-то очень важном.$B$BКазармы находятся на аллее Чести.', '', '', '', '', '', 'Поговорите с Мартином Гудчайлдом в казармах Оргриммара.', 0);
INSERT INTO `quest_template_locale` VALUES (28308, 'zhCN', '奥格瑞玛之行', '与奥格瑞玛兵营的马丁·古尔乔交谈。', '一位信使请我传递一份邀请，是牧师训练师马丁·古尔乔发来的，他就在奥格瑞玛的兵营里。$B$B他请你在方便时尽快去见他。兵营就在荣誉谷中。', '', '', '', '', '', '与奥格瑞玛兵营的马丁·古尔乔交谈。', 0);
INSERT INTO `quest_template_locale` VALUES (28308, 'zhTW', '前往奧格瑪', '到奧格瑪的兵營與馬丁‧古柴爾德交談。', '有個信差請我把這個邀請函交給你。寄件人是馬丁‧古柴爾德，他是牧師訓練師，常駐在奧格瑪的兵營。$B$B他請你一有空就馬上去見他。兵營就在榮譽谷中。', '', '', '', '', '', '到奧格瑪的兵營與馬丁‧古柴爾德交談。', 0);
INSERT INTO `quest_template_locale` VALUES (28309, 'deDE', 'Reise nach Orgrimmar', 'Sprecht mit Martin Gutkind in der Kaserne in Orgrimmar.', 'Ein Bote hat mich gebeten, Euch diese Einladung zu geben. Sie ist von Priesterlehrer Martin Gutkind aus der Kaserne in Orgrimmar.$B$BEr fordert Euch auf, ihn baldmöglichst zu treffen. Ihr findet die Kaserne im Tal der Ehre.', '', '', '', '', '', 'Sprecht mit Martin Gutkind in der Kaserne in Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28309, 'esES', 'Viaje a Orgrimmar', 'Habla con Martin Bueninfante en el cuartel de Orgrimmar.', 'Un mensajero acaba de pedirme que haga llegar una invitación. Es del instructor de sacerdotes, Martin Bueninfante, que está en el cuartel de Orgrimmar.$B$BQuiere que te reúnas con él cuando te sea posible. El cuartel se encuentra en el Valle del Honor.', '', '', '', '', '', 'Habla con Martin Bueninfante en el cuartel de Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28309, 'esMX', 'Viaje a Orgrimmar', 'Habla con Martin Goodchilde en el Cuartel de Orgrimmar.', 'Un mensajero me acaba de pedir que te transmitiera una invitación. Es de Martin Goodchilde, el instructor de sacerdotes, del Cuartel de Orgrimmar.$B$BPide que te reúnas con él lo antes posible. Encontrarás el Cuartel en el Valle del Honor.', '', '', '', '', '', 'Habla con Martin Goodchilde en el Cuartel de Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28309, 'frFR', 'Voyage à Orgrimmar', 'Parlez à Martin Bonenfant à la Caserne d’Orgrimmar.', 'Un messager m’a demandé de vous transmettre une invitation. Elle vient de Martin Bonenfant, un maître des prêtres de la caserne d’Orgrimmar.$B$BIl vous demande de le rejoindre au plus vite. La caserne se trouve dans la vallée de l’Honneur.', '', '', '', '', '', 'Parlez à Martin Bonenfant à la caserne d’Orgrimmar.', 0);
INSERT INTO `quest_template_locale` VALUES (28309, 'itIT', 'Viaggio a Orgrimmar', 'Parla con Martin Bravuomo alla Caserma di Orgrimmar.', 'Un messaggero mi ha chiesto di passarti un invito. È dell\'Istruttore dei Sacerdoti, Martin Bravuomo, nella Caserma di Orgrimmar.$B$BTi chiede di incontrarlo subito. La Caserma si trova nella Valle dell\'Onore.', '', '', '', '', '', 'Parla con Martin Bravuomo alla Caserma di Orgrimmar.', 0);
