INSERT INTO `quest_template_locale` VALUES (28309, 'zhCN', '奥格瑞玛之行', '与奥格瑞玛兵营的马丁·古尔乔交谈。', '一位信使请我传递一份邀请，是牧师训练师马丁·古尔乔发来的，他就在奥格瑞玛的兵营里。$B$B他请你在方便时尽快去见他。兵营就在荣誉谷中。', '', '', '', '', '', '与奥格瑞玛兵营的马丁·古尔乔交谈。', 0);
