INSERT INTO `quest_template_locale` VALUES (29531, 'koKR', '뭘 좀 잡자', '퀘스트기버가 말한 것 잡자', '니가 진짜 뭘 좀 잡아 없애줬으면 좋겠는데. 쫌 중요한 일이야.', '', '', '', '', '', '', 0);
