INSERT INTO `quest_template_locale` VALUES (29806, 'ptBR', 'O errante', 'Derrote o Brigão Errante em uma luta.', '[ph] Encontre o Brigão Errante e o desafie para uma luta.', '', '', '', '', '', 'Falar com o Brigão Errante.', 0);
