INSERT INTO `quest_template_locale` VALUES (30000, 'frFR', 'Le Serpent de jade', 'Écoutez le Serpent de jade.', 'Vous êtes $gvenu:venue; transmettre un message au Serpent de jade, n’est-ce pas ?$B$B<La petite fille lève les yeux vers vous d’un air inquisiteur.>$B$BJe crois qu’il est temps pour vous de le rencontrer.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'itIT', 'La Serpe di Giada', 'Ascolta la Serpe di Giada.', 'Sei qui per riferire un messaggio alla Serpe di Giada, vero?$B$B<La ragazzina solleva la testa e ti guarda con aria inquisitoria.>$B$BPer te è arrivato il momento di incontrarla.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'koKR', '옥룡', '옥룡의 말을 들어야 합니다.', '옥룡 님께 말씀을 전하러 오셨죠?$B$B<고개를 들고 호기심에 가득한 눈으로 당신을 바라보는 소녀>$B$B이제 만나보실 시간이에요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'ptBR', 'A Serpente de Jade', 'Ouça a Serpente de Jade.', 'Você veio trazer uma mensagem para a Serpente de Jade, não é?$B$B<A menina inclina a cabeça para você interrogativamente.>$B$BAcho que é hora de você conhecê-la.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'ruRU', 'Нефритовая Змея', 'Выслушайте Нефритовую Змею.', 'У тебя сообщение для Нефритовой змеи?$B$B<Девочка вопросительно смотрит на вас.>$B$BПохоже, тебе пора наконец с ней встретиться.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'zhCN', '青龙', '倾听青龙的讲述。', '你是来给青龙捎口信的，对吗？$B$B<小女孩抬着头好奇地看着你。>$B$B我想是时候让你跟她见个面了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30000, 'zhTW', '玉蛟', '聆聽玉蛟的話。', '你是來傳訊給玉蛟的對嗎？$B$B<小女孩歪過頭，好奇地看著你。>$B$B我想是讓你見她的時候了。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'deDE', 'Mottenplage', 'Vernichtet 8 gefleckte Motten.', 'Motten sind in der Bibliothek eingefallen. Motten!$B$BJeder Bibliothekar, der seines Namens würdig ist, weiß, dass Motten nach den Bücherwürmern die größte Gefahr für Papier sind.$B$BWie sich herausgestellt hat, sind meine Assistenten Feiglinge, die sich vor Insekten fürchten.$B$BBitte, Ihr MÜSST mir helfen, diese Motten loszuwerden! Die Werke tausender Pandaren-Geschichtenerzähler müssen geschützt werden!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'esES', 'Apolillado', 'Mata a 8 polillas veteadas.', 'Las polillas han infestado la biblioteca. ¡Polillas!$B$BTodo bibliotecario que se precie sabe que las polillas son la mayor amenaza para el papel, igualadas solamente por los gusanos de biblioteca.$B$BSegún parece, mis ayudantes no son más que una panda de cobardes que tienen miedo a los insectos.$B$B¡TIENES que ayudarme a deshacerme de las polillas de inmediato! ¡Las obras de un millar de escritores pandaren dependen de ti!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'esMX', 'Apolillado', 'Mata a 8 polillas veteadas.', 'Las polillas han infestado la biblioteca. ¡Polillas!$B$BTodo bibliotecario que se precie sabe que las polillas son la mayor amenaza para el papel, igualadas solamente por los gusanos de biblioteca.$B$BSegún parece, mis ayudantes no son más que una panda de cobardes que tienen miedo a los insectos.$B$B¡TIENES que ayudarme a deshacerme de las polillas de inmediato! ¡Las obras de un millar de escritores pandaren dependen de ti!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'frFR', 'Infestation de phalènes', 'Tuez 8 Phalènes tachetés.', 'La bibliothèque est infestée de phalènes… Oui, des phalènes !$B$BTout bibliothécaire qui se respecte sait que le principal ennemi du papier, ce sont les phalènes, juste après les vers biblivores.$B$BIl se trouve que mes assistants ne sont qu’une bande de poltrons insectophobes.$B$BJe vous en prie, vous DEVEZ immédiatement m’aider à me débarrasser de ces phalènes ! Les travaux d’un millier de chroniqueurs pandarens dépendent de vous !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'itIT', 'L\'infestazione di falene', 'Uccidi 8 Falene Maculate.', 'Le falene hanno infestato la libreria. Falene!$B$BQualsiasi bibliotecario degno di questo nome sa perfettamente che sono le nemiche più pericolose della carta, seconde solo ai tarli.$B$BEvidentemente, i miei assistenti sono un mucchio di inutili codardi, terrorizzati dagli insetti!$B$BTi prego, DEVI aiutarmi a eliminarle immediatamente! Il destino delle opere di mille narratori Pandaren dipende da te!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'koKR', '나방은 책의 적', '얼룩 나방 8마리를 처치해야 합니다.', '나방이 도서관에 침입했네. 나방!$B$B도서관장이라 말할 수 있는 이라면 누구나, 책벌레와 나방은 종이의 천적이라는 사실을 잘 알고 있지.$B$B이제 보니, 내 조수들은 모두 벌레를 무서워하더군. 도무지 쓸모 없는 겁쟁이들!$B$B제발, 당장 저 나방들을 없애주게! 판다렌 이야기꾼 수천 명이 남긴 작품의 운명이 자네 손에 달려 있네.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'ptBR', 'Obsessão por mariposas', 'Mate 8 Mariposas Sarapintadas.', 'A biblioteca está infestada de mariposas. Mariposas!$B$BQualquer bibliotecário sabe que, depois das traças, as mariposas são a maior ameaça ao papel.$B$BAcontece que meus ajudantes são um bando de covardes que têm medo de insetos.$B$BPor favor, você PRECISA me ajudar a acabar com as mariposas imediatamente! As obras de milhares de escritores pandarens dependem de você!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'ruRU', 'Нашествие моли', 'Уничтожьте 8 пятнистых мотыльков.', 'Библиотеку наводнила моль. Моль!$B$BКаждый библиотекарь знает, что моль – самый опасный враг книг, за исключением книжных червей.$B$BНо оказалось, что все мои помощники – жалкие трусы! Они боятся насекомых!$B$BПожалуйста, помоги мне! Ты $gдолжен:должна; немедленно избавить нас от моли! От этого зависит судьба тысяч пандарийских литературных памятников!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'zhCN', '飞蛾满屋', '杀死8只斑点飞蛾。', '飞蛾正在侵蚀藏书阁。飞蛾！$B$B任何称职的藏书人都知道飞蛾对纸张的威胁极大，仅次于书虫。$B$B事实证明，我的助手都是一群无可救药的胆小鬼，连虫子都怕。$B$B请你一定要帮我消灭些飞蛾！上千名熊猫人作家的心血之作都指望你来拯救了！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30001, 'zhTW', '蛾滿為患', '殺死8隻斑紋飛蛾。', '圖書館被蛾侵佔了。蛾!$B$B隨便一個夠格的圖書館員都知道蛾是紙張的最大威脅，僅次於書蟲。$B$B到頭來，我的助手都是一群怕蟲的膽小沒用傢伙。$B$B拜託，你得馬上幫我處理掉這些蛾!上千名熊貓人敘事者的心血結晶仰賴著你!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'deDE', 'Historische Seiten', 'Sucht in der Bibliothek nach befallenen Büchern und zerquetscht 12 Bücherwürmer.', 'Das Allerschlimmste ist geschehen, $R.$B$BWir. Haben. Bücherwürmer.$B$BDie Lage ist ernst. Alle Bücherwürmer in der Bibliothek müssen augenblicklich ausgelöscht werden, damit sie sich nicht noch weiter ausbreiten.$B$BWir müssen dieses Problem ausmerzen! Bitte untersucht die befallenen Bücher und zerquetscht jeden Bücherwurm, den Ihr findet.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'esES', 'Páginas de historia', 'Busca en la biblioteca libros infestados y aplasta a 12 gusanos de biblioteca.', 'Ha sucedido lo peor que podía pasar, $r.$B$BHay. Gusanos. De biblioteca.$b$bDebemos erradicarlos inmediatamente si queremos evitar que lo infesten todo.$B$BHay que aplastar el problema, ¡literalmente! Por favor, examina los libros de la biblioteca infestados y acaba con cualquier gusano que encuentres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'esMX', 'Páginas de historia', 'Busca en la biblioteca libros infestados y aplasta a 12 gusanos de biblioteca.', 'Ha sucedido lo peor que podía pasar, $r.$B$BHay. Gusanos. De biblioteca.$b$bDebemos erradicarlos inmediatamente si queremos evitar que lo infesten todo.$B$BHay que aplastar el problema, ¡literalmente! Por favor, examina los libros de la biblioteca infestados y acaba con cualquier gusano que encuentres.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'frFR', 'Les pages de l’Histoire', 'Arpentez la bibliothèque pour trouver des livres infestés, puis écrasez 12 Vers biblivores.', 'La pire des choses est survenue, $R.$b$bC’est une invasion de vers biblivores !$b$bLa situation est catastrophique. Tous les vers présents dans la bibliothèque doivent être éradiqués sur-le-champ pour empêcher leur propagation.$b$bNous devons anéantir ce problème, littéralement ! Je vous en prie, examinez les livres infestés dans la bibliothèque et écrasez tous les vers que vous trouverez.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'itIT', 'Pagine di storia', 'Esamina i libri della biblioteca in cerca di quelli infestati, quindi schiaccia 12 Tarli.', '$R, è accaduta la cosa peggiore che potesse capitare.$B$BAbbiamo i tarli.$B$BLa situazione è critica: bisogna eliminarli tutti e subito per evitare un\'infestazione.$B$BDobbiamo debellare il problema! Esamina i libri contaminati nella biblioteca e schiaccia tutti i tarli che trovi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'koKR', '역사의 한 장', '도서관에서 벌레 먹은 책들을 살펴보고, 책벌레 12마리를 처치해야 합니다.', '정말 끔찍한 일이 벌어졌네, $r.$B$B여기. 도서관에. 책벌레가. 나타났네.$B$B최악의 상황이야. 지금 당장, 다른 일을 모두 제쳐놓고라도 책벌레를 소탕해야 하네. 안 그랬다가는 숫자가 엄청나게 늘어나 버릴 거야!$B$B이 문제를 즉시 해결해 주게. 책벌레를 짓밟아 버리게! 도서관에서 벌레 먹은 책들을 살펴보고, 책벌레를 발견하면 당장 밟아 죽여버리게!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'ptBR', 'Páginas da história', 'Vasculhe os livros da biblioteca procurando infestações, depois esmague 12 Traças.', 'Aconteceu a pior coisa possível, $r.$B$BAgora... temos... traças.$B$BA situação é crítica. É preciso exterminar todas as traças da biblioteca imediatamente para impedir que se espalhem.$B$BTemos que esmagar esse problema, literalmente! Por favor, examine os livros infestados aqui na biblioteca e esmague as traças que achar.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'ruRU', 'Страницы истории', 'Найдите в библиотеке источники заражения и раздавите 12 книжных червей.', '$r, сбылся худший из наших кошмаров.$B$BУ нас. Завелись. Книжные. Черви.$B$BПоложение угрожающее. Их необходимо как можно быстрее уничтожить, пока они не расплодились. $B$BВрага нужно раздавить – в буквальном смысле слова! Пожалуйста, осмотри зараженные книги в библиотеке и раздави всех книжных червей, которых найдешь.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'zhCN', '历史书页', '搜遍整个藏书阁，找到被虫蛀的书，然后碾碎12只书虫。', '最糟糕的事情终于发生了，$r。$B$B我们这里长书虫了！$B$B形势非常非常严峻。我们必须立刻彻底消灭藏书阁中的书虫，绝对不能让它们扩散开来。$B$B我们必须碾碎这个大麻烦，碾碎它们！我要你检查藏书阁里被虫蛀的书籍，并碾碎你发现的所有书虫。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30002, 'zhTW', '歷史書頁', '檢驗圖書館裡的書籍，壓扁12條書蟲。', '最糟的情況發生了，$r。$B$B我們、有、書蟲!$B$B這是致命的危機。必須殲滅圖書館裡的每一條書蟲才能防止牠們擴散。$B$B我們得碾碎這些大麻煩!請你檢驗圖書館裡的書籍，壓扁你所看見的每一條書蟲。', '', '', '', '', '', '', 0);
