INSERT INTO `quest_template_locale` VALUES (30139, 'frFR', 'Le voyage du chevaucheur', 'Retournez voir l’Instructeur Ronce du Ciel à l’Arboretum, dans la Forêt de Jade.', 'Excellent, tout bonnement excellent ! Je vais me charger de replacer soigneusement les autres œufs dans leur nid.$b$bPendant ce temps, apportez votre œuf à l’instructeur Ronce du Ciel à l’Arboretum.', '', '', '', '', '', '', 0);
