INSERT INTO `quest_template_locale` VALUES (30139, 'ptBR', 'A jornada do cavalgante', 'Fale novamente com o Instrutor Espinho Celeste no Arboreto, na Floresta de Jade.', 'Excelente, simplesmente excelente! Vou colocar os outros ovos de volta nos ninhos sem mexer neles.$B$BEnquanto isso, entregue seu ovo ao Instrutor Espinho Celeste no Arboreto.', '', '', '', '', '', '', 0);
