INSERT INTO `quest_template_locale` VALUES (30142, 'deDE', 'Es ist ein...', 'Beobachtet das Ausschlüpfen Eures Eis.', 'Oh! Jeden Moment kann ein Junges aus diesem Ei schlüpfen!$B$BDer kleine Kerl scheint Euch möglichst schnell kennenlernen zu wollen.$B$BAlso, seid Ihr bereit?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'esES', 'Es...', 'Observa cómo tu huevo eclosiona.', '¡Dios mío! ¡Este huevo va a eclosionar en cualquier momento!$B$BBueno, parece que este pequeñín tiene ganas de conocerte.$B$B¿Estás $glisto:lista;?', '', '', '', '', '', '', 0);
