INSERT INTO `quest_template_locale` VALUES (30142, 'frFR', 'C’est un…', 'Regardez votre œuf éclore.', 'Ho là là ! Cet œuf est sur le point d’éclore !$b$bEh bien, on dirait que ce petit bout est pressé de faire votre connaissance !$b$bVous êtes $gprêt:prête; ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'itIT', 'Congratulazioni, è...', 'Guarda il tuo uovo schiudersi.', 'Accidenti! Quest\'uovo si schiuderà da un momento all\'altro!$B$BBe\', sembra proprio che il piccoletto non veda l\'ora di conoscerti.$B$BSei $gpronto:pronta;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'koKR', '축하합니다!', '알이 부화하는 것을 지켜봐야 합니다.', '아, 이런! 알이 곧 부화할 것 같습니다!$B$B아무래도 우리 꼬마 친구가 당신을 빨리 만나고 싶어 안달이 난 것 같아요.$B$B준비됐습니까?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'ptBR', 'É um...', 'Veja seu ovo chocar.', 'Minha nossa! Esse ovo está prestes a chocar!$B$BParece que esse filhotinho está louco para conhecer você.$B$BEstá pront$Go:a;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'ruRU', 'Это...', 'Смотрите, как из яйца вылупится змей.', 'Ой! Змей может вылупиться из этого яйца в любую секунду!$B$BПохоже, малыш мечтает поскорее с тобой познакомиться.$B$BТы $gготов:готова;?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'zhCN', '这是个……', '看着你的龙蛋孵化。', '哦，天呐！这个蛋随时有可能孵化！$B$B嗯，看来这个小家伙迫不及待想要见你了。$B$B准备好了吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30142, 'zhTW', '那是…！', '看著你的蛋孵化。', '喔我的天！這顆蛋隨時都會孵出來！$B$B好吧，看來這個小傢伙急著想見見你。$B$B你準備好了嗎？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'deDE', 'Sie vermehren sich wie Unkraut', 'Sprecht mit Ausbilderin Windklinge im Arboretum.', 'Ah, Ihr seid es wieder, $n.$B$BAusbilderin Windklinge sucht schon nach Euch. Ihr findet sie auf dem Hügelkamm im Osten.$B$BSie hat ein paar Neuigkeiten über Eure Wolkenschlange für Euch.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'esES', 'Crecen como las malas hierbas', 'Habla con la instructora Hoja Liviana en El Arboretum.', 'Ah, eres tú otra vez, $n.$B$BLa instructora Hoja Liviana te está buscando. Puedes encontrarla en la colina al este.$B$BTiene ciertas noticias sobre tu dragón nimbo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'esMX', 'Crecen como las malas hierbas', 'Habla con la instructora Hoja Liviana en El Arboretum.', 'Ah, eres tú otra vez, $n.$B$BLa instructora Hoja Liviana te está buscando. Puedes encontrarla en la colina al este.$B$BTiene ciertas noticias sobre tu dragón nimbo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'frFR', 'Ils poussent comme du chiendent', 'Parlez à l’Instructrice Lame du Vent à l’Arboretum.', 'Ah, vous revoilà, $n.$B$BL’instructrice Lame du Vent est à votre recherche. Vous pourrez la trouver sur la falaise à l’est.$B$BElle a des nouvelles à vous annoncer au sujet de votre serpent-nuage.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'itIT', 'Crescono come funghi', 'Parla con l\'Istruttrice Lama Ventosa all\'Arboreto.', 'Ah! Sei di nuovo tu, $n.$B$BL\'Istruttrice Lama Ventosa ti sta cercando. Puoi trovarla sulla cresta a est.$B$BHa delle novità da riferirti riguardo alla tua serpe delle nubi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'koKR', '잡초처럼 자라는', '도원에 있는 교관 윈드블레이드와 대화해야 합니다.', '아아, 다시 만나 반갑군, $n.$B$B윈드블레이드 교관이 자네를 찾고 있네. 동쪽 언덕에서 만날 수 있을 걸세.$B$B자네 운룡에 대해 알려줄 게 있다고 하더군.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'ptBR', 'Crescem pra chuchu', 'Fale com a Instrutora Lâmina de Vento no Arboreto.', 'Ah, é você de novo, $n.$B$BA Instrutora Lâmina de Vento está procurando por você. Ela está na serra que fica a leste.$B$BE ela tem novidades sobre a sua serpente das nuvens.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'ruRU', 'Растут, как сорняки', 'Поговорите с инструктором Режущий Ветер в Дендрарии.', 'А-а, это снова ты, $n.$B$BИнструктор Режущий Ветер ищет тебя. Сейчас она на восточной гряде.$B$BУ нее какие-то новости о твоем змее.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'zhCN', '比杂草长得还快', '和百木园的风刃教头谈一谈。', '啊，又见面了，$n。$B$B风刃教头正在找你，你可以在东边的山脊上找到她。$B$B她有些事要告诉你，是关于翔龙的。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30143, 'zhTW', '雜草般的生命力。', '到落英園找講師風刃。', '啊啊，又是你，$n。$B$B講師風刃正在找你。你可以在東邊的山脊找到她。$B$B她有關於你的雲蛟的消息。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'deDE', 'Flugausbildung: Ringelreihen', 'Reitet Eure Wolkenschlange durch 6 Wolkenringe.', 'Jetzt, da Eure Schlange ausgewachsen ist, seid Ihr bereit, mit der Flugausbildung zu beginnen.$B$BWie alle Neulinge müsst Ihr ganz von vorn beginnen.$B$BEure erste Aufgabe ist sehr einfach. Fliegt durch 6 der Wolkenringe zwischen den Spitzen im Osten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'esES', 'Instrucción de vuelo: pasar por el aro', 'Vuela a través de 6 anillos de nubes a lomos de tu dragón nimbo.', 'Ahora que tu dragón ha dejado de crecer, puedes comenzar la instrucción de vuelo.$B$BAl igual que el resto de novatos, tendrás que empezar desde el principio.$B$BTu primera tarea es sencilla. Vuela a través de 6 anillos de nubes que hay entre las agujas, al este.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'esMX', 'Instrucción de vuelo: pasar por el aro', 'Vuela a través de 6 anillos de nubes a lomos de tu dragón nimbo.', 'Ahora que tu dragón ha dejado de crecer, puedes comenzar la instrucción de vuelo.$B$BAl igual que el resto de novatos, tendrás que empezar desde el principio.$B$BTu primera tarea es sencilla. Vuela a través de 6 anillos de nubes que hay entre las agujas, al este.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'frFR', 'Leçons de vol : les anneaux', 'Volez à travers 6 Anneaux nuageux en chevauchant votre Serpent-nuage.', 'À présent que votre serpent a atteint sa taille adulte, vous allez pouvoir commencer les leçons de vol.$B$BComme tous les novices, vous commencerez par le début.$B$BVotre première tâche sera simple : voler à travers six des anneaux nuageux qui flottent entre les cimes à l’est.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'itIT', 'Addestramento di volo: Passaggio fra gli Anelli', 'Attraversa in volo 6 Anelli di Nuvole in sella alla tua serpe delle nubi.', 'Ora che la tua serpe è adulta, sei $gpronto:pronta; per l\'addestramento di volo.$B$BCome tutti i novellini, dovrai iniziare dalle basi.$B$BIl primo compito è molto semplice: attraversa in volo 6 Anelli di Nuvole fra le guglie a est.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'koKR', '비행 훈련: 고리 통과', '운룡을 타고 구름 고리 6개를 통과해야 합니다.', '이제 당신 운룡이 다 자랐으니, 비행 훈련을 시작해도 좋습니다.$B$B다른 수련생들과 마찬가지로 당신도 처음부터 시작해야 합니다.$B$B처음 해야 할 일은 간단해요. 동쪽의 봉우리를 둘러싸고 있는 구름 고리 6개를 날아서 통과하면 됩니다.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'ptBR', 'Treinamento de voo: prova dos anéis', 'Atravesse 6 anéis de nuvem voando em sua serpente das nuvens.', 'Agora que sua serpente está completamente crescida, você pode começar a ter aulas de voo.$B$BComo todo aprendiz, você tem de começar do início.$B$BSua primeira tarefa é simples. Atravessar voando 6 anéis de nuvem em meio aos espigões a leste.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'ruRU', 'Обучение полету: через кольцо', 'Пролетите через 6 облачных колец на вашем облачном змее.', 'Теперь, когда твой змей совсем взрослый, можно приступать к учебным полетам.$B$BТы – новичок, и поэтому тебе придется начать с азов.$B$BПервая задача проста. Пролети через 6 облачных колец, которые находятся на востоке, между шпилями.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'zhCN', '飞行训练：穿越云环', '骑乘云端翔龙穿越6个云之环。', '既然你的翔龙已经长大了，也是时候开始你的飞行训练了。$B$B跟所有新手一样，你必须从最基本的开始。$B$B你的第一个任务很简单，穿越东面山峰间的6个云之环。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30144, 'zhTW', '飛行訓練:穿越雲環', '乘著你的雲蛟穿越6朵雲環。', '你的蛟龍已完全長成，這也代表你準備好開始飛行訓練。$B$B和其他初學者一樣，你必須從頭開始。$B$B你的第一項任務很簡單。到東邊的尖柱地帶，穿越6朵雲環。', '', '', '', '', '', '', 0);
