INSERT INTO `quest_template_locale` VALUES (30509, 'ptBR', 'O despertar do general Nazgrim', 'Fale com o General Nazgrim na Vila Binan, que fica no Monte Kun-Lai.', 'Parece que o general Nazgrim está de novo consciente após a convalescença. Ele e a comitiva estão no andar superior da casa do boticário, na Vila Binan.$B$BO General solicitou sua presença.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30509, 'ruRU', 'Генерал Назгрим очнулся', 'Поговорите с генералом Назгримом в деревне Бинан на вершине Кунь-Лай.', 'Генерал Назгрим, похоже, оправился после полученных травм. Он и его люди наверху в доме аптекаря там, в деревне Бинан.$B$BОн хочет встретиться с тобой.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30509, 'zhCN', '纳兹戈林将军苏醒了', '去昆莱山的滨岸村和纳兹戈林将军谈一谈。', '看来，纳兹戈林将军已经从昏迷中苏醒过来了。他和他的人在滨岸村药剂师的屋子里，他们在楼上。$B$B他要你去见他。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30509, 'zhTW', '納茲格寧姆將軍甦醒', '到崑萊峰的濱南村找納茲格寧姆將軍。', '看來納茲格寧姆將軍已從傷勢中甦醒。他和他的人馬在濱南村藥劑師家樓上。$B$B他要你過去。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30510, 'deDE', 'General Nazgrim ist erwacht', 'Sprecht mit General Nazgrim in Binan am Kun-Lai-Gipfel.', 'Anscheinend ist Euer General Nazgrim von seinen Verletzungen erwacht. Er und seine Leute befinden sich oben im Haus der Apothekerin auf der anderen Straßenseite.$B$BEr hat nach Eurer Anwesenheit verlangt.', '', '', '', '', '', '', 0);
