INSERT INTO `quest_template_locale` VALUES (30534, 'deDE', 'Hofhelfer gesucht', 'Sprecht Euren besten Freund oder Eure beste Freundin an, ob er oder sie Lust hat, auf dem Gehöft Sonnensang zu arbeiten.', 'Trotz all Eurer Bemühungen türmt sich die Arbeit auf dem Hof immer höher.$B$BVielleicht könnt Ihr ja Eure Freunde fragen, ob jemand von ihnen Lust hat, auf dem Gehöft Sonnensang zu arbeiten.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30534, 'esES', 'Otro par de manos', 'Habla con uno de tus mejores amigos y pídele que trabaje en el Rancho Cantosol.', 'A pesar de tus esfuerzos, el trabajo está empezando a acumularse por aquí.$B$BQuizá deberías preguntarles a tus mejores amigos si quieren echarte una mano en el Rancho Cantosol.', '', '', '', '', '', '', 0);
