INSERT INTO `quest_template_locale` VALUES (30571, 'deDE', 'Rettet die Hofhelfer', 'Befreit 12 Bauernhofsklaven, indem Ihr die Aufseher von Ordo tötet.', 'Diese Ordo-Yaungol lassen meine Hofhelfer sich zu Tode schuften! Das darf nicht geschehen.$B$BIch bitte Euch, $C: Rettet sie, bevor es zu spät ist.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'esES', 'Liberación de los granjeros', 'Libera a 12 esclavos de la granja matando a los sobrestantes de Ordo.', '¡Esos yaungol de Ordo están exprimiendo a mis jornaleros hasta la muerte! No es justo.$B$BPor favor, $c, sálvalos antes de que sea demasiado tarde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'esMX', 'Liberación de los granjeros', 'Libera a 12 esclavos de la granja matando a los sobrestantes de Ordo.', '¡Esos yaungol de Ordo están exprimiendo a mis jornaleros hasta la muerte! No es justo.$B$BPor favor, $c, sálvalos antes de que sea demasiado tarde.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'frFR', 'Liberté pour les journaliers', 'Sauvez 12 Esclaves de la ferme en tuant des Surveillants d’Ordo.', 'Ces yaungols d’Ordo font travailler mes journaliers jusqu’à la mort ! Ce n’est pas correct.$B$BS’il vous plaît, $c, sauvez-les avant qu’il ne soit trop tard.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'itIT', 'Liberazione dei contadini', 'Libera 12 Schiavi della Fattoria uccidendo i Sovrintendenti di Ordo.', 'Quegli Yaungol di Ordo stanno sfruttando a morte i miei contadini! Non è giusto.$B$BTi prego, $C, salvali prima che sia troppo tardi.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'koKR', '농장 일꾼 구출하기', '오르도 감독관을 처치하여 농장 노예 12명을 구출해야 합니다.', '저 오르도 야운골 놈들이 우리 농장 일꾼들을 혹사시키고 있어요! 못된 짓이라고요.$B$B$c님, 너무 늦기 전에 일꾼들을 구해 주세요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'ptBR', 'Movimento dos Camponeses Escravizados', 'Liberte 12 Escravos da Fazenda matando Capatazes Ordos.', 'Os yaungóis Ordos tão tirando o couro dos camponêis! Isso num tá certo.$B$BPor favor, $c, salva os camponêis antes que seja tarde!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'ruRU', 'Свободу рабам!', 'Освободите 12 рабов с фермы, убив надзирателей Ордо.', 'Яунголы ордо загоняют моих рабочих до смерти! Это не дело.$B$BПожалуйста, |3-6($c), спаси их, пока не поздно!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'zhCN', '解救雇农', '击败斡耳朵督工，解救12名农场奴隶。', '斡耳朵野牛人快把我的雇农们给累死了！这样可不好。$B$B拜托了，$c，请在事情变得不可挽回之前救出他们。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (30571, 'zhTW', '雇農的自由', '殺死歐朵監督者，釋放12名被奴役的僱農。', '歐朵揚古會把我的雇農奴役到死!這樣不對。$B$B求求你，$c，在事態變得不能挽回前拯救他們吧。', '', '', '', '', '', '', 0);
