INSERT INTO `quest_template_locale` VALUES (30662, 'deDE', 'Der Kriegshetzer von Ordo', 'Tötet den Kriegshetzer von Ordo.', 'Ihr habt den Schutzschild im Streitlager Ordo ausgeschaltet. Begebt Euch erneut dorthin, bevor er wieder aktiv ist.$B$BZeit, in die Höhle einzudringen und den Häuptling zu erledigen!', '', 'Sein Schutzschild ist ausgeschaltet!', 'Kriegshetzer von Ordo', '', '', '', 0);
