INSERT INTO `quest_template_locale` VALUES (30663, 'itIT', 'L\'Araldo della Guerra di Ordo', 'Uccidi l\'Araldo della Guerra di Ordo.', 'Hai fatto cadere la barriera del Presidio del Fuoco Ordo. Torna laggiù prima che riescano a erigerla di nuovo.$B$BUccidi l\'Araldo della Guerra di Ordo!', '', 'Non è più protetto dalla barriera!', 'Araldo della Guerra di Ordo', '', '', '', 0);
