INSERT INTO `quest_template_locale` VALUES (31326, 'frFR', 'La tiare élégante de Tina', 'Apportez la Tiare élégante à Tina Griffe de Tourbe à la Maison de Haohan ou au Marché de Micolline, dans la Vallée des Quatre vents.', 'C\'est une tiare de diamants éblouissante. Dans la vallée, qui pourrait être assez riche pour posséder un bijou pareil ?$B$BQui que ce soit, il pourrait y avoir une belle récompense à la clé.', '', '', '', '', '', '', 0);
