INSERT INTO `quest_template_locale` VALUES (31337, 'frFR', 'Une preuve d’estime', 'Acceptez le cadeau de Nomi.', 'Merci de vous assurer que je vais bien ! J’ai pu aider les Patte de Fer, aujourd’hui et ils ont dit que je pouvais vous donner ceci.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'itIT', 'Un segno di ringraziamento', 'Ricevi il dono di Nomi.', 'Grazie per essere $gpassato:passata; a vedere come me la cavo! Oggi sono riuscito ad aiutare gli Zampa Forte e mi hanno detto di darti questo.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'koKR', '감사의 표시', '노미의 선물을 받아야 합니다.', '잘하고 있는지 일부러 보러 와 주시다니 고마워요! 오늘은 아이언포우가의 일손을 도왔는데, 그분들이 이걸 전해 드리라고 하더라고요.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'ptBR', 'Em Sinal de Agradecimento', 'Receba o presente de Nomi.', 'Obrigado por se certificar de que eu estou bem! Eu consegui ajudar os Pata de Ferro, e eles disseram que eu podia dar isso pra você.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'ruRU', 'Знак признательности', 'Получите подарок Номи.', 'Спасибо, что интересуешься, как у меня дела! Сегодня мне удалось помочь семейству Железной Лапы, и они сказали, что я могу передать это тебе.', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'zhCN', '一点心意', '接受诺米的礼物。', '多谢你来看我，我很好！今天，我还给铁掌家打下手了，他们说我可以把这个送给你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31337, 'zhTW', '表示謝意', '收下糯米的謝禮。', '謝謝你來確認我的進展！我已經有能力幫助鐵爪家的人了，他們說我可以把這個給你。', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'deDE', 'Ein verlorenes Schäfchen', 'Findet Schäfchen und bringt es zu Chi-Chi zurück.', 'Schäfchen, Chi-Chis wertvollstes Schaf, ist verschwunden!$B$BDas dumme Ding ist immer wieder vom Hof weggelaufen.$B$BDummes Schaf! Ich hab\' überall gesucht, konnte es aber nicht finden.$B$BKönnt Ihr mir helfen, $R?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'esES', 'Ovejita perdida', 'Encuentra a Ovejita y devuélvesela a Chee Chee.', '¡La oveja premiada de Chee Chee, Ovejita, ha desaparecido!$B$BEsa oveja mala siempre se escapa de la granja.$B$B¡Oveja estúpida! La he buscado en todas partes y no la he encontrado.$B$B¿Podrías ayudarme, $r?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'esMX', 'Ovejita perdida', 'Encuentra a Ovejita y devuélvesela a Chee Chee.', '¡La oveja premiada de Chee Chee, Ovejita, ha desaparecido!$B$BEsa oveja mala siempre se escapa de la granja.$B$B¡Oveja estúpida! La he buscado en todas partes y no la he encontrado.$B$B¿Podrías ayudarme, $r?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'frFR', 'Sauvez Moumoute', 'Retrouvez Moumoute et ramenez-le à Chii Chii.', 'Moumoute, le mouton primé de Chii Chii, a disparu !$b$bCe gigot sur pattes ne cesse de s’aventurer hors de la ferme.$B$BQuelle tête de mule, ce mouton ! J’ai cherché partout mais aucune trace de lui.$B$BPouvez-vous me filer un coup de main, $R ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'itIT', 'La Pecorella perduta', 'Trova Pecorella e riportala a Ghi Ghi.', 'La preziosa pecora di Ghi Ghi, Pecorella, non c\'è più!$B$BQuella pecora cattiva si è allontanata dalla fattoria.$B$BStupida pecora! La cerco dappertutto ma non la trovo.$B$B$R, mi aiuteresti?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'koKR', '잃어버린 양돌이', '양돌이를 찾아서 치 치에게 되돌려줘야 합니다.', '치 치의 소중한 양돌이가 없어졌다!$B$B그 버릇없는 양은 항상 농장 주변을 돌아다녀.$B$B멍청한 양돌이 같으니라고! 여기저기 다 찾아봤지만 찾지 못했어.$B$B나 좀 도와줘, 응?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'ptBR', 'Nininho perdido', 'Encontre Nininho e devolva-o a Tchi Tchi.', 'O carneirinho premiado do Tchi Tchi, o Nininho, sumiu!$B$BAquele carneiro danado está sempre saindo da fazenda.$B$BCarneiro burro! Já procurei em todo lugar e não achei.$B$BVocê pode me ajudar, $r?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'ruRU', 'Заблудший барашек', 'Найдите Бяшу и верните его Чи-Чи.', 'Любимый барашка Чи-Чи, Бяша, совсем пропасть!$B$BПлохой барашка всегда убегай с фермы!$B$BГлупый баран! Я искать везде — его нет.$B$BМожешь помочь, |3-6($r)?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'zhCN', '失踪的咩咩羊', '找到咩咩羊并将它带回吱吱身边。', '吱吱心爱的绵羊——咩咩羊不见了！$B$B那只坏羊总是走丢。$B$B笨羊！我找遍了所有地方都没能找到它。$B$B你能帮帮我吗，$r？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31338, 'zhTW', '失蹤的羊羊', '找回羊羊，並把牠帶回去給奇奇。', '奇奇的得獎綿羊，羊羊不見了！$B$B那隻壞綿羊每次都晃到農場外面去。$B$B笨羊！我到處都找不到牠。$B$B你可以幫我嗎，$r？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'deDE', 'Schäfchen ist schon wieder weg', 'Findet Schäfchen und bringt ihn zu Chi-Chi zurück.', 'Dieses dumme Schaf ist schon wieder abgehauen!$B$BKönnt Ihr es noch mal für mich suchen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'esES', 'Ovejita perdida… otra vez', 'Encuentra a Ovejita y llévala con Chee Chee.', '¡Esa oveja estúpida! ¡Ha vuelto a desaparecer!$B$B¿Puedes volver a buscarla?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'esMX', 'Ovejita perdida… otra vez', 'Encuentra a Ovejita y llévala con Chee Chee.', '¡Esa oveja estúpida! ¡Ha vuelto a desaparecer!$B$B¿Puedes volver a buscarla?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'frFR', 'Sauvez Moumoute… le retour', 'Retrouvez Moumoute et ramenez-le à Chii Chii.', 'Rhoo, quelle tête de mule, ce mouton ! Il a encore pris la poudre d’escampette !$B$BÇa vous dérangerait de me le retrouver encore une fois ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'itIT', 'La Pecorella... nuovamente perduta', 'Trova Pecorella e riportala a Ghi Ghi.', 'Stupida pecora! È andata via di nuovo!$B$BLa cerchi ancora per me?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'koKR', '양돌이가 또 도망쳤다고?', '양돌이를 찾아서 치 치에게 데려가야 합니다.', '멍청한 양 같으니라고! 양돌이 또 나돌아다닌다!$B$B나 대신 양돌이 좀 찾아줄래?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'ptBR', 'Nininho perdido... de novo', 'Encontre Nininho e traga-o de volta à Tchi Tchi.', 'Aquele carneiro burro! Ele fugiu de novo!$B$BVocê pode encontrar ele pra mim outra vez?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'ruRU', 'Заблудший барашек... Еще раз', 'Найдите Бяшу и верните его Чи-Чи.', 'Тупой барашка! Опять пойти гулять!$B$BМожешь найти еще раз?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'zhCN', '再次失踪的咩咩羊', '找到咩咩羊并将它带回吱吱身边。', '那只笨羊！它又走丢了！$B$B你能再次帮我把它找回来吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31339, 'zhTW', '失蹤的羊羊…又一次', '找回羊羊，並把牠帶回去給奇奇。', '那隻笨羊！牠又跑出去亂晃了$B$B你可以再幫我找牠一次嗎？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'deDE', 'Ach, Schäfchen...', 'Findet Schäfchen.', 'Seufz... Ihr habt es sicher erraten... Schäfchen ist fort... SCHON WIEDER.$B$BDummes Schaf! Macht mir immer so viel Ärger!$B$BIch muss Euch noch einmal um einen Gefallen bitten, aber ist garantiert das letzte Mal, versprochen.$B$BKönnt Ihr für mich nach Schäfchen suchen?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'esES', 'Oh, Ovejita…', 'Encuentra a Ovejita.', 'Ay… lo has adivinado… Ovejita ha desaparecido... OTRA VEZ.$B$B¡Oveja estúpida! ¡Me da demasiados problemas!$B$BNo me gusta pedírtelo otra vez, pero prometo que será la última.$B$B¿Puedes ayudarme a buscarla?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'esMX', 'Oh, Ovejita…', 'Encuentra a Ovejita.', 'Ay… lo has adivinado… Ovejita ha desaparecido... OTRA VEZ.$B$B¡Oveja estúpida! ¡Me da demasiados problemas!$B$BNo me gusta pedírtelo otra vez, pero prometo que será la última.$B$B¿Puedes ayudarme a buscarla?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'frFR', 'Oh non, Moumoute…', 'Retrouvez Moumoute.', '<Soupir>… Vous l’avez deviné… Moumoute s’est envolé… COMME C’EST ÉTONNANT.$b$bQuelle tête de mule, ce mouton ! Y’m’rend la vie impossible !$b$bJe suis désolé de vous le demander encore une fois, mais je vous promets que cette fois-ci, c’est la dernière.$b$bVous pouvez me le retrouver ?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'itIT', 'Oh Pecorella...', 'Trova Pecorella.', '<Sospira>... indovina un po\'... Pecorella è scappata... DI NUOVO.$B$BStupida pecorella! Sono sempre preoccupato!$B$BMi dispiace chiedere ancora, ma ti prometto che è l\'ultima volta.$B$BLa cerchi per me?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'koKR', '양돌이가 또...', '양돌이를 찾아야 합니다.', '하아... 그래, 맞아... 양돌이가 사라졌어... 또 말이야.$B$B멍청한 양 같으니! 정말 골치 아프게 만드네!$B$B다시 부탁하긴 싫지만, 이번이 마지막일 거야.$B$B양돌이 좀 찾아줘, 응?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'ptBR', 'Ó, Nininho...', 'Encontre Nininho.', '<Tchi Tchi suspira, desanimado.>$B$BÉ... Você adivinhou... O Nininho desapareceu... DE NOVO!$B$BCarneiro burro! Só me dá problema!$B$BDetesto pedir de novo, mas prometo que essa será a última vez.$B$BVocê consegue encontrar ele pra mim?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'ruRU', 'Ох, Бяша...', 'Найдите Бяшу.', 'Хм... ты угадай... Бяша ушел. Снова!$B$BГлупый-глупый баран! Одни хлопоты с ним!$B$BЯ не хотеть просить, но... я обещай – это в последний раз.$B$BНайдешь Бяшу?', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'zhCN', '哦，咩咩羊……', '找到咩咩羊。', '唉……你猜对了……咩咩羊又走丢了。$B$B笨羊！总是给我添麻烦！$B$B我都不好意思再开口求你了，但我保证这是最后一次。$B$B你能帮我把它找回来吗？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31340, 'zhTW', '喔羊羊…', '找回羊羊。', '唉…你猜對了…羊羊牠…又不見了。$B$B笨羊！給我帶來這麼多麻煩！$B$B我真不想再拜託你，我保證這是最後一次了。$B$B你可以幫我找牠嗎？', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'deDE', 'Ein Wolf im Schafspelz', 'Benutzt den alten Schafspelz irgendwo auf der Himmelsweite oder in Kunzen und tötet dann Krungko.', 'AAAARRRRRGGGHHHHH!!!! Dieser letzte Tropfen hat das Fass zum Überlaufen gebracht!$B$BDiese dummen Ho-zen auf dem Berg haben mein SCHÄFCHEN umgebracht!$B$BIch weiß genau, wer es war, und diesmal werde ich mich RÄCHEN.$B$BWenn Ihr mir helfen wollt, legt diesen alten Schafspelz an.$B$BVerkleidet Euch damit und wartet irgendwo auf dem Berg im Norden auf diesen kleinen Knatzel Krungko. Wenn er aus seinem Versteck kommt, brecht ihm die Knochen!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'esES', 'Un lobo con piel de cordero', 'Usa la piel de oveja antigua en cualquier lugar de Firmamento o del Poblado Kunzen y mata a Krungko.', '¡¡¡AAAAAAAHHHH!!! ¡Esto es el kolmo!$B$B¡Esos estúpidos hozen de la montaña han matado a mi OVEJITA!$B$BYo sabe kién ha sido, y es la hora de la VENGANZA.$B$BSi tú kiere ayudarme, toma esta piel de oveja antigua.$B$BÚsala para eskonderte en kualquier parte en la montaña del norte, espera hasta ke ese pekeño miko Krungko salga de su eskondite, ¡y rómpele los huesos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'esMX', 'Un lobo con piel de cordero', 'Usa la piel de oveja antigua en cualquier lugar de Firmamento o del Poblado Kunzen y mata a Krungko.', '¡¡¡AAAAAAAHHHH!!! ¡Esto es el kolmo!$B$B¡Esos estúpidos hozen de la montaña han matado a mi OVEJITA!$B$BYo sabe kién ha sido, y es la hora de la VENGANZA.$B$BSi tú kiere ayudarme, toma esta piel de oveja antigua.$B$BÚsala para eskonderte en kualquier parte en la montaña del norte, espera hasta ke ese pekeño miko Krungko salga de su eskondite, ¡y rómpele los huesos!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'frFR', 'Un loup dans la bergerie', 'Utilisez la Vieille peau de mouton n’importe où dans la Chaîne des Cieux ou à Kunzen, puis tuez Krungko.', 'AAARRRRRGGHHHH !!!! C’est la croûte qui fait déborder la vase !$b$bCes imbéciles de hozen de la montagne m\'ont tué mon MOUMOUTE !$b$bMais je sais exactement qui a fait le coup et l’heure de la VENGEANCE a sonné.$b$bSi vous voulez m’aider, prenez cette vieille peau de mouton.$b$bServez-vous-en pour vous déguiser et allez dans la montagne au nord, puis attendez que ce sale doukacque de Krungko sorte de sa cachette, et ensuite brisez-lui les os !', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'itIT', 'Un lupo travestito da pecora', 'Usa la Vecchia Pelle di Pecora a Cieloaperto o al Villaggio dei Kunzen, poi uccidi Krungko.', 'AAAIIIIEEUUGGHHHH!!! Questo è troppo!$B$BQuegli stupidi Hozen sulle montagne hanno ucciso PECORELLA!$B$BSo chi sono, e ora voglio VENDETTA.$B$BSe vuoi aiutare, prendi questa vecchia pelle da pecora.$B$BUsala per nasconderti nelle montagne a nord, aspetta quel piccolo guggoso di Krungko e poi fallo a pezzi!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'koKR', '양의 탈을 쓴 늑대', '하늘능선 또는 쿤젠 마을 아무 곳에서나 양가죽을 사용한 뒤에 크룽코를 처치해야 합니다.', '으아아아아아악!!!! 이젠 더는 못 참겠어!$B$B산에 있는 저 멍청한 호젠 놈들이 내 양돌이를 죽였어!$B$B난 누가 그랬는지 정확히 알고 있으니 이젠... 복수를 해야겠군.$B$B날 도와줄 거라면 이 오래된 양가죽을 가져가.$B$B북쪽 산 아무 데서나 이걸 뒤집어쓰고 양으로 변장한 다음, 크룽코 핑거리커가 나타나면 처치해버려!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'ptBR', 'Lobo em pele de ovelha', 'Use a pele de ovelha velha em qualquer lugar da Serra do Céu ou da Vila Kunzen e mate Krunkgo.', 'AAAAAAAAAAAGHH!!! Já chega! Essa foi a última gota!$B$BAqueles hozens estúpidos da montanha mataram o meu CARNEIRINHO!$B$BEu sei exatamente quem fez isso, e está na hora da VINGANÇA.$B$BSe você estiver dispost$go:a; a me ajudar, leve essa velha pele de ovelha.$B$BUse-a para se disfarçar próximo às montanhas ao norte, espere aquele toletinho do Krungko sair de onde estiver escondido e quebre os ossos dele!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'ruRU', 'Волк в овечьей шкуре', 'Используйте старую овечью шкуру в Небогорье или в деревне Куньцзэнь, а потом убейте Крунко.', 'А-а-а! Это был последний капля!$B$bХозены с гор убить мой Бяшу!$B$BЯ знай, кто это делать, и я хочу месть!$B$BЕсли хочешь помогать, бери этот старый овечий шкура.$B$BИди на север, в горы, и надень на себя. Жди мелкий дукер Крунко и сломать все-все его кости!', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'zhCN', '披着羊皮的狼', '在摩天山脊或昆森村的任意位置使用旧羊皮，然后击败库科。', '啊啊啊！！！我忍无可忍啦！$B$B山上那些蠢猢狲杀了我的咩咩羊！$B$B我知道是谁干的，现在到轮到我们报仇了。$B$B如果你愿意帮我，就拿上这张旧羊皮。$B$B在北边的山上用它来伪装你自己，等那小恶棍库科一从藏身处出来就敲碎他的骨头！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31341, 'zhTW', '披著羊皮的狼', '到擎天嶺或崑真村的任何一個地方使用老綿羊皮，然後殺死昆可。', '啊啊啊咿咿咿咿嗚嗚嘎吼吼吼！這是激怒奇奇的最後一根稻草！$B$B山上的那些蠢蛋猴人殺了我的寶貝羊羊！$B$B我知道是誰幹的好事，現在復仇的時候到了。$B$B如果你願意幫我，就穿上這個老綿羊皮。$B$B到北邊山區的任何地方，用這東西偽裝自己，等那個小廢渣昆可從藏身處出來，然後打斷他的骨頭！', '', '', '', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'deDE', 'Der Weg zum Respekt liegt in der Gewalt', 'Tötet das Sha der Gewalt.', 'Ihr habt Euch im Kampf bewiesen.$B$BWenn Ihr uns auch weiterhin unterstützen wollt, hätte ich noch eine Bitte an Euch.$B$BDas Sha der Gewalt erwartet Euch in den versiegelten Kammern. Vernichtet es, wenn Ihr Euch meinen Respekt verdienen wollt.', '', 'Meister Schneewehe hat Euch gebeten, diese teuflische Kreatur zu töten.', 'Sha der Gewalt', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'esES', 'El camino al respeto yace en la violencia', 'Mata al sha de la violencia.', 'Has demostrado tu valía en la batalla.$B$BAun así, si pudieses, me gustaría pedirte algo más.$B$BEl sha de la violencia aguarda más allá de las Cámaras Selladas. Si quieres ganarte mi respeto, acaba con él.', '', 'El Maestro Ventisca Algente te ha pedido que acabes con este vil monstruo.', 'Sha de la violencia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'esMX', 'El camino al respeto yace en la violencia', 'Mata al Sha de la violencia.', 'Has demostrado tu valía en la batalla.$B$BAun así, si pudieses, me gustaría pedirte algo más.$B$BEl Sha de la violencia aguarda más allá de las Cámaras Selladas. Si quieres ganarte mi respeto, acaba con él.', '', 'El Maestro Ventisca Algente te ha pedido que acabes con este vil monstruo.', 'Sha de la violencia', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'frFR', 'Le respect s\'obtient par la violence', 'Tuez le Sha de la violence.', 'Vous avez fait vos preuves au combat.$b$bMais si vous voulez encore nous aider, j\'ai une autre requête à vous présenter.$b$bLe sha de la violence se terre derrière ces portes, dans les chambres Scellées. Anéantissez-le, et vous gagnerez mon respect.', '', 'Maître Banc de Neige vous a demandé de tuer cette vile créature.', 'Sha de la violence', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'itIT', 'La via per il rispetto è nella violenza', 'Uccidi lo Sha della Violenza.', 'Hai provato le tue abilità sul campo di battaglia.$B$BCiò nonostante, se vuoi aiutarci ulteriormente, devo assegnarti un ultimo compito.$B$BLo Sha della Violenza attende dentro alle Sale Sigillate. Distruggilo e ti guadagnerai il mio rispetto.', '', 'Il Maestro Neve Lesta ti ha chiesto di uccidere questa vile creatura.', 'Sha della Violenza', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'koKR', '인정을 받는 비결은 폭력에', '폭력의 샤를 처치해야 합니다.', '너는 전투에서 실력을 증명했다.$B$B아직도 우릴 돕고 싶다면 부탁 하나만 하마.$B$B폭력의 샤가 봉인된 방에서 기다리고 있다. 그 녀석을 처치하면 널 특별히 인정해 주마.', '', '사부 스노우드리프트가 이 괴물을 처치해 달라고 했습니다.', '폭력의 샤', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'ptBR', 'O caminho para o respeito passa pela violência', 'Mate o Sha da Violência.', 'Você provou sua habilidade em batalha.$B$BAinda assim, se você se dispuser a prosseguir nos ajudando, devo lhe pedir algo mais.$B$BO Sha da Violência aguarda no interior das Câmaras Seladas. Destrua-o se desejar meu respeito.', '', 'Mestre Monte de Neve lhe pediu que matasse esta criatura torpe.', 'Sha da Violência', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'ruRU', 'Путь к уважению лежит через насилие.', 'Убейте Ша Жестокости.', 'Твое боевое мастерство несомненно.$B$BНо если ты хочешь помогать нам и дальше, я попрошу тебя о еще одной услуге.$B$BВ Закрытых покоях под землей притаился Ша Жестокости. Уничтожь его, и заслужишь мое уважение.', '', 'Мастер Снежный Вихрь попросил вас убить это кошмарное существо.', 'Ша Жестокости', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'zhCN', '尊严来自实力', '杀死狂之煞。', '你已经在战斗中证明了自己。$B$B但是，如果你愿意继续帮助我们，我不得不再让你做一些事情。$B$B狂之煞在封印之厅等待着挑战。如果想要得到我的尊重，就去消灭它吧。', '', '雪流大师让你杀死这个邪恶的怪物。', '狂之煞', '', '', '', 0);
INSERT INTO `quest_template_locale` VALUES (31342, 'zhTW', '暴力是通往尊重的道路', '殺死暴力之煞。', '你已在戰鬥中證明了自己的能力。$B$B不過，如果你想進一步幫助我們，我還有一件事得請你去做。$B$B暴力之煞在封印的房間中等著你。如果你想贏得我的尊敬，就去摧毀它。', '', '雪迅大師要求你去殺死這個邪惡的生物。', '暴力之煞', '', '', '', 0);
